INSERT INTO `unit_comments` VALUES (/*id*/1257012, /*unit_id*/125731, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日はあなたのお誕生日ですね\nプレゼントは少々お待ちください\n今作っているところですので…\nえ？　危なくなんてないですよ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1258001, /*unit_id*/125801, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/5.1, /*change_face*/2, /*description*/"まだまだ冒険者としては\n知らぬことばかりの身\nこれから近くで、冒険のいろはを\n学ばせてくださいませ。兄様", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1258002, /*unit_id*/125801, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/5.6, /*change_face*/5, /*description*/"この姿に変身すること自体は\n造作もないことなのですが\n姿が変わる最中を見られるのは\n…す、少し恥ずかしいです", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
