INSERT INTO `unit_comments` VALUES (/*id*/1260001, /*unit_id*/126001, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/6.1, /*change_face*/5, /*description*/"あまりにお兄さんが普通なので\nこの姿なこと忘れちゃいます…♪\nこれでも本当は、ちょっと\n恥ずかしかったりするんですよ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1260002, /*unit_id*/126001, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"まだまだ未熟なわたしですけど、\nお兄さんが側にいると安心です♪\n魔物との戦闘の時はもちろん…\nそれ以外も…なんて…♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1260003, /*unit_id*/126001, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/6.3, /*change_face*/4, /*description*/"聖女って呼ばれるの苦手なので、\nこの姿のままいればいいかもです\n代わりに、悪魔…とか呼ばれて\nしまいそうですけど…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1260004, /*unit_id*/126001, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"少し落ち込んじゃった時も\nお兄さんの優しい笑顔と言葉で\nわたしの心はすーって癒される…\nお兄さんはわたしの救急箱です♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1260005, /*unit_id*/126001, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/6.4, /*change_face*/5, /*description*/"お兄さんの前では普通の女の子で\nいられるから居心地いいです…♪\nだからお兄さん、少しの間だけ…\n二人っきりでいていいですか…？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
