INSERT INTO `unit_comments` VALUES (/*id*/1262001, /*unit_id*/126201, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/4.5, /*change_face*/2, /*description*/"眷属よ、艶やかな装いじゃろう？\nほれ、思いつく限り\n気の利いた文句を並べてみよ\nわらわが聞いてやろう", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1262002, /*unit_id*/126201, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/5.9, /*change_face*/2, /*description*/"なりゆきで手に入った装束じゃが\nわらわは気に入っておる\n夜を統べる者にふさわしい\n意匠じゃからの♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1262003, /*unit_id*/126201, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/10.2, /*change_face*/1, /*description*/"この晴れ着を披露するのに\nうってつけの舞台はないかの？\nなに、今から魔物の討伐とな？\nまあそれでもよかろう…ゆくぞ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1262004, /*unit_id*/126201, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/4.9, /*change_face*/1, /*description*/"眷属よ、どこか大きな会場を\nおさえよ。お主とわらわの\n威風堂々たる姿を\n世界に知らしめてやるのじゃ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1262005, /*unit_id*/126201, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/7.1, /*change_face*/2, /*description*/"わらわの艶姿を隣で楽しめるのは\nお主だけの特権じゃぞ\n特別な眷属であることを\n誇りに思うがよい♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1262006, /*unit_id*/126231, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"眷属よ、礼を言うぞ\nこの装束を纏うたび、わらわは\n今宵の景色を思い出すじゃろう…\n湖上の月と…お主の笑顔を", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1262007, /*unit_id*/126231, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"きれいな湖じゃのう…\nじゃが、油断は禁物じゃ\nお主は泳ぎが苦手であろう？\nわらわの傍を離れるでないぞ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1262008, /*unit_id*/126231, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"眷属よ、お主は夜が好きか？\nもし一日の終わりをさみしく\n思うことがあれば、わらわが\n一緒にいてやらんこともないぞ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1262009, /*unit_id*/126231, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"まったく、お主は大胆じゃのう…\n裳裾が濡れてしまったではないか\n風邪を引いてはならん…\n近う寄れ…く、くっつくのじゃ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1262010, /*unit_id*/126231, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"水面に映る、今宵の月のように\nわらわはきれいなものとして…\nお主の瞳に映っておるか？\nこ、言葉にして、教えるのじゃ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1262011, /*unit_id*/126201, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/4.1, /*change_face*/2, /*description*/"ハッピーバースデー、眷属よ\n今日が最良の日となるように\nわらわが全てを尽くして\nお主を祝福してやろう！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1262012, /*unit_id*/126231, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日はお主の誕生日じゃ\n遠慮せず欲しいものを述べてみよ\nいつも隣で支えてくれるお主に\n感謝の気持ちを示したいのじゃ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1263001, /*unit_id*/126301, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/9.3, /*change_face*/2, /*description*/"人の心を惑わすほどの美を持ち、\nその美を磨くことを怠らない…\nそんなボクがどこまで行きつくか\nちゃんと期待しててよね♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1263002, /*unit_id*/126301, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/6.2, /*change_face*/2, /*description*/"吸血鬼の力の影響かな…\n時々、無性に飲みたくなるんだ…\nあの真っ赤なトマトジュースを！\n差し入れ、いつでも歓迎するよ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1263003, /*unit_id*/126301, /*use_type*/1, /*voice_id*/3, /*face_id*/5, /*change_time*/4.9, /*change_face*/2, /*description*/"魔性の雰囲気をたたえた\nこの麗しきドレス…！\n人の身とは思えぬ美しさの\nボクにピッタリだよね♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1263004, /*unit_id*/126301, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/5.4, /*change_face*/2, /*description*/"このドレス着てると、凄い力が\n勝手に出ちゃって不便なんだよね\nまあ、全然オッケーだけど！\n美は全てに優先されるからね♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1263005, /*unit_id*/126301, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/6.2, /*change_face*/5, /*description*/"遠慮せずにもっと近付きなよ？\n体温すら感じるくらいにさ\nそれくらいの距離じゃないと\n見えない美があるからね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1263006, /*unit_id*/126331, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ボクほどの美しさになれば\n魔物すらかしずくのさ♪\nま、それも当然だよね\nボクは美を統べる者なんだから！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1263007, /*unit_id*/126331, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ボクという存在を後世に残すため\n絵画を描いてもらおうと思ってね\n有名な画家に依頼したから\nサイフの中身は空っぽさ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1263008, /*unit_id*/126331, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ああ、ボクの周りのこの枠かい？\n美を統べる者としての圧倒的な\nかわいい力で具現化したものさ！\nどうだい、すごいでしょ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1263009, /*unit_id*/126331, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"吸血鬼っていろいろな姿に\n変身できるみたいだけど…\nボク以上に美しいものには\nさすがになれないだろうね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1263010, /*unit_id*/126331, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ボクという美を見ること\nそれ以上の幸福なんて、\n存在するわけないだろう？\n思う存分、目に焼きつけて？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1263011, /*unit_id*/126301, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/3.9, /*change_face*/1, /*description*/"誕生日だって？　めでたいね♪\nそれはそうと、話があるんだ\n実は欲しい化粧品があってね\nボクにプレゼントする気はない？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1263012, /*unit_id*/126331, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハッピーバースデー！\n今日という日の記念に、\nボクを撮影する権利をあげるよ！\nさぁ、思う存分、ボクを撮って！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1264001, /*unit_id*/126401, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/2.8, /*change_face*/2, /*description*/"こう見えてもわたし、\nシュヴァリエなんですっ！\n『王家の装備』を\nお守りしてるんですよっ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1264002, /*unit_id*/126401, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ライラエルさまは\nとってもとっても優しいんですっ\nいつも笑顔でわたしの話を\n聞いてくださるんですよ～っ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1264003, /*unit_id*/126401, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/4.0, /*change_face*/2, /*description*/"ほんとはハグとか握手とか\nいっぱいしたいんですけどねっ？\nわたしのパワーってちょっと\nすごすぎちゃって！　我慢です～", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1264004, /*unit_id*/126401, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/5.3, /*change_face*/2, /*description*/"わたしを充填できる人が\nもう一人いるなんて、ぜーんぜん\n知りませんでしたっ！\nナイトさんってすごいんですね♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1264005, /*unit_id*/126401, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"ナイトさんのこと、\nもっともっと教えてくださいっ！\nわたし、あなたに\n興味津々みたいなんですっ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1264006, /*unit_id*/126431, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"戦うことはあんまり楽しくない\nですけど…悪い魔物さんを\n放置なんて絶対できませんっ！\nクローチェ、戦闘開始しますっ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
