INSERT INTO `unit_comments` VALUES (/*id*/1264012, /*unit_id*/126431, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうございます！\n今日はどんなお願いだって\nフルパワーで叶えちゃいますっ！\nさあさあ、なんなりとっ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1265001, /*unit_id*/126501, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/4.0, /*change_face*/2, /*description*/"『ジオ・テオゴニア』を\nわたしは愛しています\nあなたにも気に入って\nいただけると…うれしく思います", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1265002, /*unit_id*/126501, /*use_type*/1, /*voice_id*/2, /*face_id*/4, /*change_time*/6.7, /*change_face*/1, /*description*/"頼りになる家臣たちがいなければ\nわたしは国主たり得ません\n…みなの想いに応えるためにも\n迷いがあってはいけませんね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1265003, /*unit_id*/126501, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/7.3, /*change_face*/4, /*description*/"『王家の装備』の力は強大で…\n反動も覚悟しなければなりません\n…傲慢なわたしを見ても\nどうか怖がらないでください", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1265004, /*unit_id*/126501, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/4.3, /*change_face*/6, /*description*/"かしこまった服装は\nあまり好みではないのです\nどうにも窮屈に感じてしまって…\nそれほど奇抜ではないですよね？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1265005, /*unit_id*/126501, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/4.0, /*change_face*/2, /*description*/"これでも、しっかり者だと\n言われているのですよ？\n…あなたには気を抜いたところを\nよく見られてしまっていますが", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1265006, /*unit_id*/126531, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなた方は希望の光…\nその光を守るため\nあらゆる困難を退けましょう\n…どうか安心してくださいね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1265007, /*unit_id*/126531, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"天使のように見えた…ですか\nそれは羽のせいでしょうか\nあるいは…別の理由が？\nふふ、教えてくださいませんか♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1265008, /*unit_id*/126531, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"偽ることも後ろ暗いこともなく\nあなたと向き合うことができる…\nああ、なんと晴れやかな…\nなんと清々しい心持ちでしょう！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1265009, /*unit_id*/126531, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなたはためらうことなく\n手を差し伸べてくださいましたね\n…ですから、わたしも\n大丈夫。もう迷いはありません", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1265010, /*unit_id*/126531, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"高き天から舞い降りて\n大地の上に立つというのも\n悪くはないかもしれません\n…そう、あなたの隣であれば", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1265011, /*unit_id*/126501, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"お誕生日おめでとうございます\nあなたの未来が\n幸多きものになりますように…\n心から祈っています", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1265012, /*unit_id*/126531, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日という日の奇跡のおかげで\nあなたと出会うことができました\nだから…どうか言わせてください\n万感の想いを込めた感謝と祝福を", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1266001, /*unit_id*/126601, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/7.1, /*change_face*/6, /*description*/"暑さ対策は万全にすべきです\nこの麦わら帽子を被りなさい\n…私の帽子とお揃いなことに、\n特に深い意味はありませんよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1266002, /*unit_id*/126601, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/7.2, /*change_face*/1, /*description*/"秘密とは人にとって大事なもの…\n貴方も秘密を作るといいでしょう\nただし、私にだけはその内容を\n共有すること。いいですね？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1266003, /*unit_id*/126601, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/7.9, /*change_face*/7, /*description*/"七冠とは利己的な存在\n当然、私も例外ではありません\nというわけで、貴方には私の夏に\n徹底的に付き合ってもらいますよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1266004, /*unit_id*/126601, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/12.2, /*change_face*/2, /*description*/"ふむ…大人らしい水着で、\n私によく似合っている、と…\n褒め方を教えた覚えはないですが\nしかし…悪くありませんね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1266005, /*unit_id*/126601, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/7.6, /*change_face*/2, /*description*/"おや、日差しが眩しいのですか？\nでは私のサングラスを使いなさい\n…ふふっ。いえ、失礼\nお似合いですよ、かわいらしくて", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1266006, /*unit_id*/126631, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私が…いえ、私たちが\n存分にもてなしてあげますよ\n貴方の望む夏を\n実現してあげましょう", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1266007, /*unit_id*/126631, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"…考えてみれば、こうして\nゆっくり過ごす時間など\nいつ以来でしょうか\n…想い出に残る夜にしましょう", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1266008, /*unit_id*/126631, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日くらいはすべて忘れて\n羽を伸ばすことにします\n貴方もそうしなさい？\n我々は日々働きすぎなのですから", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1266009, /*unit_id*/126631, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"このナイトプールは貸切です\nつまり、ここでの出来事は\nなにもかも我々だけのもの…\n今夜のことは秘密ですよ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1266010, /*unit_id*/126631, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"呆れるほどのお人よし…\n貴方を一言で表せばこうも単純\nなのに、私は貴方になれない…\nこの秘密をいつか暴いてみせます", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1266011, /*unit_id*/126601, /*use_type*/2, /*voice_id*/6, /*face_id*/6, /*change_time*/10.2, /*change_face*/2, /*description*/"虫取り網をプレゼントした程度で\nそこまで喜ぶとは…まあ、\n気に入ったのならいいのですが\n…ハッピーバースデー", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1266012, /*unit_id*/126631, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"夏らしく貴方の誕生日を\n祝わせてもらいましょう。分身に\n腕利きの花火職人を模倣させます\n思う存分、花火を楽しみなさい", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1267001, /*unit_id*/126701, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/1.8, /*change_face*/2, /*description*/"夏といえば…いえ春夏秋冬\nいつでもマイシーズンですわ！\n私に似合わない季節など\nありませんもの。そうでしょう？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1267002, /*unit_id*/126701, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/6.5, /*change_face*/1, /*description*/"きらめく海に輝く太陽…\nそして魅力的な私！\n夏を楽しむのに、これ以上\n必要なものがありまして？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1267003, /*unit_id*/126701, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/2.6, /*change_face*/2, /*description*/"あなたさま、夏ですわ！\nウィスタリア家の威信を\nかけたバカンスの始まりを\nここに宣言しますわよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1267004, /*unit_id*/126701, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/3.0, /*change_face*/2, /*description*/"夏のグルメ、夏のレジャー…\n全て楽しみ切るなら\nいくら時間があっても\n足りませんわね♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1267005, /*unit_id*/126701, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/6.8, /*change_face*/2, /*description*/"叶うならあなたさまと\nこの時間を永遠に…なんて\n…ふふ、何でもありませんわ\n先へ進んでこそ私たちですもの！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1267006, /*unit_id*/126731, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"いつも慌ただしいからこそ\n今だけはあなたさまと…\nこの潮騒と夕焼けのひと時に\n身を委ねていたいのです", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1267007, /*unit_id*/126731, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ほら、あなたさまも\nもっと近くに来てくださいな\nふふっ…捕まえましたわ♪\nさ、こちらで横になって…♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1267008, /*unit_id*/126731, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ずっと走り続けていれば\nいつか疲れ切ってしまいますわ\nさざ波の音に耳を傾け\nしばしの休憩といたしましょう♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1267009, /*unit_id*/126731, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"夏の美しさに目を奪われるのも\n結構ですが、ちゃんと私も\n見てくださいませんと\n拗ねてしまいますわよ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1267010, /*unit_id*/126731, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"太陽はまばゆく燃える\nばかりではありませんわ\n夕焼けのように甘く焼き付く…\nそんな一面もありますのよ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1267011, /*unit_id*/126701, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/9.0, /*change_face*/6, /*description*/"ハッピーバースデーですわね！\nささやかなお祝いですが、記念に\n打ち上げ花火を用意しましたの\n一万発では少なすぎるかしら？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
