INSERT INTO `unit_comments` VALUES (/*id*/1270002, /*unit_id*/127001, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/8.0, /*change_face*/5, /*description*/"はうぅ…視線を感じます…\n水着は肌の露出が多くて\nちょっと恥ずかしいです…\n体が熱くなって…それがイイ…！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
