INSERT INTO `unit_comments` VALUES (/*id*/1270004, /*unit_id*/127001, /*use_type*/1, /*voice_id*/4, /*face_id*/3, /*change_time*/7.8, /*change_face*/5, /*description*/"水着のクウカを前に、ドＳさんは\n「まだ布の面積が多いな…\n俺が減らしてやるぜ」\nと言い放ち…じゅるり…♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1270005, /*unit_id*/127001, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/6.2, /*change_face*/1, /*description*/"海に二人で来るのって\nなんだか…照れくさいです、ね\n何をしたらいいんでしょう？\nドＳさん…教えてくれますか？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
