INSERT INTO `unit_comments` VALUES (/*id*/1271002, /*unit_id*/127101, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/7.1, /*change_face*/2, /*description*/"この水着、どうでしょうか？\n普段は黒ばかり着てますが\nこれは幽霊っぽくて\n涼しげで私は好きです", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1271003, /*unit_id*/127101, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/6.8, /*change_face*/2, /*description*/"お水や帽子、日焼け止めなど\n日差し対策は万全です\nさあ、安全で健やかに\n夏を満喫しましょう", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1271004, /*unit_id*/127101, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/2, /*description*/"海や川、湖などに行くのなら\nお付き合いしますよ\n水場は霊が集まりやすいので\n私がお守りします", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1271005, /*unit_id*/127101, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/8.4, /*change_face*/2, /*description*/"すみません、夏の海はあまり\n縁がないので楽しみ方が\n分からなくて…よければ教えて\nいただけますか？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1271006, /*unit_id*/127131, /*use_type*/1, /*voice_id*/1, /*face_id*/4, /*change_time*/0.0, /*change_face*/2, /*description*/"今の私は海に溶けゆく白き\n泡沫のよう…ふふ、夏のせい\nでしょうか、がらにもなく詩的に\nなってしまいました", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1271007, /*unit_id*/127131, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"海はときに恐ろしい一面を\n見せます。でもどうか恐れないで\n静かに身を任せれば必ず\n受け入れてくれるはずだから", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1271008, /*unit_id*/127131, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"昏く、涼やかな静寂の世界\n海がこんなにも落ち着くのは\n霊の世界に似ているからなのかも\nしれません", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1271009, /*unit_id*/127131, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"もし暑いようでしたら\nもっと私のそばにどうぞ…\n体温が低いので\nきっと涼しいと思います…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1271010, /*unit_id*/127131, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"青く昏い海の底でこのまま\nあなたと二人永遠に…\nここにいるとそんな夢想ばかり\nしてしまいます", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1271011, /*unit_id*/127101, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"お誕生日おめでとうございます\n占いによると海が縁起がいいと\nでましたので、そちらでお祝い\nさせてください", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1271012, /*unit_id*/127131, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"これ、誕生日プレゼントです…\n流木と貝殻で作った特製のお守り\nしっかりご祈祷したので、\nあらゆる魔を防ぐはずです", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1272001, /*unit_id*/127201, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/9.5, /*change_face*/5, /*description*/"制服ってけっこういいわね\nなんとなく気が引き締まるし\nそれに意外と…かわいいし？\nや…別に頷かなくていいから…！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1272002, /*unit_id*/127201, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/8.6, /*change_face*/4, /*description*/"今日の３時間目体育か～…ねえ、\nかったるいし屋上でサボらない？\n…はいはい、やっぱダメよね～\n分かりました～行くってば～", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1272003, /*unit_id*/127201, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/9.5, /*change_face*/3, /*description*/"なんで青い春っていうのかしらね\n燃えるような赤い春でも、希望に\n満ちた黄色い春でもよくない？\n…めんどくさいって顔すんな～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1272004, /*unit_id*/127201, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/5.6, /*change_face*/5, /*description*/"ねー新しくクレープ屋ができたん\nだって。今日帰りに二人で…\n…って、べ…別に放課後デートの\nお誘いとかじゃないんだから！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1272005, /*unit_id*/127201, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/7.7, /*change_face*/2, /*description*/"今日、夕方から雨らしいわよ。\n…へ～、傘持ってきてないんだ？\nったくしょうがないわね～\n入れてあげるから感謝しなさい！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1272006, /*unit_id*/127231, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あいつ遅いな…待ち合わせ場所、\nここで合ってるわよね…あれ\nやっぱり裏門だったかも…？\nもう…不安になるから早く来い！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1272007, /*unit_id*/127231, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"マジックアワーって名付けた人、\nきっとロマンチストな魔法使いね\n幻想的な空の下で、\nあいつとのんびり下校したいな…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1272008, /*unit_id*/127231, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"放課後暇だし部活とかしようかな\n吹奏楽とか美術とか…あたし、\n芸術的センスあると思うのよね～\n今度あいつと見学いこうかしら♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1272009, /*unit_id*/127231, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"なーんか最近、青春のせいで\n感情駄々洩れ気味なのよね…\nうっかりしてるとあいつに告は…\n…って、うっかりすんなあたし！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1272010, /*unit_id*/127231, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"まっすぐ寮に帰らないで\nどこかに寄り道しちゃいたいな…\n目的地なんてないけど、このまま\nあんたと一緒に歩きたいだけよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1272011, /*unit_id*/127201, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/4.5, /*change_face*/6, /*description*/"へーあんた今日誕生日なんだー\n知らなか…って教室行くの禁止！\nみんなでサプライズパーティーの\n準備してんだからね！？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1272012, /*unit_id*/127231, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日おめでと～！\n空の方見てて。今から魔法で、\nあんたの歳の数のバースデー花火\nぶっぱなすわよ～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1273001, /*unit_id*/127301, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/7.9, /*change_face*/7, /*description*/"ヒデサイ、どーうちの制服？\nアオハルって感じっしょ？\nどういう意味って…それは\nうちと一緒に見つけるんじゃん♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1273002, /*unit_id*/127301, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/7.9, /*change_face*/1, /*description*/"ねねね、ヒデサイは何派？\nセーラー、ブレザー、ボレロ、\nどれが好き？　教えて！\n…あ、もしかして全部好きの人？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1273003, /*unit_id*/127301, /*use_type*/1, /*voice_id*/3, /*face_id*/4, /*change_time*/4.9, /*change_face*/1, /*description*/"魔法まじムズい\n九九といい勝負してるよ\nあ、もしかして両方できたら\nブンブリョードーってやつ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1273004, /*unit_id*/127301, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/6.1, /*change_face*/7, /*description*/"ヒデサイ、暇な感じ？\n制服デートしよーよ\n楽しそーっしょ？\nじゃあ放課後よろ～♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1273005, /*unit_id*/127301, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/6.9, /*change_face*/5, /*description*/"ヒデサイといるとさ\nバイブス上がるんだよね\n楽しいし、ウキウキするし…\nうちに何か魔法かけたっしょ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1273006, /*unit_id*/127331, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ねねね、ヒデサイ\nみんなに見つからないように\n隠れるのドキドキして楽しーね！　\nあっ、そっか今は、しー…だね♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1273007, /*unit_id*/127331, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"二人でこーしてるとさ\nなんか少女漫画っぽくね？\nキラキラでアオハル系のやつ\nふふ、もうちょっと浸ってこーよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1273008, /*unit_id*/127331, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"てゆーか、いい時間だし\nちょっちお腹減ったね…\nあ、そだ、お菓子あるし食べる？\nふふ、センセーには内緒だよ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1273009, /*unit_id*/127331, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この屋上でも色々あったね\nヒデサイとの思い出いっぱいだよ\n…せっかくだし、今日も\n二人だけの思い出作っちゃう？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1273010, /*unit_id*/127331, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"割と風あるし\nちょっち冷えてきたね\nヒデサイも寒いっしょ？\n…ね、そっち寄ってもいい？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1273011, /*unit_id*/127301, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/7.0, /*change_face*/7, /*description*/"ヒデサイ、おたおめ～！\nこないだ、パないお店\n見つけたから、一緒いこーよ\nもち、今日はうちがおごるし！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1273012, /*unit_id*/127331, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ヒデサイ、誕生日だよね\n今日はうちがハッピーな日に\nするから、覚悟してて！\nたくさんお祝いしちゃうよ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1275001, /*unit_id*/127501, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/7.7, /*change_face*/2, /*description*/"この格好、地元の子どもたちが\nきれいだって褒めてくれたわ♪\nあんたの感想も聞かせて？\n…ふふ、ありがと♪　うれしいわ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1275002, /*unit_id*/127501, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/9.9, /*change_face*/6, /*description*/"その土地の匂いってあるわよね\nサラサリアだとスパイスの香りに\nお菓子と果物の甘い匂いも…\nあ～、おなか空いてきちゃった…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1275003, /*unit_id*/127501, /*use_type*/1, /*voice_id*/3, /*face_id*/4, /*change_time*/6.2, /*change_face*/1, /*description*/"砂漠を渡る長旅なんて\n正直、一人だと心細かったのよ\nあんたが居てくれてよかったわ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1275004, /*unit_id*/127501, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/6.9, /*change_face*/5, /*description*/"大通りだけあって人が多いわね…\n迷子にならないようにしないと\nたとえば…う、腕を組む、とか？\n…ほら、照れてないで早くする！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1275005, /*unit_id*/127501, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/9.0, /*change_face*/2, /*description*/"いつもあんたが居てくれるから\nどこにいたって頑張れるの\n改まって言うと、変な感じね？\nでも…今言っておきたかったのよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
