INSERT INTO `unit_comments` VALUES (/*id*/1280008, /*unit_id*/128031, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ひゃあっ！？　む、胸に…胸に！\nこの量ですら麦しゅわが何杯も\nおかわりできますのに…！\nああ…なんてもったいない～っ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1280009, /*unit_id*/128031, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ちょっとちょっと秘書１号？\nなぜ少年に撮影を任せようと\nしていますの？　小休憩？\nまったく…少しだけですわよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1280010, /*unit_id*/128031, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"貴方にはジュースがお似合いです\nただ…そうですわね\nいつか大人になったあかつきには\nご一緒してあげてもよろしくてよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1280011, /*unit_id*/128001, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/5.1, /*change_face*/2, /*description*/"わたくしの記憶によれば\n今日は貴方のお誕生日でしたわね\nおめでとうございます\nケーキの一つでもいかがかしら？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1280012, /*unit_id*/128031, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうございます\n本日ばかりはケチケチせずに\n貴方の希望を聞いて差し上げます\nさ、言ってごらんなさいな", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1283001, /*unit_id*/128301, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/6.9, /*change_face*/8, /*description*/"どうです？　大幹部にふさわしい\nゴージャスな装いってヤツです！\nさあ、思う存分\n褒めるがいいですよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1283002, /*unit_id*/128301, /*use_type*/1, /*voice_id*/2, /*face_id*/8, /*change_time*/8.9, /*change_face*/2, /*description*/"ふっふっふ、新年早々\nあたしの晴れ着姿を見られて\nお兄さんは運がいいですね！\nさあ、お年玉をよこすですよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1283003, /*unit_id*/128301, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/5.4, /*change_face*/8, /*description*/"トーゴクにはドラゴンの年\nってのがあるらしいですね\nまさにあたしのための年\nじゃないですか！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1283004, /*unit_id*/128301, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/2.2, /*change_face*/3, /*description*/"なんですか？　いくらあたしの\n晴れ着姿がキレイだからって\nそんなにジロジロみてたら\n通報もんですからね？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1283005, /*unit_id*/128301, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/7.6, /*change_face*/2, /*description*/"はー、どこも人ごみだらけで\nまともに歩けないですね…\nはぐれちゃ大変ですから\n手をつないどいてやるですよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1283006, /*unit_id*/128331, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ふっふっふ、あたしの勝ちです！\nさーて、何を描いてやるですかね\n覚悟するですよ～♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1283007, /*unit_id*/128331, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あたしの顔に落書きした罰です\nその顔にたーっぷりと\n仕返ししてやるですからね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1283008, /*unit_id*/128331, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"やっぱりあたしが最強です！\nこの調子で勝ちまくって\nランドソルをせーふくして\nやるのもいいですね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1283009, /*unit_id*/128331, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ま～たあたしの勝ちですね！\nお兄さんザコザコすぎですよ\nもしかしてあたしに描いて\nほしくて手を抜いてやがるです？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1283010, /*unit_id*/128331, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"イ、ノ、リ、さ、ま、専、用っと\nふっふっふ、これでお兄さんは\nあたしの所有物ですよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1283011, /*unit_id*/128301, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/3.0, /*change_face*/8, /*description*/"お誕生日おめでとうですよ！\nさあ、このあたしの晴れ姿、\nプレゼント代わりに\n目に焼き付けとくがいいです！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1283012, /*unit_id*/128331, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"さあ、バースデープレゼントは\nあたしの直筆サインです！\nこうやってたっぷりの墨で…\nふふっ、遠慮はいらねーですよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1801001, /*unit_id*/180101, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/4.2, /*change_face*/2, /*description*/"チェンジ、プリンセスフォーム！\n…くぅぅー！　やっぱり変身って\nすっごくワクワクするよね！\n騎士くん、一緒にポーズとって！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1801002, /*unit_id*/180101, /*use_type*/1, /*voice_id*/2, /*face_id*/4, /*change_time*/0.0, /*change_face*/4, /*description*/"ううぅ…変身した後は\nお腹が減るって言ってたけど、\nほんとなんだね…\nご飯まだかなぁ…お腹空いたな", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1801003, /*unit_id*/180101, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/7.8, /*change_face*/2, /*description*/"この力で、世界中の人たちを\n助けにいくよ！　騎士くんも\n何かあったら言ってね？\n困った時はお互いさまさま♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1801004, /*unit_id*/180101, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/11.0, /*change_face*/2, /*description*/"えっと…騎士くん\n聞いていいかな？　このカッコ…\n…え？　似合ってる…？\nえへへっ、ほめられちゃった♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1801005, /*unit_id*/180101, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/10.1, /*change_face*/2, /*description*/"騎士くん、ちょっとほっぺ触るね\nどうどう？　肉球、\nいつもよりあったかいでしょ？\nこれぞ、プリンセス肉球だー♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1801006, /*unit_id*/180131, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"うにゃにゃにゃにゃー！\nパワーが溢れて止まらない！\nこれがあたしと騎士くんの！\nプリンセスフォームの力だぁー！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1801007, /*unit_id*/180131, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"すごい力だよね…だけど、\nあたし、使いこなしてみせるよ！\n大切な人たちに、\nもう傷ついて欲しくないもん！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1801008, /*unit_id*/180131, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あたしの姿が変わっても、\n騎士くんはそのままなんだ…あ！\nこれと同じ服作る？　そしたら\n一緒にプリンセスフォームだよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1801009, /*unit_id*/180131, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"変身するとね？\n胸の奥が燃えてるみたいに\n熱くなるんだ…\n騎士くん、あたしに何かした？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1801010, /*unit_id*/180131, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この力は、騎士くんと一緒に\nいないと使えないんだよね？\nじゃあ、ずーっといちゃおっかな\n…じょ、冗談！　冗談だよー！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1801011, /*unit_id*/180101, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/5.9, /*change_face*/1, /*description*/"お誕生日おめでとう、騎士くん！\nお祝いになんでも食べて！\n串焼き…リンゴ飴…うーん、\nそうだ！　全部食べちゃおー！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1801012, /*unit_id*/180131, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハッピーバースデー、騎士くん！\nあたしにしてほしいこと、ない？\n今日は騎士くんのためだけに\n超頑張っちゃうよ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1802001, /*unit_id*/180201, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/3.5, /*change_face*/7, /*description*/"チェンジ・プリンセスフォーム…\nあれ！？　変身しちゃってる！？\n騎士クン！　今のはちょっと、\n言ってみたかっただけで…！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1802002, /*unit_id*/180201, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/9.5, /*change_face*/2, /*description*/"変身したらお腹が減るけど\n何かを頑張った後のごはんって\n美味しいから、楽しみだよね\n今日は何を食べようかな…♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1802003, /*unit_id*/180201, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/13.3, /*change_face*/7, /*description*/"この力を使ってる時、騎士クンの\n格好は変わらないんだね…\nその…わたしがプリンセスなら\n王子様みたいに…なんでもない！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1802004, /*unit_id*/180201, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/5.3, /*change_face*/5, /*description*/"騎士クンのためなら、わたしは\nどんなことが起きても平気だよ\n騎士クンと一緒にいられて、\n騎士クンが笑顔でいてくれたら…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1802005, /*unit_id*/180201, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/3.3, /*change_face*/7, /*description*/"騎士クンと二人でパワーアップ…\nううぅ～…すごくうれしくて、\n胸の奥の気持ちが溢れ出そう\nこれも力の影響なのかな…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1802006, /*unit_id*/180231, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"たとえこの先に、何があっても…\n騎士クンを、みんなを守りたいの\nわたしの、全てをかけて！\nこの思いは…誰にも負けない！　", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1802007, /*unit_id*/180231, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"これだけの大きな魔力、\n本当に使いこなせるのかな…\n…ううん、わたしはもう迷わない\nこの瞬間に、全力を出し切る！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1802008, /*unit_id*/180231, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"戦うのは、わたしにまかせて！\n今の幸せも、未来の幸せも\n壊させたりしないよ…！\n今度こそ！　必ず！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1802009, /*unit_id*/180231, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"騎士クンのあったかい力\n前よりもすごく強くなってる…\nわたしの気持ちも、同じだよ\n騎士クンの気持ちは…どう？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1802010, /*unit_id*/180231, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"戦いや普段の時だけじゃなくて\nもっと色んな時間を騎士クンと\nずっっと一緒に過ごしたい…\n…まずは、食事に誘わなくちゃ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1802011, /*unit_id*/180201, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/6.5, /*change_face*/5, /*description*/"お誕生日おめでとう！\n今日はずっと騎士クンを\nお祝いしてあげたいの\nだから、そばにいてもいい…？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1802012, /*unit_id*/180231, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"騎士クン、お誕生日おめでとう！\n…お祝いの気持ちだけじゃなくて\nもう一つ、伝えたいことがあるの\n…やっぱり言えないよぉ～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1803001, /*unit_id*/180301, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/4.5, /*change_face*/2, /*description*/"プリンセスフォーム…\nすごい力だね。ありがとう\nキミのおかげで私はまた\n一つ強くなれたよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1803002, /*unit_id*/180301, /*use_type*/1, /*voice_id*/2, /*face_id*/3, /*change_time*/3.8, /*change_face*/6, /*description*/"チェンジ！　プリンセスフォー…\nキミ！？　いつからそこに！？\n今のはその、かけ声の練習で…\n…え？　もっと凛々しく？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1803003, /*unit_id*/180301, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/4.6, /*change_face*/1, /*description*/"変身できるからといって\n日々の鍛錬は欠かさないよ\nそうだ。キミも一緒にどう？\n特別メニューを組んであるんだ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1803004, /*unit_id*/180301, /*use_type*/1, /*voice_id*/4, /*face_id*/4, /*change_time*/5.6, /*change_face*/6, /*description*/"やっぱり私が着るには、\nヒラヒラしたものが多すぎる…\n似合ってる？　そうかな…\n…ねえキミ。全部聞いてたの？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1803005, /*unit_id*/180301, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/9.2, /*change_face*/6, /*description*/"この姿でいると、まるでキミに\n抱き締められているようだ\nふふっ、冗談だよ…キミ？\nじ、実際にはしなくていいから！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1803006, /*unit_id*/180331, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"見ていてくれ！\nこれこそ私の思いが…\n私たちの力が\n一つになった証だ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1803007, /*unit_id*/180331, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"何度強敵が立ちはだかろうとも\n私は決して挫けない！\n世界の滅びを食い止めてでも\nキミのそばにいてみせる！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1803008, /*unit_id*/180331, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"限界なんてものはない…\nキミとの絆が私に教えてくれたよ\nだから私は剣を振るい続ける！\nあらゆる困難を断ち切るために！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1803009, /*unit_id*/180331, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"キミへの思いが強さになるなら\nもっと早く素直に\nなっておけばよかったよ…\nどんな思いか？　そ、それは…！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1803010, /*unit_id*/180331, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私とキミ…お互いへの気持ちを\nより強くするにはどうしたら…\n剣を交える以外の方法を\n見つけないといけないな", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1803011, /*unit_id*/180301, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/7.6, /*change_face*/5, /*description*/"今日はキミの誕生日だ\nプレゼントを贈らせてほしいから\n一緒に買い物へいかない？\n…変身を解いてからね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1803012, /*unit_id*/180331, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日おめでとう！\nキミを祝えて私もうれしいよ\n次の誕生日もまた\nキミの隣で祝えるといいな", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1804001, /*unit_id*/180401, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/4.5, /*change_face*/1, /*description*/"オイッスー☆\n一緒にお散歩、行きませんか？\n王女様なんて呼んじゃだめですよ\n今のわたしはペコリーヌです☆", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1804002, /*unit_id*/180401, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/10.9, /*change_face*/2, /*description*/"いつもと違うこの姿、\nあなたはどう思いますか？\n強そう？　う～ん、そっちですか\nかわいい？　えっへへ、それです", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1804003, /*unit_id*/180401, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/7.1, /*change_face*/2, /*description*/"王宮から抜けだして会っていると\nなんだかドキドキしますね♪\nお付きの護衛もいませんから\nあなたが守ってくださいね☆", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1804004, /*unit_id*/180401, /*use_type*/1, /*voice_id*/4, /*face_id*/4, /*change_time*/4.2, /*change_face*/1, /*description*/"わたしがごはんのことばかり\n考えてると思っていませんか？\n半分は正解ですけど、もう半分は\n…あなたのことですよ☆", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1804005, /*unit_id*/180401, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/7.7, /*change_face*/6, /*description*/"やっぱりあなたと囲む食卓が\n一番ですね☆　いっそのこと\n王宮で一緒に住みませんか？\nやっぱり、まずいですかね～？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1804006, /*unit_id*/180431, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"超全力全開☆\n私は守られるだけの王女様じゃ\nありませんよ？　この国と…\nあなたのために、戦います！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1804007, /*unit_id*/180431, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この姿、とっても強いんですけど\nその分お腹が減るんですよ～…\nでも、それってつまり…\nもっとごはんが食べられますね☆", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1804008, /*unit_id*/180431, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"力が溢れ出て、止まりません！\nこの力で、わたしの大切なものを\nぜ～んぶ守ってみせますね\n今度は何もなくしたりしません！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1804009, /*unit_id*/180431, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなたの力をこれまでより\n強く感じます…温かくて、\n胸がキュンキュンしちゃいます…\nえへへ、やばいですね☆", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1804010, /*unit_id*/180431, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お腹が空きましたね！\nさぁ、わたしと一緒にごはんを\n食べに行きましょう♪\n今日も明日も仲良くごはんです☆", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1804011, /*unit_id*/180401, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/8.1, /*change_face*/5, /*description*/"あなたの誕生日となれば、\n国を挙げてお祝いしなきゃですね\nそれとも二人きりがいいですか？\n…えへへ、照れちゃいますね☆", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1804012, /*unit_id*/180431, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日はあなたの誕生日ですね\nおめでとうございます！　\nそれと…ありがとうございます\nこうしてわたしと出会ってくれて", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1805001, /*unit_id*/180501, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/6.1, /*change_face*/2, /*description*/"姿形が変わっても\nわたくしはわたくしのまま…\nですが、これまで以上に\n主さまにお仕えいたします", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1805002, /*unit_id*/180501, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/4.5, /*change_face*/2, /*description*/"主さまが変身するところも\n見とうございますね\nわたくしに続いて\n掛け声をどうぞ。せーの…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1805003, /*unit_id*/180501, /*use_type*/1, /*voice_id*/3, /*face_id*/5, /*change_time*/5.3, /*change_face*/6, /*description*/"プリンセス耳かきに\nプリンセスふーふー…\nあ、いえ。この姿でのサポートは\n一味違ったものにしてみようかと", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1805004, /*unit_id*/180501, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/5.5, /*change_face*/1, /*description*/"この姿はまるでおとぎ話の\n妖精のようでございます\nいつも主さまの隣で\n舞っていたいものです", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1805005, /*unit_id*/180501, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/2.7, /*change_face*/2, /*description*/"『プリンセス・フォーム』…\nいつもより力が湧いてくるのは\n心の奥底で、主さまとの絆を\n強く感じるからでしょうか", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1805006, /*unit_id*/180531, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"わたくしの願いは、ただ一つ…\n末永く主さまの隣にいることです\nどんな困難もこの力で乗り越えて\n必ずや叶えてみせます！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1805007, /*unit_id*/180531, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"わたくしたちの旅がどんな結末を\n迎えようとも、主さまのために\n戦います。迷いはありません\nこの力が全ての証明でございます", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1805008, /*unit_id*/180531, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"もっと強く、もっと高く…\n絆が生んだこの羽で\n主さまと未来に羽ばたきます\nいつまでも…どこまでも一緒に…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1805009, /*unit_id*/180531, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"主さまが傷つくところを\nこれ以上見たくはありません\nいつも笑っていていただくために\nわたくしはこの力でお守りします", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
