INSERT INTO `unit_comments` VALUES (/*id*/1810012, /*unit_id*/181031, /*use_type*/1, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"昔の私は自分の力が怖かった…\nでも今は、どんな私を見ても\nアンが笑ってくれるから…だから\nもっと強くなりたいって願えた！", /*all_comments_flag*/0, /*target_unit_id*/124401, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
