INSERT INTO `unit_comments` VALUES (/*id*/1811001, /*unit_id*/181101, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/5.1, /*change_face*/5, /*description*/"私とリノちゃんがお揃いなんだし\n弟くんも着替えるべきだよねっ！\n…恰好よくタキシードとかどう？\nお姉ちゃん、隣を歩きたいな…♪", /*all_comments_flag*/0, /*target_unit_id*/128101, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1811002, /*unit_id*/181101, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/3.1, /*change_face*/5, /*description*/"姉妹の力でパワーアップだよっ♪\nリノちゃんとお姉ちゃんの\n愛の力で、大好きな弟くんを\n守り抜いてみせるからね…！", /*all_comments_flag*/0, /*target_unit_id*/128101, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1811003, /*unit_id*/181101, /*use_type*/1, /*voice_id*/3, /*face_id*/5, /*change_time*/6.9, /*change_face*/2, /*description*/"弟くんお兄ちゃんっ♪\n今ね、姉妹の絆を深めるために\nリノちゃんになりきってるの♪\n似てるでしょ？　似てるよねっ？", /*all_comments_flag*/0, /*target_unit_id*/128101, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1811004, /*unit_id*/181101, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お、弟くんっ、お姉ちゃんだぞ♪\n…いま、もっと絆を深めるために\nお姉ちゃんになりきってるんです\nわっ、笑わないでくださいよ～！", /*all_comments_flag*/0, /*target_unit_id*/128201, /*face_id_2*/6, /*change_time_2*/6.3, /*change_face_2*/4, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1811005, /*unit_id*/181101, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この姿は私とお姉ちゃんの\n繋がりの証、なんですよね\n心強くて…力が湧いてきます\nこれがお姉ちゃん力…え、違う？", /*all_comments_flag*/0, /*target_unit_id*/128201, /*face_id_2*/5, /*change_time_2*/12.6, /*change_face_2*/6, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1811006, /*unit_id*/181101, /*use_type*/1, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お兄ちゃんっ、ちょっと私の手を\n引いて、歩いてもらえませんか？\n…こんなにきれいな衣装ですから\n特別感を味わいたくて…えへへ♪", /*all_comments_flag*/0, /*target_unit_id*/128201, /*face_id_2*/2, /*change_time_2*/5.5, /*change_face_2*/5, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1811007, /*unit_id*/181131, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"姉と妹のダブルアタックだぞ♪\nえへへ、覚悟してね弟くん！\nパワーアップした私たちからは\nもう逃げられないんだからっ♪", /*all_comments_flag*/0, /*target_unit_id*/128101, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1811008, /*unit_id*/181131, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"弟くんと、リノちゃんと、私\nこの繋がりは特別なものなんだ\nだから必ず、守ってみせるからね\nあなたたちの、お姉ちゃんとして", /*all_comments_flag*/0, /*target_unit_id*/128101, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1811009, /*unit_id*/181131, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ねぇ…あとでお姉ちゃんと\n一緒にお出かけしない？\nこの衣装でデートしよっ♪\n…リノちゃんには内緒だよ♪", /*all_comments_flag*/0, /*target_unit_id*/128101, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
