INSERT INTO `unit_comments` VALUES (/*id*/1001006, /*unit_id*/100131, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"強くなるんだ…！\nあたしはみんなと…\nずっと笑顔でいたい！");
INSERT INTO `unit_comments` VALUES (/*id*/1001007, /*unit_id*/100131, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"傷だらけになっても大丈夫！\n支えてくれる人がいるから…\n何度でも立ち上がってみせる！");
INSERT INTO `unit_comments` VALUES (/*id*/1001008, /*unit_id*/100131, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"騎士くんが近くにいてくれたら\n元気100万倍って感じ！\n…ちょっと言い過ぎかな？");
INSERT INTO `unit_comments` VALUES (/*id*/1001009, /*unit_id*/100131, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"聞こえるよ…騎士くんの声\nどんなに離れてても、\nあたしには分かる！");
INSERT INTO `unit_comments` VALUES (/*id*/1001010, /*unit_id*/100131, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"たとえどんな人が相手でも、\n今は負ける気がしない…！\n支えてくれるあの人がいるから！");
INSERT INTO `unit_comments` VALUES (/*id*/1001011, /*unit_id*/100101, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/11.3, /*change_face*/2, /*description*/"ハッピーバースデー、騎士くん！\nプレゼントは何がいい？\nごはん？　装備？　それとも…\nそうだ！　修行に付き合うね！");
INSERT INTO `unit_comments` VALUES (/*id*/1001012, /*unit_id*/100131, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとー！\nはいこれ！　猫のお守り！\nあたしがそばにいない時は、\nきっと代わりに守ってくれるよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1001013, /*unit_id*/100161, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"うにゃにゃにゃにゃー！\n騎士くんが一緒なら\nあたしは無敵だもんね！\nいっくぞぉー！");
INSERT INTO `unit_comments` VALUES (/*id*/1001014, /*unit_id*/100161, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"どんなピンチだって、今日まで\n騎士くんと乗り越えてきた！\nこれからも二人でいれば\nあたしは負けないよー！");
INSERT INTO `unit_comments` VALUES (/*id*/1001015, /*unit_id*/100161, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"守りたい笑顔があるから\nあたしは戦える！\nだから騎士くん\nあたしのそばにいて…！");
INSERT INTO `unit_comments` VALUES (/*id*/1001016, /*unit_id*/100161, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"もっとドキドキを感じたい！\nよくわかんないけど、\nそうすれば騎士くんとなんだって\nできそうな気がするから！");
INSERT INTO `unit_comments` VALUES (/*id*/1001017, /*unit_id*/100161, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あたし、こんなに強かったっけ？\nきっと騎士くんのおかげだね！\nだからずっとそばにいてくれる？\nだ、だって、強くなりたくて…");
INSERT INTO `unit_comments` VALUES (/*id*/1002001, /*unit_id*/100201, /*use_type*/1, /*voice_id*/1, /*face_id*/4, /*change_time*/6.0, /*change_face*/5, /*description*/"あ、あの…\nわたしが話し相手で、\n本当にいいの？\n…そっか。ありがとう…！");
INSERT INTO `unit_comments` VALUES (/*id*/1002002, /*unit_id*/100201, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/4.0, /*change_face*/5, /*description*/"戦うなら、\n治療と援護はまかせて！\n攻撃はその、一緒に\n頑張って欲しい…かな");
INSERT INTO `unit_comments` VALUES (/*id*/1002003, /*unit_id*/100201, /*use_type*/1, /*voice_id*/3, /*face_id*/4, /*change_time*/4.0, /*change_face*/8, /*description*/"お願い…\nそんなにじっと見ないで…\nは、恥ずかしいよ…あうぅ…");
