INSERT INTO `unit_comments` VALUES (/*id*/1002005, /*unit_id*/100201, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/3.0, /*change_face*/5, /*description*/"何か手伝ったほうがいい？\nどこかへ行くなら、わたしも\n連れて行って欲しいな…");
INSERT INTO `unit_comments` VALUES (/*id*/1002006, /*unit_id*/100231, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"わたしに出来ることがあったら、\n何でも言ってね\nちょっとした怪我なら、\nすぐに治せるよ");
INSERT INTO `unit_comments` VALUES (/*id*/1002007, /*unit_id*/100231, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"騎士クンのためなら\nわたし…もっともっと\nがんばろうって思える！");
INSERT INTO `unit_comments` VALUES (/*id*/1002008, /*unit_id*/100231, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"癒しの輝きを…！\n見てるだけで癒されるって、\nそんな…なんか、\n恥ずかしくなってきちゃった…");
INSERT INTO `unit_comments` VALUES (/*id*/1002009, /*unit_id*/100231, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"どんな魔法を勉強しよう…\n恋が成就する魔法…\nそ、そんなのあるわけないよね！");
INSERT INTO `unit_comments` VALUES (/*id*/1002010, /*unit_id*/100231, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"新しく覚えた魔法はね、\n防炎、防寒、耐熱…あとは、\nのどに魚の骨が刺さらない\n魔法…内緒だよ？");
INSERT INTO `unit_comments` VALUES (/*id*/1002011, /*unit_id*/100201, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/7.9, /*change_face*/5, /*description*/"騎士クン。お誕生日おめでとう\nあの…これ、お祝いのケーキ！\nその…良かったら食べてみて！\nえっと…これからも、よろしくね！");
INSERT INTO `unit_comments` VALUES (/*id*/1002012, /*unit_id*/100231, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日おめでとう、騎士クン\n今日はケーキの他にも\nいろんな料理を用意したから\nみんなでお祝いしようね！");
INSERT INTO `unit_comments` VALUES (/*id*/1002013, /*unit_id*/100261, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"騎士クンが、大切な人が\n傷ついた時は癒してあげたい…\nそれが今の、わたしの願い");
INSERT INTO `unit_comments` VALUES (/*id*/1002014, /*unit_id*/100261, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"騎士クン、痛いところはない？\n傷はちゃんと治ってる？\n…よかった。もう大丈夫だね");
INSERT INTO `unit_comments` VALUES (/*id*/1002015, /*unit_id*/100261, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お願い騎士クン。これからも\nわたしに、勇気をください…\nもっと強く、優しくなるために！");
INSERT INTO `unit_comments` VALUES (/*id*/1002016, /*unit_id*/100261, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"思いの強さなら、わたしだって…\nでも、気持ちだけじゃダメだよね\nちゃんと行動で示さないと！");
INSERT INTO `unit_comments` VALUES (/*id*/1002017, /*unit_id*/100261, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"騎士クンを好きな気持ちが\nいつもわたしに勇気をくれる…\n騎士クン！？　今の聞いて…！？");
INSERT INTO `unit_comments` VALUES (/*id*/1003001, /*unit_id*/100301, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/4.0, /*change_face*/2, /*description*/"目的を果たすためなら、\nどんな努力でもする。キミにも\nそういう気持ちでいてほしいな");
