INSERT INTO `unit_comments` VALUES (/*id*/1003016, /*unit_id*/100361, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私にとってキミは、\nかけがえのない存在だよ\n今までも、この先も…");
