INSERT INTO `unit_comments` VALUES (/*id*/1004001, /*unit_id*/100401, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/2.5, /*change_face*/2, /*description*/"あっ、にいちゃんだー！\nねえねえ、\nミソギとなにして遊ぶ？");
