INSERT INTO `unit_comments` VALUES (/*id*/1004010, /*unit_id*/100431, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ミソギねぇ、本当の\nにいちゃんが出来たみたいで、\nすっごくうれしいんだー♪");
INSERT INTO `unit_comments` VALUES (/*id*/1004011, /*unit_id*/100401, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/7.6, /*change_face*/2, /*description*/"誕生日おめでとう、にいちゃん！\nはい、バースデープレゼント！\nそれから、バースデーケーキ！\n早く開けて！　食べて！　わくわく");
INSERT INTO `unit_comments` VALUES (/*id*/1004012, /*unit_id*/100431, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日おめでと！　ミソギ、\nにいちゃん大好き！　本当だよ！\nんーイタズラばっかりしてるから\n信じてもらえないや…");
INSERT INTO `unit_comments` VALUES (/*id*/1004013, /*unit_id*/100461, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"にいちゃん！\nパワーアップしたミソギの\nスペシャルなイタズラ\nぜったい見逃さないでね！");
INSERT INTO `unit_comments` VALUES (/*id*/1004014, /*unit_id*/100461, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"にいちゃんとミソギなら\nどんな魔物もあっという間に\nやっつけられちゃうよね！\nえへへ～♪");
INSERT INTO `unit_comments` VALUES (/*id*/1004015, /*unit_id*/100461, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"にいちゃんと一緒に居ると\nどんどん力が湧いてくる！\nこの力でにいちゃんに\nすっごいイタズラしちゃお♪");
INSERT INTO `unit_comments` VALUES (/*id*/1004016, /*unit_id*/100461, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"どんどん新しいイタズラが\n浮かんでくるよ！\nあ～早く全部にいちゃんで\n試したいな～！");
INSERT INTO `unit_comments` VALUES (/*id*/1004017, /*unit_id*/100461, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"にいちゃんも\nミソギのイタズラからは\n逃げられないよ！\n覚悟してね♪");
INSERT INTO `unit_comments` VALUES (/*id*/1005001, /*unit_id*/100501, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/4.6, /*change_face*/2, /*description*/"あんたが困ったときは、是非とも\n自分を呼んでほしいッス！\n見習いでも、\n心はヒーローッスから！　");
