INSERT INTO `unit_comments` VALUES (/*id*/1005005, /*unit_id*/100501, /*use_type*/1, /*voice_id*/5, /*face_id*/4, /*change_time*/9.8, /*change_face*/5, /*description*/"あんたと一緒にもっともっと\n遊びたいッスけど…これ以上\n仲良くなってもし秘密が…\nい、いやその何でもないッス！");
INSERT INTO `unit_comments` VALUES (/*id*/1005006, /*unit_id*/100531, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"とぉーうっ！\n悪に立ち向かう勇気がある限り\n正義は必ず勝つッス！");
INSERT INTO `unit_comments` VALUES (/*id*/1005007, /*unit_id*/100531, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"うおおおっ！　燃えてきたー！\n真のスーパーヒーロー目指して\n突撃ッスー！");
INSERT INTO `unit_comments` VALUES (/*id*/1005008, /*unit_id*/100531, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"自分、世界平和のためなら\nどんな敵にも負けないッス！\nヒーローに二言は無いッスよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1005009, /*unit_id*/100531, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あんたがくれた勇気で\n最強のヒーローに\nバッチリ変身ッス！");
INSERT INTO `unit_comments` VALUES (/*id*/1005010, /*unit_id*/100531, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あんたと一緒なら勇気100倍、\nいや100万倍ッス！\nこれからも一緒に頑張るッスよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1005011, /*unit_id*/100501, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/5.3, /*change_face*/2, /*description*/"えっ、今日誕生日なんスか！？\nじゃあ、お祝いにとっておきの\nトラタイガーグッズあげるッス！\nお近づきの印ッスよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1005012, /*unit_id*/100531, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハッピーバースデー♪\nいつもお世話になってるアンタに\nトラタイガーのカップケーキッス！\n自分、手作りに挑戦したッスよ");
INSERT INTO `unit_comments` VALUES (/*id*/1006001, /*unit_id*/100601, /*use_type*/1, /*voice_id*/1, /*face_id*/5, /*change_time*/3.5, /*change_face*/2, /*description*/"お兄ちゃん、\nまたあとでサックスの練習、\n付き合ってくれますか？");
INSERT INTO `unit_comments` VALUES (/*id*/1006002, /*unit_id*/100601, /*use_type*/1, /*voice_id*/2, /*face_id*/4, /*change_time*/5.0, /*change_face*/5, /*description*/"はぁ、はぁ…\nもう練習できません…\nお兄ちゃん激しすぎます…");
INSERT INTO `unit_comments` VALUES (/*id*/1006003, /*unit_id*/100601, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/3.0, /*change_face*/4, /*description*/"え？　もっとするの…？\n今日はもうヘトヘト…\n私、限界です、お兄ちゃん…");
INSERT INTO `unit_comments` VALUES (/*id*/1006004, /*unit_id*/100601, /*use_type*/1, /*voice_id*/4, /*face_id*/5, /*change_time*/5.5, /*change_face*/2, /*description*/"お兄ちゃんったら\nいつも激しすぎて、私…\nドキドキが止まらないんです…");
INSERT INTO `unit_comments` VALUES (/*id*/1006005, /*unit_id*/100601, /*use_type*/1, /*voice_id*/5, /*face_id*/3, /*change_time*/4.5, /*change_face*/4, /*description*/"あのね、お兄ちゃんは\n私とお姉ちゃんとどっちが…\nううん、何でもないです…");
INSERT INTO `unit_comments` VALUES (/*id*/1006006, /*unit_id*/100631, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ふふ、お兄ちゃん見てください！\nジャズのリズムに乗って\nアカリ流ポールダンスです！");
INSERT INTO `unit_comments` VALUES (/*id*/1006007, /*unit_id*/100631, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お兄ちゃん、そんなに\nアカリのが見たいんですか…\nそ、それじゃ…恥ずかしいけど\n行きます！　アカリ大車輪―！");
INSERT INTO `unit_comments` VALUES (/*id*/1006008, /*unit_id*/100631, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"はあ、はあ…もう、らめぇ…\nお兄ちゃんが上手だって\n褒めてくれるから、つい\n調子に乗って回りすぎちゃった♪");
INSERT INTO `unit_comments` VALUES (/*id*/1006009, /*unit_id*/100631, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"め、目が回っちゃいました～\nお兄ちゃん抱っこしてください♪\nアカリをこんなにした責任…\nとってくださいね！");
INSERT INTO `unit_comments` VALUES (/*id*/1006010, /*unit_id*/100631, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"こんなアカリの姿、誰にでも\n見せるわけじゃないですよ？\nお兄ちゃんは特別だから、\nアカリの全てを見てほしいの♪");
INSERT INTO `unit_comments` VALUES (/*id*/1006011, /*unit_id*/100601, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/5.9, /*change_face*/5, /*description*/"あ！　お兄ちゃんったら、\nまた大きくなっちゃいましたね\nなんだかアカリ、\nドキドキしてきちゃいました～♪");
INSERT INTO `unit_comments` VALUES (/*id*/1006012, /*unit_id*/100631, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お兄ちゃん、お誕生日ですよね\nおめでとうございます！\nアカリと一緒に、\n大人の階段、上りましょうね♪");
INSERT INTO `unit_comments` VALUES (/*id*/1006013, /*unit_id*/100661, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"感じます、お兄ちゃんの力…\nあぁ、ドキドキが止まりません！\nいまのアカリなら、きっと\nなんでもできちゃいます♪");
INSERT INTO `unit_comments` VALUES (/*id*/1006014, /*unit_id*/100661, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お兄ちゃんのおかげで、アカリ、\n魔法だってうまくなったんです！\nかわいくて強い、最高の\nアカリになれちゃいました♪");
INSERT INTO `unit_comments` VALUES (/*id*/1006015, /*unit_id*/100661, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"見ててくださいね、お兄ちゃん！\n一緒に特訓してきた成果を\n見せちゃいますから♪");
INSERT INTO `unit_comments` VALUES (/*id*/1006016, /*unit_id*/100661, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お兄ちゃん、今日のアカリも\nかわいいですか？\nもう、恥ずかしがらないで、\nかわいいって褒めてほしいです～");
INSERT INTO `unit_comments` VALUES (/*id*/1006017, /*unit_id*/100661, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お兄ちゃんとの特訓はいつも\n『ハジメテ』だらけで楽しいです！\nこれからも二人で『ハジメテ』を\nいっぱい体験しましょうね♪");
INSERT INTO `unit_comments` VALUES (/*id*/1007001, /*unit_id*/100701, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/4.5, /*change_face*/2, /*description*/"１にプリン、２にプリン、\n３、４もプリンで、\n５も、もちろんプリンなの～！");
INSERT INTO `unit_comments` VALUES (/*id*/1007002, /*unit_id*/100701, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/6.0, /*change_face*/2, /*description*/"お空を飛ぶのが好きなの～\nイタズラするのも好きなの～\nでもプリンは\nもーっと好きなの～");
INSERT INTO `unit_comments` VALUES (/*id*/1007003, /*unit_id*/100701, /*use_type*/1, /*voice_id*/3, /*face_id*/7, /*change_time*/5.0, /*change_face*/6, /*description*/"魔物に追いかけられて、\n死ぬかと思ったの～\n…って、よく考えたら、\nもう死んでたの！");
INSERT INTO `unit_comments` VALUES (/*id*/1007004, /*unit_id*/100701, /*use_type*/1, /*voice_id*/4, /*face_id*/5, /*change_time*/7.0, /*change_face*/6, /*description*/"ミヤコは幽霊なの～\nだから、オマエが死んでも\nずっと一緒なの～♪\nん？　うれしくないの？");
INSERT INTO `unit_comments` VALUES (/*id*/1007005, /*unit_id*/100701, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/3.0, /*change_face*/5, /*description*/"今日は一緒にお出かけするのー\nだから今日はずっと、\nオマエに憑いてるの～");
INSERT INTO `unit_comments` VALUES (/*id*/1007006, /*unit_id*/100731, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"もっともっともっともっと\nたくさんプリンを買って来るの！\nそうじゃないとミヤコ、\n成仏しちゃうかもしれないの！");
INSERT INTO `unit_comments` VALUES (/*id*/1007007, /*unit_id*/100731, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ミヤコのプリンは、\n絶対にあげないの～！\nもし一口でも盗ったら、\n夢枕に立ち続けてやるの！");
INSERT INTO `unit_comments` VALUES (/*id*/1007008, /*unit_id*/100731, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"幽霊は人を怖がらせるものなの～\nオマエも、もっと怖がれなのー！");
INSERT INTO `unit_comments` VALUES (/*id*/1007009, /*unit_id*/100731, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ムー！　オマエはミヤコとプリン\nどっちが大事なの！？\n…どっちも大事？\nそれならいいの～");
INSERT INTO `unit_comments` VALUES (/*id*/1007010, /*unit_id*/100731, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ミヤコばかり幽霊でずるいの！\nオマエも死んで幽霊になるの！\nそうすれば、二人でずっと\nいっしょにいられるの～");
INSERT INTO `unit_comments` VALUES (/*id*/1007011, /*unit_id*/100701, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/5.8, /*change_face*/2, /*description*/"ミヤコは死んでるけど、誕生日の\nお祝いは大事にしてるの！\nだから早くバースデープリン\nパーティーを始めるの～♪");
INSERT INTO `unit_comments` VALUES (/*id*/1007012, /*unit_id*/100731, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"おめでとうなの！　誕生日は、\n仲良しとごちそうを食べるの\nだから今日は、ミヤコと一緒に\nプリンケーキを食べるの～♪");
INSERT INTO `unit_comments` VALUES (/*id*/1008001, /*unit_id*/100801, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/7.0, /*change_face*/2, /*description*/"あ、良いところに来てくれた！\nより可愛く見える角度を、\nさっき発見したんだよ！\nキミも、見て見て！");
INSERT INTO `unit_comments` VALUES (/*id*/1008002, /*unit_id*/100801, /*use_type*/1, /*voice_id*/2, /*face_id*/5, /*change_time*/6.5, /*change_face*/2, /*description*/"このボクの美しさ…\nいつか、「歩く観光名所」って\n呼ばれちゃったりするのかな？\nガイドブックを作らないと！");
INSERT INTO `unit_comments` VALUES (/*id*/1008003, /*unit_id*/100801, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/7.0, /*change_face*/1, /*description*/"あっ、キミ！　いいところに！\nボクの美しさがとどまることを\n知らないんだ！\n止めて！　いや、止めないで！");
INSERT INTO `unit_comments` VALUES (/*id*/1008004, /*unit_id*/100801, /*use_type*/1, /*voice_id*/4, /*face_id*/5, /*change_time*/4.5, /*change_face*/2, /*description*/"こんなに可愛いボクと\n一緒にいられること、喜んでね？\nキミは、世界一の\n幸せ者なんだから♪");
INSERT INTO `unit_comments` VALUES (/*id*/1008005, /*unit_id*/100801, /*use_type*/1, /*voice_id*/5, /*face_id*/5, /*change_time*/4.0, /*change_face*/2, /*description*/"ボクは、キミといたいから\nここにいるんだよ\n引き立て役ってわけじゃ\nないから、安心してね！");
INSERT INTO `unit_comments` VALUES (/*id*/1008006, /*unit_id*/100831, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"どんな宝石や宝物も…\nボクの美しさと輝きには\n勝てないんだなあ…\n罪作りな、ボク♪");
INSERT INTO `unit_comments` VALUES (/*id*/1008007, /*unit_id*/100831, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ああ、本当にボクは美しい♪\nボクの美しさを表現するには…\nいや、言葉じゃ表現できないよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1008008, /*unit_id*/100831, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ボクほど美しくない、なんて\n恥ずかしがることじゃないよね\nだって改めて言うまでもなく\n当たり前じゃない、そんなの♪");
INSERT INTO `unit_comments` VALUES (/*id*/1008009, /*unit_id*/100831, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"いつか、どんな宝石よりも\n価値のあるこのボクを…\nキミにプレゼントするって\n言ったら、キミは喜ぶかな？");
INSERT INTO `unit_comments` VALUES (/*id*/1008010, /*unit_id*/100831, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"このボクがここにいるという\nプレシャス…キミと\n二人で作っていくときめき…\n世界は希望に満ちているんだね");
INSERT INTO `unit_comments` VALUES (/*id*/1008011, /*unit_id*/100801, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/9.9, /*change_face*/2, /*description*/"誕生日おめでとう♪\n今日のキミは輝いて見えるね\n綺麗で美麗で華麗で壮麗だ\nきっと世界で二番目に美しいよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1008012, /*unit_id*/100831, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハッピーバースデー♪\n今日はボクの美しさを、\n独り占めにしてもいいよ♪\n全部受け止めきれるかな？");
INSERT INTO `unit_comments` VALUES (/*id*/1009001, /*unit_id*/100901, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/3.5, /*change_face*/2, /*description*/"貴様も早く前世の記憶を\n取り戻すのだ！　我が宿敵にして\n盟友、閃光のシグルドよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1009002, /*unit_id*/100901, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/2.5, /*change_face*/2, /*description*/"我が真名はちゃんと覚えたか？\nアンネローゼ・フォン・\nシュテッヒパルムだ！");
INSERT INTO `unit_comments` VALUES (/*id*/1009003, /*unit_id*/100901, /*use_type*/1, /*voice_id*/3, /*face_id*/4, /*change_time*/5.0, /*change_face*/3, /*description*/"くっ…！　魔力を封じた\n「瞳」が疼く…！\n目薬？　いらんわっ！");
INSERT INTO `unit_comments` VALUES (/*id*/1009004, /*unit_id*/100901, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/5.5, /*change_face*/2, /*description*/"シグルドよ、現世においても\n我ら盟友の絆は不滅だ！\nはーっはっはっはっ！");
INSERT INTO `unit_comments` VALUES (/*id*/1009005, /*unit_id*/100901, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/5.0, /*change_face*/5, /*description*/"我ら二人で手を取り合い\n『機関』の野望を打ち砕こう！\nそしてその暁には、前世と同じく\nめ、夫婦の誓いを…");
INSERT INTO `unit_comments` VALUES (/*id*/1009006, /*unit_id*/100931, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"その目に、魂に刻めっ…！\nこれぞ我が禁断の\n最上究極殲滅魔法だ！");
INSERT INTO `unit_comments` VALUES (/*id*/1009007, /*unit_id*/100931, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"貴様も技を持っているなら\nかっこよく放つべきだ！\n例えばだな…");
INSERT INTO `unit_comments` VALUES (/*id*/1009008, /*unit_id*/100931, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"魔力を込め続けた\n私の瞳が輝きを放つ時\n敵の命運は尽きる…！");
INSERT INTO `unit_comments` VALUES (/*id*/1009009, /*unit_id*/100931, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"防御を捨て、攻撃に特化した\n『撃滅形態』をとくと見よ！\n…ど、どこを見ているのだ！？");
INSERT INTO `unit_comments` VALUES (/*id*/1009010, /*unit_id*/100931, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"シグルドよ…貴様のおかげで\nいつも私は自分を貫けている\nだから…ありがとう");
INSERT INTO `unit_comments` VALUES (/*id*/1009011, /*unit_id*/100901, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/6.8, /*change_face*/2, /*description*/"今日は貴様が転生した日か\n記念に私からこれを授けよう！\n見よ！　暗黒騎士の紋章を！\n…どうだ。かっこいいだろう？");
INSERT INTO `unit_comments` VALUES (/*id*/1009012, /*unit_id*/100931, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"シグルドよ、誕生日おめでとう！\n私からは日記をプレゼントだ！\n我らの活動を書き残すがいい！\n…字は書ける、よな？");
INSERT INTO `unit_comments` VALUES (/*id*/1010001, /*unit_id*/101001, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/5.5, /*change_face*/2, /*description*/"王子はん、王子はん\nおぶういかがどすかぁ？\nおぶういうんはお茶どす！");
INSERT INTO `unit_comments` VALUES (/*id*/1010002, /*unit_id*/101001, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/6.0, /*change_face*/2, /*description*/"女の子は夢の国で暮らすことに、\n夢を見るんどすなぁ\n運命の王子はんと夢の国～♪");
INSERT INTO `unit_comments` VALUES (/*id*/1010003, /*unit_id*/101001, /*use_type*/1, /*voice_id*/3, /*face_id*/5, /*change_time*/5.5, /*change_face*/2, /*description*/"古今東西の絵本ではなぁ、\nお姫はんを守るのは、\n王子はんの役目なんよ？");
INSERT INTO `unit_comments` VALUES (/*id*/1010004, /*unit_id*/101001, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/4.5, /*change_face*/2, /*description*/"怪我したときは、\nうちを頼ってほしいわぁ\n特別な呪文があるんどす\n痛いの痛いの飛んでけ～");
