INSERT INTO `unit_comments` VALUES (/*id*/1015008, /*unit_id*/101531, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あらあら、怪我しちゃったの？\nこっちに来て見せてごらん？\n先生が治してあげるわね～\nほーら、痛いの痛いの飛んでけ～");
