INSERT INTO `unit_comments` VALUES (/*id*/1016005, /*unit_id*/101601, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/4.0, /*change_face*/1, /*description*/"ヒデサイ～、ちょす～！\n一緒にごはんしよ！\n最近みつけた美味しいお店、\nうちが教えてあげる～");
