INSERT INTO `unit_comments` VALUES (/*id*/1016013, /*unit_id*/101661, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"うちが楽しいって思うこと\nみんなにも楽しいって\n思ってもらいたいんだよね！\nもち、ヒデサイにもね♪");
