INSERT INTO `unit_comments` VALUES (/*id*/1018001, /*unit_id*/101801, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/9.0, /*change_face*/2, /*description*/"私、こう見えても新米教師なの\nもし何か国語でわからないことが\nあったら質問してね\n先生が教えてあ・げ・る♪");
