INSERT INTO `unit_comments` VALUES (/*id*/1018003, /*unit_id*/101801, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/5.5, /*change_face*/2, /*description*/"魔族の女子校時代…\n楽しい思い出がいっぱいあるわ\n君もこの学院で\nすてきな時間を過ごしてね！");
INSERT INTO `unit_comments` VALUES (/*id*/1018004, /*unit_id*/101801, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/10.0, /*change_face*/6, /*description*/"宿題、手こずってるの？\n先生がじっくり教えてあげる♪\nまず…この文章の意味は…\n…え？　くっつきすぎ？");
INSERT INTO `unit_comments` VALUES (/*id*/1018005, /*unit_id*/101801, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/8.0, /*change_face*/5, /*description*/"二人で歩いてるとよくカップルに\n間違われちゃうわね\n恋人同士に見えるのかな？\n私はイヤじゃないけど…　");
INSERT INTO `unit_comments` VALUES (/*id*/1018006, /*unit_id*/101831, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"こ、ころんじゃったぁ～\n教師なんだし、もっとしっかり\nしなくちゃって思うんだけど\nまだまだなのよねぇ…");
INSERT INTO `unit_comments` VALUES (/*id*/1018007, /*unit_id*/101831, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私、天然キャラじゃないのよ？\nたま～にうっかりすることが\nあるだけで…ほんとよ？");
