INSERT INTO `unit_comments` VALUES (/*id*/1018009, /*unit_id*/101831, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あのね、もしまた転んじゃったら\n助けてくれる？\nえへへ、私すっかり君の事頼りに\nしちゃってる。先生なのにね");
INSERT INTO `unit_comments` VALUES (/*id*/1018010, /*unit_id*/101831, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"初めての彼氏はどんな人かな\n転んでもバカにせず助けてくれる\n君みたいに優しい人がいいな…");
INSERT INTO `unit_comments` VALUES (/*id*/1018011, /*unit_id*/101801, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/6.2, /*change_face*/2, /*description*/"お誕生日おめでとう～♪\n昨日より一つ大人になったのね\nお祝いに先生が、\nナデナデしてあげるわ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1018012, /*unit_id*/101831, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日は君の誕生日ね～！\n年下の男の子が、結婚できる\n年齢になってプロポーズ…なんて\n小説みたいであこがれちゃうな♪");
INSERT INTO `unit_comments` VALUES (/*id*/1018013, /*unit_id*/101861, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"君にだけ\n特別な授業をしてあげる\nじっくり教える予定だから\nサボっちゃダメよ？");
