INSERT INTO `unit_comments` VALUES (/*id*/1060013, /*unit_id*/106061, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あたしの魔力とあんたの力が\n一つになっていく…！\nさあ、かかってきなさい！\n今日のあたしは一味違うわよ！");
