INSERT INTO `unit_comments` VALUES (/*id*/1061008, /*unit_id*/106131, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"もう逃げも隠れもしないぞっ！\nどんなに傷ついても最後まで\n戦ってやる！　そう決めたんだ！");
INSERT INTO `unit_comments` VALUES (/*id*/1061009, /*unit_id*/106131, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"はっはっはーっ！\nどけどけえっ！！\nノウェム様のお通りだぁ！");
INSERT INTO `unit_comments` VALUES (/*id*/1061010, /*unit_id*/106131, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"やっとオマエと味方になれたんだ\n早いとこオクトーの目を覚まして\nアイツとも一緒に戦いたいな！");
INSERT INTO `unit_comments` VALUES (/*id*/1061011, /*unit_id*/106101, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/2.1, /*change_face*/5, /*description*/"誕生日おめでとう！\nあんまり人に物あげたこと\nなくてさ…何が欲しいんだ？");
INSERT INTO `unit_comments` VALUES (/*id*/1061012, /*unit_id*/106131, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハッピーバースデー！\nよーし！　ケーキ買いに行くぞ！\n今日はアタシのおごりだ！\nたぶん明日は雪が降るぞ！");
INSERT INTO `unit_comments` VALUES (/*id*/1063001, /*unit_id*/106301, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/5.8, /*change_face*/1, /*description*/"ランドソルってすごいなぁ…\n色々あって、大きいし…\n私の里とは全然違うや");
INSERT INTO `unit_comments` VALUES (/*id*/1063002, /*unit_id*/106301, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/5.3, /*change_face*/1, /*description*/"私の里もいいところなんだよ\n…なーんもないけど、\nでも、気に入ってくれると思うな！");
INSERT INTO `unit_comments` VALUES (/*id*/1063003, /*unit_id*/106301, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/6.1, /*change_face*/2, /*description*/"あなたとロザリアって、\nちょっと似てるかも？\n例えば…\n一緒にいて安心するところ、とか");
INSERT INTO `unit_comments` VALUES (/*id*/1063004, /*unit_id*/106301, /*use_type*/1, /*voice_id*/4, /*face_id*/5, /*change_time*/5.7, /*change_face*/2, /*description*/"あなたといると、\nドキドキしたり、苦しくなったり…\nでも、なんか…嬉しいんだ");
INSERT INTO `unit_comments` VALUES (/*id*/1063005, /*unit_id*/106301, /*use_type*/1, /*voice_id*/5, /*face_id*/5, /*change_time*/4.1, /*change_face*/2, /*description*/"私、あなたと一緒にいると…\n安心するし、ドキドキするんだ");
INSERT INTO `unit_comments` VALUES (/*id*/1063006, /*unit_id*/106331, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"いつか必ず、森番になるんだ\nもちろん、ロザリアと一緒にね！");
INSERT INTO `unit_comments` VALUES (/*id*/1063007, /*unit_id*/106331, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ランドソルは広いけど…\n風の匂いは、里と同じかも");
INSERT INTO `unit_comments` VALUES (/*id*/1063008, /*unit_id*/106331, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私の里にはね、\n妖精さんがいっぱいいるんだよ！\nあなたにも見せてあげたいなぁ");
INSERT INTO `unit_comments` VALUES (/*id*/1063009, /*unit_id*/106331, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ドキドキするのに、落ち着くし…\n安心するのに、たまに苦しくて…\nあなたの側にいると、\n不思議な気持ちになっちゃうな");
INSERT INTO `unit_comments` VALUES (/*id*/1063010, /*unit_id*/106331, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"森にいた頃よりも、\n今の方が楽しく思えちゃうのは…\nきっと、あなたのおかげかな？");
INSERT INTO `unit_comments` VALUES (/*id*/1063011, /*unit_id*/106301, /*use_type*/2, /*voice_id*/6, /*face_id*/5, /*change_time*/4.9, /*change_face*/2, /*description*/"ちょっと自信ないけど…\nこれ！　作ったんだ！\n今日は特別な日だから…\nあーんってしてあげよっか…？");
INSERT INTO `unit_comments` VALUES (/*id*/1063012, /*unit_id*/106331, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"このままどんどん成長したら\nあなたはどうなっちゃうんだろ？\n…なんか、ずっと楽しみだね\nえへへ…お誕生日おめでと！");
INSERT INTO `unit_comments` VALUES (/*id*/1064001, /*unit_id*/106401, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/7.2, /*change_face*/2, /*description*/"自分のなくした記憶もだけど…\nあなたが忘れた過去も気になるわ\n二人で一緒に\n見つけられたらいいわね");
INSERT INTO `unit_comments` VALUES (/*id*/1064002, /*unit_id*/106401, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/6.6, /*change_face*/2, /*description*/"アイスクリームって、どうして\nこうもおいしく感じるのかしら…\n『前の私』もひょっとしたら\nアイスが好きだったりしてね");
INSERT INTO `unit_comments` VALUES (/*id*/1064003, /*unit_id*/106401, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/4.7, /*change_face*/1, /*description*/"そんなに真面目っぽい性格に、\n見えるかしら？\n冗談とか言うのは、確かに苦手ね");
INSERT INTO `unit_comments` VALUES (/*id*/1064004, /*unit_id*/106401, /*use_type*/1, /*voice_id*/4, /*face_id*/4, /*change_time*/4.1, /*change_face*/5, /*description*/"…ふとした弾みで、思い出すの\nコッコロさんに甘えてたこととか\nキャルのしっぽの味とか…\nもう…顔から火が出そうよ…");
INSERT INTO `unit_comments` VALUES (/*id*/1064005, /*unit_id*/106401, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/6.7, /*change_face*/1, /*description*/"【美食殿】での生活は\n毎日が充実していて、楽しいわ\nこの時間が、少しでも長く続く\nように…精一杯努力するつもりよ");
INSERT INTO `unit_comments` VALUES (/*id*/1064006, /*unit_id*/106431, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"必ず、みんなを守ってみせる！\nだって、みんなの隣が\n『今の私』の居場所だから！");
INSERT INTO `unit_comments` VALUES (/*id*/1064007, /*unit_id*/106431, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"氷を滑る、この動き―\n身体が覚えてる？\nううん、今はそんなこと\n考えてる場合じゃない…！");
INSERT INTO `unit_comments` VALUES (/*id*/1064008, /*unit_id*/106431, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"っくしゅっ…！\n氷の魔法は使えても、そこまで\n寒さに強いわけじゃないみたい\n後で、温かいスープが欲しいわね");
INSERT INTO `unit_comments` VALUES (/*id*/1064009, /*unit_id*/106431, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私を迎えてくれた\n【美食殿】のみんな、\nそれにあなたも…\n絶対に、守ってみせるわ！");
INSERT INTO `unit_comments` VALUES (/*id*/1064010, /*unit_id*/106431, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"…不思議ね\nあなたから力をもらうと、\n胸が熱くなって…\nどこまでも跳べそうな気がするわ");
INSERT INTO `unit_comments` VALUES (/*id*/1064011, /*unit_id*/106401, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/6.5, /*change_face*/4, /*description*/"お誕生日、おめでとう\nケーキ、一緒に食べましょう\nでも…魔物とか昆虫とか\nケーキに入ってないわよね？");
INSERT INTO `unit_comments` VALUES (/*id*/1064012, /*unit_id*/106431, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私、自分の誕生日が分からなくて\n…だから、お祝いとかできないの\nその分、あなたの誕生日を\nお祝いさせて貰っていいかしら？");
INSERT INTO `unit_comments` VALUES (/*id*/1065001, /*unit_id*/106501, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/7.7, /*change_face*/2, /*description*/"キミと一緒にいれば\nなんか面白いことが起きるだろ？\nだから期待させてもらってるよ\nさ、どっか遊びにいこうぜ！");
INSERT INTO `unit_comments` VALUES (/*id*/1065002, /*unit_id*/106501, /*use_type*/1, /*voice_id*/2, /*face_id*/3, /*change_time*/0.0, /*change_face*/3, /*description*/"オレはこの世界に\nなにが起きたのか知りたい…\nそのためにもオレは\nもっと強くなる必要があるんだよ");
INSERT INTO `unit_comments` VALUES (/*id*/1065003, /*unit_id*/106501, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/8.9, /*change_face*/1, /*description*/"なあ、仕事の依頼はないのか？\nだからケンカだって。ケンカの\n仕事。オレがたぎるようなやつを\n持ってきてくれよな");
INSERT INTO `unit_comments` VALUES (/*id*/1065004, /*unit_id*/106501, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/7.6, /*change_face*/2, /*description*/"そう心配すんなって\nそこらのヤツにやられるほど\nオレは弱くはないさ\nなんなら、試してみるか？");
INSERT INTO `unit_comments` VALUES (/*id*/1065005, /*unit_id*/106501, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/5.8, /*change_face*/1, /*description*/"ドラゴン族以外のやつと\nここまで仲良くなるなんて\n思わなかったぜ\nキミ、一体何者なんだ？");
INSERT INTO `unit_comments` VALUES (/*id*/1065006, /*unit_id*/106531, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"喧嘩屋の本領、見せてやるよ！\nダチが一緒じゃ負けられねぇ！\n誰だろうとぶっ倒して、オレ様が\n最強だって証明してやるぜ！");
INSERT INTO `unit_comments` VALUES (/*id*/1065007, /*unit_id*/106531, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"すげえぜ、この力！\n体の奥から\nどんどん溢れてきやがる！\nオレたちに壊せないものはねえ！");
INSERT INTO `unit_comments` VALUES (/*id*/1065008, /*unit_id*/106531, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"これがドラゴン族の力…！\nオレ様が使いこなしてやるよ！\nダチが見てるんだ。情けねえ\nカッコは見せらんねぇよな！");
INSERT INTO `unit_comments` VALUES (/*id*/1065009, /*unit_id*/106531, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ダチと一緒に戦うのが\nこんなに楽しいなんてな…！\n知らないこの世界で知った\n最高にたぎる感情だ！");
INSERT INTO `unit_comments` VALUES (/*id*/1065010, /*unit_id*/106531, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"いいぜいいぜ、たぎってきた！\nキミが一緒なら、これっぽっちも\n負ける気がしねえ！\n派手に暴れてやろうぜ！");
INSERT INTO `unit_comments` VALUES (/*id*/1065011, /*unit_id*/106501, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/4.7, /*change_face*/2, /*description*/"よっ！　おめでとう！\n今日はキミの誕生日だろ？\nパーッとなんか食べに行こうぜ\n今日はオレがおごってやるよ");
INSERT INTO `unit_comments` VALUES (/*id*/1065012, /*unit_id*/106531, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日おめでとう！\nダチにとって良い日ってのは\nこっちまでうれしくなるんだな\nこんな気持ち、初めてだ！");
INSERT INTO `unit_comments` VALUES (/*id*/1066001, /*unit_id*/106601, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/4.2, /*change_face*/3, /*description*/"ぴゃっ！　って、お兄さんですか\n何じろじろ見てるです、えっち！\n煩悩まみれ！　不審者だったら\n叩き潰してるところですよ？");
INSERT INTO `unit_comments` VALUES (/*id*/1066002, /*unit_id*/106601, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/5.4, /*change_face*/4, /*description*/"あたしがサボってここにいるのは\nボスには内緒にするですよ？\nもしあのババアに見つかったら\n何されるか分からないです…");
INSERT INTO `unit_comments` VALUES (/*id*/1066003, /*unit_id*/106601, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/6.9, /*change_face*/1, /*description*/"は～…ほんとうに\nあたしたち以外にも\nドラゴン族っているんですかね？\n…そこらへんに歩いてないです？");
INSERT INTO `unit_comments` VALUES (/*id*/1066004, /*unit_id*/106601, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/5.0, /*change_face*/6, /*description*/"今日の仕事はここまでです！\n縄張りを見回ってたですよ！\nべ、別にサボってブラブラしてた\nわけじゃないですからね？");
INSERT INTO `unit_comments` VALUES (/*id*/1066005, /*unit_id*/106601, /*use_type*/1, /*voice_id*/5, /*face_id*/4, /*change_time*/6.5, /*change_face*/3, /*description*/"あ～あ、どこかにぽろっと\n大金が落ちてないですかね？\n早く自由の身になって\nボスを見返してやりたいですよ…");
INSERT INTO `unit_comments` VALUES (/*id*/1066006, /*unit_id*/106631, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"どうですかお兄さん！\nこれがあたしの…\nドラゴン族の力ですよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1066007, /*unit_id*/106631, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ふっふっふ…心技体ともに\n覚醒した今のあたしには\n死角はないです…ってぴゃあ！\nあ、足場が崩れたですよぉ！");
INSERT INTO `unit_comments` VALUES (/*id*/1066008, /*unit_id*/106631, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"力がわきあがってくるです！\n知性派のあたしですけど\nたまには本能のおもむくまま\nってのも、悪くないです！");
INSERT INTO `unit_comments` VALUES (/*id*/1066009, /*unit_id*/106631, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この力があれば…\nお兄さんがどれだけ\nへっぽこナイトでも\n助けてあげられるですよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1066010, /*unit_id*/106631, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"もう守ってもらうばかりの\nあたしじゃないですよ！\nこの力でなりあがってやるです！");
INSERT INTO `unit_comments` VALUES (/*id*/1066011, /*unit_id*/106601, /*use_type*/2, /*voice_id*/6, /*face_id*/6, /*change_time*/6.3, /*change_face*/2, /*description*/"え？　なんです？　今日は\nお兄さんの誕生日ですか？\nじゃあ特別に、あたしに\n何かおごる権利をやるですよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1066012, /*unit_id*/106631, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうです！\nこのあたしに祝われるなんて\nそうそうない名誉ですからね！\n感謝して何かおごりやがれです！");
INSERT INTO `unit_comments` VALUES (/*id*/1068001, /*unit_id*/106801, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/8.1, /*change_face*/1, /*description*/"やあ少年。元気にしてる？\nたまには店に寄っていってよ\n美味しいクレープ作るからさ\n味は普通？　…そうかな？");
INSERT INTO `unit_comments` VALUES (/*id*/1068002, /*unit_id*/106801, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/8.5, /*change_face*/6, /*description*/"おや、今日は人助けはお休み？\n一人きりでのんびりしたいなら\n迷宮を作ってあげようか\nあれ？　なんで逃げるの？");
INSERT INTO `unit_comments` VALUES (/*id*/1068003, /*unit_id*/106801, /*use_type*/1, /*voice_id*/3, /*face_id*/4, /*change_time*/8.5, /*change_face*/6, /*description*/"実はちょっと困ってるんだよね…\n予約してた新作ゲームが\nこっちの世界だと遊べない！\nこれは由々しき問題だよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1068004, /*unit_id*/106801, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/7.7, /*change_face*/2, /*description*/"少年と一緒にいると\n世界も自分も、いい方向に\n向かう予感がするんだ\nキミはずっと、そのままでいてね");
INSERT INTO `unit_comments` VALUES (/*id*/1068005, /*unit_id*/106801, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/8.4, /*change_face*/2, /*description*/"…記憶がなくても、キミの本質は\n変わらない。それを知った時は\n本当にうれしかったよ\nこれからもよろしくね、少年");
INSERT INTO `unit_comments` VALUES (/*id*/1068006, /*unit_id*/106831, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"迷宮女王とプリンセスナイトが\n力を合わせれば、どんな敵が\n相手でも勝てるさ\nさあ少年、一緒に迎え撃とう！");
INSERT INTO `unit_comments` VALUES (/*id*/1068007, /*unit_id*/106831, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ああ…やっとキミのそばで戦える\nうれしいよ、少年。七冠として、\n一人の人間としてキミを\n守り抜くと、今ここに誓うよ");
INSERT INTO `unit_comments` VALUES (/*id*/1068008, /*unit_id*/106831, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"おいで、憐れな挑戦者たち\nアタシの迷宮から、果たして\n生きて帰れるかな…？\nその足掻き、見せてもらうよ");
INSERT INTO `unit_comments` VALUES (/*id*/1068009, /*unit_id*/106831, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"近頃少年を近くに感じていると\n力が溢れて止まらないんだ\nこれってもしかして…恋？\n…いや、愛だ！　だよね、少年！");
INSERT INTO `unit_comments` VALUES (/*id*/1068010, /*unit_id*/106831, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"アタシは人間が大好きだ\nでもそれ以上に、少年が好きだよ\n…どうかな、ドキッとした？\n真剣だよ？　それがどうかした？");
INSERT INTO `unit_comments` VALUES (/*id*/1068011, /*unit_id*/106801, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"少年、誕生日おめでとう～！\n今日くらいはクレープじゃなくて\nケーキをプレゼントしちゃおう！\nでもクレープもあげちゃう！");
INSERT INTO `unit_comments` VALUES (/*id*/1068012, /*unit_id*/106831, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"少年、誕生日おめでとう\nキミがこの世に生を受けた日は\n盛大に祝うべきだ。今から一緒に\nプレゼントを選びに行こう！");
INSERT INTO `unit_comments` VALUES (/*id*/1070001, /*unit_id*/107001, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/5.8, /*change_face*/2, /*description*/"敵の敵は味方…\n今回は貴方に協力しましょう\nよろしく頼みます");
INSERT INTO `unit_comments` VALUES (/*id*/1070002, /*unit_id*/107001, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/5.6, /*change_face*/1, /*description*/"プリンセスナイトとしてではなく\n貴方だけが持つ可能性\nそれに賭けさせてもらいます");
INSERT INTO `unit_comments` VALUES (/*id*/1070003, /*unit_id*/107001, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/2.0, /*change_face*/2, /*description*/"今の私？　ええ、私本人ですよ\n今の貴方に自分を偽っても\n意味がありませんから");
INSERT INTO `unit_comments` VALUES (/*id*/1070004, /*unit_id*/107001, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/5.3, /*change_face*/6, /*description*/"貴方といると、犬を\nもう一匹飼い始めた気分です\n飼ったこと？　ありませんよ？");
INSERT INTO `unit_comments` VALUES (/*id*/1070005, /*unit_id*/107001, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/5.3, /*change_face*/2, /*description*/"貴方の好みのタイプを\n教えなさい。再現してあげます\n妹系？　姉系？　幼馴染？");
INSERT INTO `unit_comments` VALUES (/*id*/1070006, /*unit_id*/107031, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"自分の目に映った私が\n本当の私だと思いますか…？\n私は変貌大妃\n千変万化の女ですよ");
INSERT INTO `unit_comments` VALUES (/*id*/1070007, /*unit_id*/107031, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私の『冠』が偽物ではないと\nその身に刻み込んであげます\nもっとも全ての敵は私に模倣され\n消えて無くなりますが");
INSERT INTO `unit_comments` VALUES (/*id*/1070008, /*unit_id*/107031, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"鏡よ鏡…世界で最も\n賢く強いのは誰ですか？\n…ええその通り。当然私です");
INSERT INTO `unit_comments` VALUES (/*id*/1070009, /*unit_id*/107031, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私に仕えたくなりましたか？\n私の中にデータさえあれば\n本人は不要なのです\n…彼以外は");
INSERT INTO `unit_comments` VALUES (/*id*/1070010, /*unit_id*/107031, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"こそこそ隠れるのは終わりです\nこれから先は打って出ます\n貴方とその仲間たち…\n戦力として当てにしていますよ");
INSERT INTO `unit_comments` VALUES (/*id*/1070011, /*unit_id*/107001, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/5.8, /*change_face*/6, /*description*/"今日が誕生日ですか\nおめでとうございます\n…これ以上何か必要ですか？");
INSERT INTO `unit_comments` VALUES (/*id*/1070012, /*unit_id*/107031, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日おめでとうございます\n贈り物は何にしましょう…\n計算問題集ではどうですか？");
INSERT INTO `unit_comments` VALUES (/*id*/1071001, /*unit_id*/107101, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/5.8, /*change_face*/2, /*description*/"ややこしい話し合いは面倒だ！\n物事はシンプルに解決しよう\nそう、生きるか死ぬかの\n本気の殺り合いでな☆");
INSERT INTO `unit_comments` VALUES (/*id*/1071002, /*unit_id*/107101, /*use_type*/1, /*voice_id*/2, /*face_id*/4, /*change_time*/2.5, /*change_face*/1, /*description*/"息苦しい世の中だ\n自分の欲求に従って\n好き勝手に生きた者の勝ちだとは\n思わんか？");
INSERT INTO `unit_comments` VALUES (/*id*/1071003, /*unit_id*/107101, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/3.1, /*change_face*/4, /*description*/"強すぎるというのも考えものだ\n歯ごたえのない連中ばかりで\n命の遣り取りをする愉しい戦いに\nそうそう出会えないからな");
INSERT INTO `unit_comments` VALUES (/*id*/1071004, /*unit_id*/107101, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/8.5, /*change_face*/2, /*description*/"ワタシの前に散っていった\n男たちは、この胸を触ったオマエを\n心底憎く思うだろうなあ\n安心しろ、全員墓の中だ☆");
INSERT INTO `unit_comments` VALUES (/*id*/1071005, /*unit_id*/107101, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/6.8, /*change_face*/5, /*description*/"オマエが幸せな結婚生活を\n送れるように、ワタシだって\n努力に手を抜かない\n意外と可愛いところもあるだろ？");
INSERT INTO `unit_comments` VALUES (/*id*/1071006, /*unit_id*/107131, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"イかれてなどいないさ\n人間の命は儚く脆い…\nその刹那の時もワタシは\n楽しみ切ってやるのさ☆");
INSERT INTO `unit_comments` VALUES (/*id*/1071007, /*unit_id*/107131, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"戦場を満たす血だまりの上で\n一緒に陽気なステップを\n踏もうじゃないか\nさあ、お手をどうぞ☆");
INSERT INTO `unit_comments` VALUES (/*id*/1071008, /*unit_id*/107131, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誓約で仕方なく…ではないぞ？\n少なくともワタシは納得している\nこのワタシに触れた男だ\n結婚相手として認めているよ☆");
INSERT INTO `unit_comments` VALUES (/*id*/1071009, /*unit_id*/107131, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この胸の高ぶるような\n愉しい出来事はないか？\nないならばワタシが\n火種をつけてやろう☆");
INSERT INTO `unit_comments` VALUES (/*id*/1071010, /*unit_id*/107131, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ワタシの幼女時代の写真が\nもっと見たいだと？　もしや\n第二次性徴期前の幼子にしか\n興奮しない性癖だったのか？");
INSERT INTO `unit_comments` VALUES (/*id*/1071011, /*unit_id*/107101, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/9.1, /*change_face*/2, /*description*/"オマエ、今日が誕生日らしいな？\nほら、一つ歳をとった哀れな子羊に\nプレゼントだ\n今年もワタシを楽しませてくれよ？");
INSERT INTO `unit_comments` VALUES (/*id*/1071012, /*unit_id*/107131, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日おめでとう♪\nオマエの記念すべき日に\n立ち会えてうれしいよ！\n記念の誓約は…まだ早いかな？");
INSERT INTO `unit_comments` VALUES (/*id*/1075001, /*unit_id*/107501, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/2.2, /*change_face*/2, /*description*/"水着買っちゃいました～！\nどうですどうです？\n似合ってますか～？");
INSERT INTO `unit_comments` VALUES (/*id*/1075002, /*unit_id*/107501, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この水着、結構動きやすいので\n素潜りとかして\n海の幸をゲットできそうです！");
INSERT INTO `unit_comments` VALUES (/*id*/1075003, /*unit_id*/107501, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"マリンスポーツしましょう！\nビーチボールとか、\nビーチフラッグとか！\n負けませんよ～");
INSERT INTO `unit_comments` VALUES (/*id*/1075004, /*unit_id*/107501, /*use_type*/1, /*voice_id*/4, /*face_id*/5, /*change_time*/4.3, /*change_face*/2, /*description*/"私の水着姿で、\nあなたを誘惑しちゃいます♪\nなんちゃってー！");
INSERT INTO `unit_comments` VALUES (/*id*/1075005, /*unit_id*/107501, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/5.0, /*change_face*/2, /*description*/"せっかくの水着ですし、\nたくさん泳いで、\nたくさんはしゃいで…\n思い出いっぱい作りましょうね！");
INSERT INTO `unit_comments` VALUES (/*id*/1075006, /*unit_id*/107531, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"わぁっ、やりましたね～！\n塩水しょっぱいぃ～\nこうなったらわたしも本気です！\n覚悟してくださいよぉ～！");
INSERT INTO `unit_comments` VALUES (/*id*/1075007, /*unit_id*/107531, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"青い海、白い砂浜、輝く太陽！\nそしておいしいお魚！\nここは天国ですね！");
INSERT INTO `unit_comments` VALUES (/*id*/1075008, /*unit_id*/107531, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"水が気持ちいいですね～\nこんなに充実した夏、\n生まれて初めてです！");
INSERT INTO `unit_comments` VALUES (/*id*/1075009, /*unit_id*/107531, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"えへへ♪\nあなたと海で遊ぶなんて、\nとっても新鮮な感じです！\n青春ですね♪");
INSERT INTO `unit_comments` VALUES (/*id*/1075010, /*unit_id*/107531, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"はしゃぎすぎて水着が\n流されちゃったりしたら、\nあなたに抱きついて\n隠しますね！");
INSERT INTO `unit_comments` VALUES (/*id*/1075011, /*unit_id*/107501, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/2.6, /*change_face*/5, /*description*/"お誕生日おめでとうございます！\nお魚、貝、タコ、エビ、カニ…\n海の幸満載のバースデー\nパーティーにしましょうね！");
INSERT INTO `unit_comments` VALUES (/*id*/1075012, /*unit_id*/107531, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"水着で過ごす、あなたの誕生日！\n開放的でわくわくしちゃいます！\nさぁ、今日は二人で\nおもいっきりはしゃぎましょう！");
INSERT INTO `unit_comments` VALUES (/*id*/1076001, /*unit_id*/107601, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/4.8, /*change_face*/5, /*description*/"救護院の皆さまに、水着を\n選んでいただきました…\n似合っていますでしょうか？");
INSERT INTO `unit_comments` VALUES (/*id*/1076002, /*unit_id*/107601, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"主さま、よろしければ\n一緒に泳ぎの\n練習をしましょう");
INSERT INTO `unit_comments` VALUES (/*id*/1076003, /*unit_id*/107601, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"主さま。わたくし、\n砂のお城というものを\n作ってみたいのですが、\nお手伝いいただけますでしょうか");
INSERT INTO `unit_comments` VALUES (/*id*/1076004, /*unit_id*/107601, /*use_type*/1, /*voice_id*/4, /*face_id*/5, /*change_time*/0.0, /*change_face*/5, /*description*/"このような\n過度な肌の露出は、\nやはり恥ずかしいです…\n主さまにしかお見せできません");
INSERT INTO `unit_comments` VALUES (/*id*/1076005, /*unit_id*/107601, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/4.2, /*change_face*/2, /*description*/"日焼け止めを塗っていただいても\nよいでしょうか？\n主さまにでしたら、\n肌を許すことができます");
INSERT INTO `unit_comments` VALUES (/*id*/1076006, /*unit_id*/107631, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"主さま。どんな場所でも、\nわたくしがついております\nこの手を離さないでくださいね");
INSERT INTO `unit_comments` VALUES (/*id*/1076007, /*unit_id*/107631, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"見てください、主さま\nおさかながこんなに近くに…\nとても神秘的でございます");
INSERT INTO `unit_comments` VALUES (/*id*/1076008, /*unit_id*/107631, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"このまま流れに身を任せ、\n二人でどこまでも進む\n…それも悪くありませんね");
INSERT INTO `unit_comments` VALUES (/*id*/1076009, /*unit_id*/107631, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"これからもずっと、\n主さまと同じ景色を見て、\n主さまと同じ感動を抱き、\nともに歩んでいきたいです");
INSERT INTO `unit_comments` VALUES (/*id*/1076010, /*unit_id*/107631, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この広い海の中で、\n主さまと二人っきり…\nいまだけは、主さまを\n独り占めです♪");
INSERT INTO `unit_comments` VALUES (/*id*/1076011, /*unit_id*/107601, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"海で、主さまのお誕生日を\nお祝いするなどと、故郷にいた\n時には考えもしませんでした\nわたくし、幸せでございます");
INSERT INTO `unit_comments` VALUES (/*id*/1076012, /*unit_id*/107631, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"空も海も輝いて…この世の\n全てが、主さまのお誕生日を\n祝福しているようです\nわたくしも心よりお祝いします♪");
INSERT INTO `unit_comments` VALUES (/*id*/1077001, /*unit_id*/107701, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/2.8, /*change_face*/5, /*description*/"働くのって楽しいですね！\nあなたと一緒だと、もっと\n楽しく感じちゃいます♪");
INSERT INTO `unit_comments` VALUES (/*id*/1077002, /*unit_id*/107701, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/2.9, /*change_face*/2, /*description*/"休憩時間に海へ\n行きたいんですか？\nもちろん、いいですよ～\n一緒に泳ぎましょう！");
INSERT INTO `unit_comments` VALUES (/*id*/1077003, /*unit_id*/107701, /*use_type*/1, /*voice_id*/3, /*face_id*/4, /*change_time*/6.2, /*change_face*/2, /*description*/"私ドジだから、砂のお城って\n完成させたことないんです～\n…一緒に作ってくれるんですか？\nありがとうございます♪");
INSERT INTO `unit_comments` VALUES (/*id*/1077004, /*unit_id*/107701, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/4.2, /*change_face*/2, /*description*/"水着姿を褒めて下さるのは\n嬉しいですが…あ、あまり\n見つめられると恥ずかしいです～");
INSERT INTO `unit_comments` VALUES (/*id*/1077005, /*unit_id*/107701, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/4.7, /*change_face*/5, /*description*/"御用はありますか？\nもしもなくても…\nお、おそばにいても\nいいですか？");
INSERT INTO `unit_comments` VALUES (/*id*/1077006, /*unit_id*/107731, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"まさか足元に花火があるなんて～！\n子供たちは危険ですから、\n離れてください！\nひゃう！　あつい～っ！");
INSERT INTO `unit_comments` VALUES (/*id*/1077007, /*unit_id*/107731, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"だ、大惨事です～！\nきれいとか言ってる場合じゃ\nありませんから～！");
INSERT INTO `unit_comments` VALUES (/*id*/1077008, /*unit_id*/107731, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"みんなで花火をするのって\nとっても楽しいですね\n今度は両手持ちで\n行きますよ～♪");
INSERT INTO `unit_comments` VALUES (/*id*/1077009, /*unit_id*/107731, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"二人で花火をしてると\n恋人同士に見えるんでしょうか？\nなんて…えへへ☆");
INSERT INTO `unit_comments` VALUES (/*id*/1077010, /*unit_id*/107731, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あ、あなたにも、お仕えさせて\nもらえませんか？\nや、やっぱり、今の言葉は\n忘れてください！");
INSERT INTO `unit_comments` VALUES (/*id*/1077011, /*unit_id*/107701, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/8.6, /*change_face*/6, /*description*/"お誕生日おめでとうございます♪\nクリームとおっきな苺を乗せた\nかき氷ケーキを召し上がれっ\nあわわ、溶けかけてる～！");
INSERT INTO `unit_comments` VALUES (/*id*/1077012, /*unit_id*/107731, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうございます♪\n今日は特別に、あなただけに\nお仕えさせてください\nえへへ、お嬢さまには内緒ですよ");
INSERT INTO `unit_comments` VALUES (/*id*/1078001, /*unit_id*/107801, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/5.8, /*change_face*/5, /*description*/"水着が似合ってるって？\nあんたに褒められてもねぇ～\nで、でも…ありがと");
INSERT INTO `unit_comments` VALUES (/*id*/1078002, /*unit_id*/107801, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/2.0, /*change_face*/2, /*description*/"この日を待ってたわ！\n思いっきり遊ぶから、\n覚悟しなさいよね！");
INSERT INTO `unit_comments` VALUES (/*id*/1078003, /*unit_id*/107801, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/4.3, /*change_face*/2, /*description*/"この浮輪、かわいいでしょ\n乗り心地もいいんだから\nどうしてもって言うなら、\n乗せてあげるけど？");
INSERT INTO `unit_comments` VALUES (/*id*/1078004, /*unit_id*/107801, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/5.6, /*change_face*/5, /*description*/"あんたといると退屈しないわ\n…どういうことかって？\nそれは、えっと…\n一緒にいると楽しいってこと！");
INSERT INTO `unit_comments` VALUES (/*id*/1078005, /*unit_id*/107801, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/3.7, /*change_face*/2, /*description*/"たまには二人っきりで\n出かけてあげてもいいわよ\n言っておくけど、\nあんたに拒否権はないから♪");
INSERT INTO `unit_comments` VALUES (/*id*/1078006, /*unit_id*/107831, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ね、ねえ、浮輪に穴あいてない？\n空いてるわよね！？　っていうか\n魔物もいるわよね！？\nぎゃー！　こっち来んなぁ！");
INSERT INTO `unit_comments` VALUES (/*id*/1078007, /*unit_id*/107831, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"何よ、物欲しそうな顔して\nこのジュースが飲みたいの？\nしょうがないわね、\n一口だけよ？");
INSERT INTO `unit_comments` VALUES (/*id*/1078008, /*unit_id*/107831, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"汗をかいた後の飲み物って\n最高よね\nはぁー、おいし♪");
INSERT INTO `unit_comments` VALUES (/*id*/1078009, /*unit_id*/107831, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"疲れてるみたいね\n一緒に浮輪に乗って、\nゆっくりする？");
INSERT INTO `unit_comments` VALUES (/*id*/1078010, /*unit_id*/107831, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"なんか、幸せーって感じ\nこれもあんたのおかげ、かな？\n…その、あ、ありが…\nな、なんでもないわよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1078011, /*unit_id*/107801, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/5.9, /*change_face*/1, /*description*/"誕生日、おめでと！\nこんな日くらい、パーッと\n羽目を外して楽しみましょ☆\nさ～海、海！　遊ぶわよー！");
