INSERT INTO `unit_comments` VALUES (/*id*/1082006, /*unit_id*/108231, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"幽霊大集合なの～\nみんなでハロウィンを\n盛り上げるの！\nパーティーのはじまりなの～");
