INSERT INTO `unit_comments` VALUES (/*id*/1082011, /*unit_id*/108201, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/5.2, /*change_face*/2, /*description*/"ひゅ～どろろろ～\nお誕生日～\nおめでとうなの～～♪");
