INSERT INTO `unit_comments` VALUES (/*id*/1083007, /*unit_id*/108331, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"おほほほほ♪　どう！？\nこのエレガントで優雅な仮装！\nまさに魔性の女って感じよね～");
