INSERT INTO `unit_comments` VALUES (/*id*/1084006, /*unit_id*/108431, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"メリークリスマス！\nありったけの気持ちを込めて\n歌いますから…聴いてくださいね");
INSERT INTO `unit_comments` VALUES (/*id*/1084007, /*unit_id*/108431, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"クリスマスを\n幸せな歌で彩ることができたら…\n唱喚士としてこの上ない幸せです");
INSERT INTO `unit_comments` VALUES (/*id*/1084008, /*unit_id*/108431, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"平和を守るため…そして\n人々にささやかな幸せを\n届けるために、私は歌い続けます");
INSERT INTO `unit_comments` VALUES (/*id*/1084009, /*unit_id*/108431, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私の歌声の可能性…\n今は信じています\nだって、あなたが\n信じてくれているんですから…");
INSERT INTO `unit_comments` VALUES (/*id*/1084010, /*unit_id*/108431, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"どんな些細な感情でも\nあなたと分かち合いたいんです\nもう、あなたがいてくれないと\nダメみたいですね、私…");
INSERT INTO `unit_comments` VALUES (/*id*/1084011, /*unit_id*/108401, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/4.1, /*change_face*/5, /*description*/"今日は誕生日ですよね、\nおめでとうございます\nプレゼントの他に、私の\n歌も贈らせてください");
INSERT INTO `unit_comments` VALUES (/*id*/1084012, /*unit_id*/108431, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日、おめでとうございます\n子どもたちや精霊も一緒に\nお祝いしてくれるそうです\n素敵な一日にしましょうね");
INSERT INTO `unit_comments` VALUES (/*id*/1085001, /*unit_id*/108501, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/7.6, /*change_face*/2, /*description*/"【サレンディア救護院】のみんな\nだけじゃなく、お兄ちゃんとも\nクリスマスを過ごせるなんて…\n今年は最高のクリスマスです…♪");
INSERT INTO `unit_comments` VALUES (/*id*/1085002, /*unit_id*/108501, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/4.8, /*change_face*/2, /*description*/"この服なら、サンタさんの役\nだってできちゃうかも…♪\nえへへ…子供たちに\nプレゼントを配っちゃいますよ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1085003, /*unit_id*/108501, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"サンタさんをイメージした\n新しいお洋服です…♪\nスズメお姉ちゃんに\n作ってもらったんです…♪");
INSERT INTO `unit_comments` VALUES (/*id*/1085004, /*unit_id*/108501, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/4.1, /*change_face*/2, /*description*/"お兄ちゃんの手…あったかい…\nお兄ちゃん…来年も…再来年も…\nずーっと一緒に居てくださいね♪");
INSERT INTO `unit_comments` VALUES (/*id*/1085005, /*unit_id*/108501, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/6.4, /*change_face*/5, /*description*/"あの…お兄ちゃん…\nこの服似合って…ひゃぅぅ…！\nきゅ、急に頭を撫でないでぇぇ…");
INSERT INTO `unit_comments` VALUES (/*id*/1085006, /*unit_id*/108531, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"うう…ツリーのてっぺんに\n手が届かないよぉ…\nお行儀悪いけど、\n椅子を使って…ひゃわぁ！？");
INSERT INTO `unit_comments` VALUES (/*id*/1085007, /*unit_id*/108531, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ツリーの飾り付けが終わったら、\nお買い物と、お料理の準備と…\nうう…アヤネちゃん、雪だるま\n作ってないで手伝ってよぉ…");
INSERT INTO `unit_comments` VALUES (/*id*/1085008, /*unit_id*/108531, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あ…このお人形かわいい…♪\n作り方を教えてもらって、\nお兄ちゃん人形を\n作ってみようかなぁ…♪");
INSERT INTO `unit_comments` VALUES (/*id*/1085009, /*unit_id*/108531, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お、お兄ちゃん…\nクリスマスは恋人たちの夜って\nいいますよね…だ、だからその…\nあうぅぅ…何でもないですぅ…");
INSERT INTO `unit_comments` VALUES (/*id*/1085010, /*unit_id*/108531, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"…お兄ちゃんは、私の両親みたい\nに居なくなったり…ひゃわっ！\nい、いきなり抱きしめるなんて…\nでも…あったかいです…♪");
INSERT INTO `unit_comments` VALUES (/*id*/1085011, /*unit_id*/108501, /*use_type*/2, /*voice_id*/6, /*face_id*/5, /*change_time*/7.6, /*change_face*/2, /*description*/"お兄ちゃん、お誕生日…\nお、おめでとうございますぅ…！\n私なりに一生懸命お祝いするから\nよ、喜んでくれると嬉しいです…♪");
INSERT INTO `unit_comments` VALUES (/*id*/1085012, /*unit_id*/108531, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうございますぅ\nいい子にしてたお兄ちゃんに\nプ、プレゼントを用意しました…\n靴下の中を見てみてください…♪");
INSERT INTO `unit_comments` VALUES (/*id*/1086001, /*unit_id*/108601, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/4.0, /*change_face*/2, /*description*/"メリークリスマース♪\nお兄ちゃん見て見て！\nぷうきちも私みたいに、\nクリスマス衣装なんだよ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1086002, /*unit_id*/108601, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/2.7, /*change_face*/5, /*description*/"あったか～い♪\nクリスマスのお洋服で、\nぷうきちがぬっくぬくだよ～♪\n『俺のハートが燃えてるぜ』");
INSERT INTO `unit_comments` VALUES (/*id*/1086003, /*unit_id*/108601, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/9.6, /*change_face*/5, /*description*/"サンタさんからのプレゼント…\nデッカい七面鳥に甘ぁいケーキ…\nやっぱり、\nクリスマスは最高だよぉ～♪");
INSERT INTO `unit_comments` VALUES (/*id*/1086004, /*unit_id*/108601, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/3.1, /*change_face*/3, /*description*/"クリスマスのごちそう楽しみ～♪\n…なんか、お兄ちゃんから食べて\nばかりと思われている気がする！\n私だってお料理できるんだから！");
INSERT INTO `unit_comments` VALUES (/*id*/1086005, /*unit_id*/108601, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/5.8, /*change_face*/5, /*description*/"今年のクリスマスは、\nぷうきちだけじゃなくて、\nお兄ちゃんも居る…えへへ♪\nなんだかとっても幸せかも♪");
INSERT INTO `unit_comments` VALUES (/*id*/1086006, /*unit_id*/108631, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"クリスマスのお洋服で、\nぷうきちもさらに\nかわいくなったよねぇ♪\n『ぷりてぃーだろ？』");
INSERT INTO `unit_comments` VALUES (/*id*/1086007, /*unit_id*/108631, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ふっふっふっ…見よこの王冠！\nクリスマスの私は王様なのだぁ♪\nだからお兄ちゃんは、\n私を思いっきり甘やかすように♪");
INSERT INTO `unit_comments` VALUES (/*id*/1086008, /*unit_id*/108631, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ぷうきち大家族完成～♪\nこっちは、ぷうすけとぷうきち…\nあっちは、ぷうままとぷう兄…\nじゃなくて、お兄ちゃん♪");
INSERT INTO `unit_comments` VALUES (/*id*/1086009, /*unit_id*/108631, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"雪だるまで手がつめたいからぁ…\nお兄ちゃんにたーっち♪\nえへへ…やっぱり\nお兄ちゃんはあったかいね♪");
INSERT INTO `unit_comments` VALUES (/*id*/1086010, /*unit_id*/108631, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あったかくなったら、\n雪だるまはなくなっちゃうけど…\n私たちはずーっと一緒だよね、\nお兄ちゃん♪");
INSERT INTO `unit_comments` VALUES (/*id*/1086011, /*unit_id*/108601, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/5.8, /*change_face*/6, /*description*/"お誕生日おめでとう！\nバースデーケーキは、\nぷうきちの形で作ってみたよ！\nダメっ、そっちは本物だよー！");
INSERT INTO `unit_comments` VALUES (/*id*/1086012, /*unit_id*/108631, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お兄ちゃん、誕生日おめでとー！\n大切な日を家族みたいに\n一緒に過ごせてうれしいな！\nぷうきちも喜んでるよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1087001, /*unit_id*/108701, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/3.6, /*change_face*/1, /*description*/"年末年始はみんな忙しそう！\n騎士くん。あたしの手なら\nいくらでも貸しちゃうよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1087002, /*unit_id*/108701, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/4.5, /*change_face*/2, /*description*/"動きやすい着物だから\n力仕事もオッケーだよ！\nねぇねぇ、なにすればいい？");
INSERT INTO `unit_comments` VALUES (/*id*/1087003, /*unit_id*/108701, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/6.1, /*change_face*/2, /*description*/"お正月の空気って好きだなー\n新しい年を迎えるのって\n気分がピーンってなるよね");
INSERT INTO `unit_comments` VALUES (/*id*/1087004, /*unit_id*/108701, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/5.4, /*change_face*/5, /*description*/"騎士くんだー！\nおーい、ここだよー！\n騎士くんなら、どこにいても\nすぐに分かるよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1087005, /*unit_id*/108701, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/7.8, /*change_face*/5, /*description*/"初詣のおみくじ？　大吉だよ！\nいいことばっかり書いてたなー\n恋愛運！？　…内緒っ！　");
INSERT INTO `unit_comments` VALUES (/*id*/1087006, /*unit_id*/108731, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"次はあっちの屋台に行こうよ！\n持ちきれない分は早く食べちゃお！\n騎士くんもほら、食べて食べて！");
INSERT INTO `unit_comments` VALUES (/*id*/1087007, /*unit_id*/108731, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ユイちゃんのおせちに\n屋台の食べ物…！\n最高のお正月だよね！");
INSERT INTO `unit_comments` VALUES (/*id*/1087008, /*unit_id*/108731, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"大晦日にたくさん頑張ったし\n今日くらいは、もうちょっと\n食べちゃってもいいよね？");
INSERT INTO `unit_comments` VALUES (/*id*/1087009, /*unit_id*/108731, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あっ…そのお菓子\nあたしが口付けちゃった…\n気にしない？　そ、そっか！\nならオッケー！　うん！");
INSERT INTO `unit_comments` VALUES (/*id*/1087010, /*unit_id*/108731, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"騎士くん…手、つないでもいい？\nあっ、これ食べちゃわないとだね！\nはい騎士くん！　あーんっ！");
INSERT INTO `unit_comments` VALUES (/*id*/1087011, /*unit_id*/108701, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/4.4, /*change_face*/1, /*description*/"騎士くん、誕生日おめでとー！\n騎士くんにとって\n新しい一年のはじまりだね！\n一緒にお祝いしよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1087012, /*unit_id*/108731, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハッピーバースデー！\n騎士くんがこれからの一年間も\n幸せでありますように！");
INSERT INTO `unit_comments` VALUES (/*id*/1088001, /*unit_id*/108801, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/4.7, /*change_face*/1, /*description*/"あけましておめでとう！\n騎士クン。今年もよろしくね\nおみくじはもう引いた？");
INSERT INTO `unit_comments` VALUES (/*id*/1088002, /*unit_id*/108801, /*use_type*/1, /*voice_id*/2, /*face_id*/3, /*change_time*/5.1, /*change_face*/1, /*description*/"お正月でも気を抜かずに\n魔法の修行をしないと…\n今年はもっと上達したいな");
INSERT INTO `unit_comments` VALUES (/*id*/1088003, /*unit_id*/108801, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/3.1, /*change_face*/2, /*description*/"騎士クン、寒くない？\n防寒の魔法が必要なら\nいつでも言ってね");
INSERT INTO `unit_comments` VALUES (/*id*/1088004, /*unit_id*/108801, /*use_type*/1, /*voice_id*/4, /*face_id*/5, /*change_time*/7.4, /*change_face*/8, /*description*/"いいな…恋人同士でお参り\nわたしもいつか…\nな、なんでもないよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1088005, /*unit_id*/108801, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/7.8, /*change_face*/5, /*description*/"今年の恋愛運は…\n「相手の胃袋を掴むべし」…\n…騎士クン、お腹空いてない？");
INSERT INTO `unit_comments` VALUES (/*id*/1088006, /*unit_id*/108831, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"騎士クン、去年はありがとう！\n今年もよろしくね！\n来年も…その先も！");
INSERT INTO `unit_comments` VALUES (/*id*/1088007, /*unit_id*/108831, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"…こっちに来て欲しいな\nか、風が強くて寒いから！\n…そばにいてくれる？");
INSERT INTO `unit_comments` VALUES (/*id*/1088008, /*unit_id*/108831, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"初日の出、キレイだね…\n…騎士クン？　どうしたの？\n太陽、まぶしくない？");
INSERT INTO `unit_comments` VALUES (/*id*/1088009, /*unit_id*/108831, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あのね？　今日みたいに\n毎年騎士クンと…\n…太陽が見たいの！\n…なに言ってるのわたし～！");
INSERT INTO `unit_comments` VALUES (/*id*/1088010, /*unit_id*/108831, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"騎士クン。わたしね…\n騎士クンといるどんな時間も\n幸せに思ってるから…！\n…今、言えた…！？");
INSERT INTO `unit_comments` VALUES (/*id*/1088011, /*unit_id*/108801, /*use_type*/2, /*voice_id*/6, /*face_id*/5, /*change_time*/11.3, /*change_face*/2, /*description*/"あ、あけましておめでとう！\nああっ、間違えちゃった…\nえっと…その…\nお誕生日おめでとう、騎士クン！");
INSERT INTO `unit_comments` VALUES (/*id*/1088012, /*unit_id*/108831, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"騎士クン、お誕生日おめでとう！\nこれからも騎士クンが\n幸せな日々を過ごせますように…");
INSERT INTO `unit_comments` VALUES (/*id*/1089001, /*unit_id*/108901, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"着物を着ると、いつもより\n歩き方や所作に注意して\n動くようになるんだ");
INSERT INTO `unit_comments` VALUES (/*id*/1089002, /*unit_id*/108901, /*use_type*/1, /*voice_id*/2, /*face_id*/3, /*change_time*/7.1, /*change_face*/2, /*description*/"正月といえど、羽目を外して\n暴れる輩は、この刀で成敗！\n…冗談だよ。本気にした？");
INSERT INTO `unit_comments` VALUES (/*id*/1089003, /*unit_id*/108901, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/2.9, /*change_face*/2, /*description*/"キミは正月をどう過ごすの？\n参考にしたいから、良ければ\n教えてくれない？");
INSERT INTO `unit_comments` VALUES (/*id*/1089004, /*unit_id*/108901, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/6.8, /*change_face*/5, /*description*/"いつもと雰囲気が違う？\nまぁ、着物を着ているし、\nキミの隣にいるから…かな");
INSERT INTO `unit_comments` VALUES (/*id*/1089005, /*unit_id*/108901, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/5.2, /*change_face*/2, /*description*/"おみくじを引いたら\n恋愛運が良かったんだけど…\n…さっそく運が向いたみたいだ");
INSERT INTO `unit_comments` VALUES (/*id*/1089006, /*unit_id*/108931, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"新年早々なんて邪な…！\n人の幸せを邪魔する者は\n私が全力で斬る！　いくぞ！");
INSERT INTO `unit_comments` VALUES (/*id*/1089007, /*unit_id*/108931, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"やっぱり今年も、私にはキミが\n必要だ。…お願い。これからも、\nキミの力を貸して欲しい");
INSERT INTO `unit_comments` VALUES (/*id*/1089008, /*unit_id*/108931, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"冷気を帯びたこの剣技…\nキミのおかげで、更に磨きが\nかかったよ。ありがとう");
INSERT INTO `unit_comments` VALUES (/*id*/1089009, /*unit_id*/108931, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"…私は、自分の進む道は\n自らの手で切り開きたいんだ\nそしてその道は、大切な仲間と\nキミと一緒に歩んでいきたい");
INSERT INTO `unit_comments` VALUES (/*id*/1089010, /*unit_id*/108931, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"…煩悩は去年に置いてきたはず\n気にしないで！　なんでもない！\nなんでもない、はずだから…");
INSERT INTO `unit_comments` VALUES (/*id*/1089011, /*unit_id*/108901, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/2.1, /*change_face*/2, /*description*/"誕生日おめでとう！\nキミと一緒に過ごす日々は\nこれからも私にとって\n大切な宝物だよ");
INSERT INTO `unit_comments` VALUES (/*id*/1089012, /*unit_id*/108931, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日はキミがこの世に生まれた\n特別な日だ。特別なお祝いを\nあげよう。なんでもいいよ\n…キスのフリ、以外なら…");
INSERT INTO `unit_comments` VALUES (/*id*/1090001, /*unit_id*/109001, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/12.7, /*change_face*/2, /*description*/"あなた様。手作りのチョコです\n受け取ってくださいますか…？\n愛情がいっぱい入っていますわ\nさぁ、お口を開けてください…");
INSERT INTO `unit_comments` VALUES (/*id*/1090002, /*unit_id*/109001, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/10.8, /*change_face*/2, /*description*/"いい香りでしょう…？\nハーブを混ぜてあるんです\nどんな効能か？\nクスクス…なんでしょうね…？");
INSERT INTO `unit_comments` VALUES (/*id*/1090003, /*unit_id*/109001, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/3.7, /*change_face*/2, /*description*/"周りのビンが気になりますか？\nご安心を、すべて調味料です\n料理するときは手になじむものを\n使いたいという、乙女心ですわ");
INSERT INTO `unit_comments` VALUES (/*id*/1090004, /*unit_id*/109001, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/3.2, /*change_face*/5, /*description*/"不思議なものですね\nチョコを作っているとき、\nあなた様の喜ぶ顔が見たくて\n一生懸命になってしまいました");
INSERT INTO `unit_comments` VALUES (/*id*/1090005, /*unit_id*/109001, /*use_type*/1, /*voice_id*/5, /*face_id*/5, /*change_time*/11.2, /*change_face*/2, /*description*/"こうして料理をしていると、\n本当の伴侶のようですわね\nもちろん、いずれは本当に\nなるのですけれど…クスクス");
INSERT INTO `unit_comments` VALUES (/*id*/1090006, /*unit_id*/109031, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"バレンタインデーとは\n愛する方にチョコレートを\nお贈りする日…私の甘い愛で\nお腹を満たして差し上げましょう");
INSERT INTO `unit_comments` VALUES (/*id*/1090007, /*unit_id*/109031, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"運命で結ばれたあなた様への想い\nチョコだけでは表現しきれません\n三日三晩かけて想いを綴った\nお手紙も一緒にお付けしますわ…");
INSERT INTO `unit_comments` VALUES (/*id*/1090008, /*unit_id*/109031, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"エプロン、似合っていますか？\nクスクス…うれしいですわ\nでしたら毎日お見せしますね\nどんな時間、どんな場所でも…");
INSERT INTO `unit_comments` VALUES (/*id*/1090009, /*unit_id*/109031, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"バレンタインデーでは、チョコと\n一緒に告白する方もいるみたい\nですが…特別な日じゃなくても\n私は毎日愛をお伝えしますわ");
INSERT INTO `unit_comments` VALUES (/*id*/1090010, /*unit_id*/109031, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"調合した薬を混ぜるのもいいです\nけれど、やっぱりチョコレートを\n甘くするのは、込めた想いですわ\n愛しています…あなた様");
INSERT INTO `unit_comments` VALUES (/*id*/1090011, /*unit_id*/109001, /*use_type*/2, /*voice_id*/6, /*face_id*/5, /*change_time*/11.2, /*change_face*/2, /*description*/"あなた様が生まれた日…つまり\n私たちの運命が始まった日ですわ\n祝杯をあげましょう\nさぁ、これをお飲みになって…");
INSERT INTO `unit_comments` VALUES (/*id*/1090012, /*unit_id*/109031, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日おめでとうございます\nふたりきりでお祝いする準備は\nできていますわ\nそのあとは…クスクス");
INSERT INTO `unit_comments` VALUES (/*id*/1091001, /*unit_id*/109101, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/6.7, /*change_face*/2, /*description*/"弟くん、バレンタインは\n知ってる？　大好きな人に\nチョコレートをあげる日なんだよ\nお姉ちゃんの愛、受け止めてね？");
INSERT INTO `unit_comments` VALUES (/*id*/1091002, /*unit_id*/109101, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/7.5, /*change_face*/5, /*description*/"お姉ちゃんのよしよしみたいに\n甘いチョコか、お姉ちゃんの\nナデナデみたいに大人なチョコ\n弟くんはどっちがいいかな？");
INSERT INTO `unit_comments` VALUES (/*id*/1091003, /*unit_id*/109101, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/2.0, /*change_face*/1, /*description*/"虫歯になっちゃいそう？\n大丈夫だよっ、お姉ちゃんが\n弟くんの歯を綺麗に磨いてあげる\nあーんしてみて♪");
INSERT INTO `unit_comments` VALUES (/*id*/1091004, /*unit_id*/109101, /*use_type*/1, /*voice_id*/4, /*face_id*/5, /*change_time*/5.5, /*change_face*/2, /*description*/"ナデナデしてあげようか？\nそれとも肩もみ？　膝枕？\nチョコとセットで、お姉ちゃんの\nあま～いお世話はいかがっ？");
INSERT INTO `unit_comments` VALUES (/*id*/1091005, /*unit_id*/109101, /*use_type*/1, /*voice_id*/5, /*face_id*/5, /*change_time*/7.1, /*change_face*/2, /*description*/"ほら、口元にチョコが付いてるよ\nお姉ちゃんがとって…あむ\nうん、とっても甘い♪");
INSERT INTO `unit_comments` VALUES (/*id*/1091006, /*unit_id*/109131, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"バレンタインは甘いチョコと\n一緒に愛を伝える日♪\nお姉ちゃんの愛はチョコよりも\n甘いから、覚悟しててね♪");
INSERT INTO `unit_comments` VALUES (/*id*/1091007, /*unit_id*/109131, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"チョコはいくつもらえたのかな？\nお姉ちゃんに教えてごらん？\n弟くんのすべてを知りたい\n純粋な姉心だよ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1091008, /*unit_id*/109131, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"そわそわしちゃってどうしたの？\n弟くんは何を待ってるのかな？\nふふっ、焦らされる弟くんも\nかわいいなぁ～♪");
INSERT INTO `unit_comments` VALUES (/*id*/1091009, /*unit_id*/109131, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"えっ？　この衣装、似合ってる？\n弟くんったら、女の子の褒め方が\n上手になったね♪\nお姉ちゃん、ドキッとしちゃった");
INSERT INTO `unit_comments` VALUES (/*id*/1091010, /*unit_id*/109131, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"弟くん、大好きだよ♪\nチョコはまだまだあるから\nあげるたびに言ってあげるね♪");
INSERT INTO `unit_comments` VALUES (/*id*/1091011, /*unit_id*/109101, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/2.8, /*change_face*/2, /*description*/"今日は弟くんの誕生日！\n大きなチョコケーキを作ったから\n一緒に食べようね♪");
INSERT INTO `unit_comments` VALUES (/*id*/1091012, /*unit_id*/109131, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日だね！　お姉ちゃんは\n今日という記念日のために、\nプレゼントを考え続けてたよ！\nはい、お姉ちゃんをあげるね☆");
INSERT INTO `unit_comments` VALUES (/*id*/1092001, /*unit_id*/109201, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/5.0, /*change_face*/2, /*description*/"せっかく別の世界に来たんだし、\nあなたとも仲良くなりたいな！\nよろしくねっ");
INSERT INTO `unit_comments` VALUES (/*id*/1092002, /*unit_id*/109201, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/4.0, /*change_face*/1, /*description*/"確かに、マナリアの第一王女では\nあるけど…あまり気にしないで、\n気軽に話してくれるとうれしいな！");
INSERT INTO `unit_comments` VALUES (/*id*/1092003, /*unit_id*/109201, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/3.7, /*change_face*/2, /*description*/"普段はグレアと二人でいることが\n多いかな！　グレアの隣は\nとっても落ち着くし、安心するの");
