INSERT INTO `unit_comments` VALUES (/*id*/1118007, /*unit_id*/111831, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"おせちには魔除けの意味も\nあるみたいなんですけど\n素材に魔物を使うのは…\n魔を倒してるからありですよね☆");
INSERT INTO `unit_comments` VALUES (/*id*/1118008, /*unit_id*/111831, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今年もこうやってみんなで\nおいしいものをたくさん\n食べて行きたいですね！");
INSERT INTO `unit_comments` VALUES (/*id*/1118009, /*unit_id*/111831, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"こうして料理を食べるのは\nいつものことですけど、お正月を\n一緒に過ごしてるって思うと\nなんだか照れちゃいますね♪");
INSERT INTO `unit_comments` VALUES (/*id*/1118010, /*unit_id*/111831, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"毎年こうやってあなたに\n手作りおせちを食べさせたいって\n思うのは…欲張りでしょうか？");
INSERT INTO `unit_comments` VALUES (/*id*/1118011, /*unit_id*/111801, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/7.1, /*change_face*/1, /*description*/"誕生日おめでとうございます！\n縁起のいい食べ物をたくさん\n揃えたので一緒に食べましょう！\n魔物は…ヒミツです☆");
INSERT INTO `unit_comments` VALUES (/*id*/1118012, /*unit_id*/111831, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうございます\n今年もこうしてあなたと過ごせて\n本当に幸せです☆\n来年もお祝いさせてくださいね？");
INSERT INTO `unit_comments` VALUES (/*id*/1119001, /*unit_id*/111901, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/5.2, /*change_face*/2, /*description*/"主さま、新年あけまして\nおめでとうございます\n今年も主さまと一緒に\n歩めることを幸せに思います");
INSERT INTO `unit_comments` VALUES (/*id*/1119002, /*unit_id*/111901, /*use_type*/1, /*voice_id*/2, /*face_id*/4, /*change_time*/3.9, /*change_face*/1, /*description*/"まだまだ寒い日が続きますね\n主さま、お手手を繋ぎ\n暖めあって歩くといたしましょう\nぎゅっと握ってくださいまし");
INSERT INTO `unit_comments` VALUES (/*id*/1119003, /*unit_id*/111901, /*use_type*/1, /*voice_id*/3, /*face_id*/4, /*change_time*/8.0, /*change_face*/5, /*description*/"たまの里帰りもいいものですが\n主さまと離れるのは心細いです\n次の里帰りは一緒に、というのは\n欲張りでしょうか…？");
INSERT INTO `unit_comments` VALUES (/*id*/1119004, /*unit_id*/111901, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/4.9, /*change_face*/1, /*description*/"甘酒には疲労回復の効果が\nあるとのことです。お疲れの際は\nわたくしがいつでもフーフーして\n飲ませて差し上げます");
INSERT INTO `unit_comments` VALUES (/*id*/1119005, /*unit_id*/111901, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/6.7, /*change_face*/5, /*description*/"主さまにお仕えするという至高の\n初夢を見ることができました\n主さまの初夢にも、わたくしが\n出ていたならいいのですが…");
INSERT INTO `unit_comments` VALUES (/*id*/1119006, /*unit_id*/111931, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"一つ一つの動きに、主さまへの\n想いを込めております\n拙い舞ではございますが\n見守っていてくださいまし");
INSERT INTO `unit_comments` VALUES (/*id*/1119007, /*unit_id*/111931, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"わたくしの想いに反応して\n精霊たちも楽しそうに\n舞っておりますね。主さまも\nお楽しみいただけておりますか？");
INSERT INTO `unit_comments` VALUES (/*id*/1119008, /*unit_id*/111931, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"風情のある舞台で主さまと\n二人きりで過ごせる…\nわたくしにとって最高の\nお年玉でございます");
INSERT INTO `unit_comments` VALUES (/*id*/1119009, /*unit_id*/111931, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今年の抱負でございますか？\nそれはもちろんこれまで通り…\nいえ、これまで以上に主さまを\nお導きすることでございます");
INSERT INTO `unit_comments` VALUES (/*id*/1119010, /*unit_id*/111931, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"主さまを想って舞っていると\n心も体も暖かくなります\nこの熱を主さまと分け合うことが\nできればいいのに…");
INSERT INTO `unit_comments` VALUES (/*id*/1119011, /*unit_id*/111901, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/5.9, /*change_face*/5, /*description*/"お誕生日おめでとうございます\nもし…もし主さまがよろしければ\n二人きりで過ごすことを\nお許しいただきたく…");
INSERT INTO `unit_comments` VALUES (/*id*/1119012, /*unit_id*/111931, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日は主さまにとって\nおめでたい一日。祝いの舞を\n奉納させてくださいまし\n心からの祝福をお贈りいたします");
INSERT INTO `unit_comments` VALUES (/*id*/1120001, /*unit_id*/112001, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/4.0, /*change_face*/2, /*description*/"なに？　あたしと羽根つきで\n勝負したいの？\n手加減しないからね！\n墨まみれにしてあげるわ！");
INSERT INTO `unit_comments` VALUES (/*id*/1120002, /*unit_id*/112001, /*use_type*/1, /*voice_id*/2, /*face_id*/5, /*change_time*/4.6, /*change_face*/1, /*description*/"あけましておめでとう\nまぁ今年もよろしくね\nまた騒がしい一年に\nなりそうだけど…");
INSERT INTO `unit_comments` VALUES (/*id*/1120003, /*unit_id*/112001, /*use_type*/1, /*voice_id*/3, /*face_id*/4, /*change_time*/7.8, /*change_face*/6, /*description*/"みんな年の始めから元気ね\nあたしは家で静かに過ごしたい…\nい、いや、別にあんたと居るのが\n嫌ってワケじゃないから！");
INSERT INTO `unit_comments` VALUES (/*id*/1120004, /*unit_id*/112001, /*use_type*/1, /*voice_id*/4, /*face_id*/3, /*change_time*/5.5, /*change_face*/2, /*description*/"着物が似合ってる？\nいちいち言わなくていいわよ！\nけど、見る目あるじゃない\nふふん。いい気分だわ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1120005, /*unit_id*/112001, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/5.9, /*change_face*/5, /*description*/"おみくじ引いたんだけど、\n待ち人はすぐそばに、だって\nまさかあんたのこと…？\nあ、あっちいきなさい！");
INSERT INTO `unit_comments` VALUES (/*id*/1120006, /*unit_id*/112031, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"せっかくだからいろいろ願い事\nしたいわよね。あんたは何を\n願うの？　あたしのこと？\nふ、ふーん。そ、そう…");
INSERT INTO `unit_comments` VALUES (/*id*/1120007, /*unit_id*/112031, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"夜の参道ってのも悪くないわね\n一緒なら怖くないし…って！\n暗いの平気だから！\n勘違いしないでよね！");
INSERT INTO `unit_comments` VALUES (/*id*/1120008, /*unit_id*/112031, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ほら、行くわよ\n疲れたっていうなら\n手繋いでやっても…て、本当に\n繋ぐ気？　べ、別にいいけど…");
INSERT INTO `unit_comments` VALUES (/*id*/1120009, /*unit_id*/112031, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"神殿にお参りするだけなのに\n特別な気がするのは\n着物のおかげ？　それとも…\nあんたと一緒だから？");
INSERT INTO `unit_comments` VALUES (/*id*/1120010, /*unit_id*/112031, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"すごく静かね…まさか道間違えて\nないわよね？　まぁ、のんびり\n行くのも悪くないかもね。二人で\nゆっくり歩きましょ");
INSERT INTO `unit_comments` VALUES (/*id*/1120011, /*unit_id*/112001, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/5.7, /*change_face*/4, /*description*/"ハッピーバースデー\nケーキ買ってきたから\n一緒に食べましょう\n変なモノは入ってないわよ？");
INSERT INTO `unit_comments` VALUES (/*id*/1120012, /*unit_id*/112031, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとう\nい、一応プレゼントも用意して\nあるのよ？　\nありがたく受け取りなさい！");
INSERT INTO `unit_comments` VALUES (/*id*/1121001, /*unit_id*/112101, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/5.9, /*change_face*/1, /*description*/"あなたもお参りに来たんですね\n私は見ての通り、巫女さんです！\n年末から新年にかけて、神殿の\nお仕事をお手伝いしてるんです");
INSERT INTO `unit_comments` VALUES (/*id*/1121002, /*unit_id*/112101, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/2.7, /*change_face*/1, /*description*/"この服、かわいいですよね♪\n巫女さんの服を着る機会なんて\n普段はありませんから\n新鮮で楽しいんです");
INSERT INTO `unit_comments` VALUES (/*id*/1121003, /*unit_id*/112101, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/5.2, /*change_face*/2, /*description*/"神殿には色んなお守りがあるので\nぜひ見ていってくださいね\n私は家内安全のお守りを\n買っておきました～");
INSERT INTO `unit_comments` VALUES (/*id*/1121004, /*unit_id*/112101, /*use_type*/1, /*voice_id*/4, /*face_id*/4, /*change_time*/4.5, /*change_face*/5, /*description*/"はぁ～…寒くて手が冷えますね\n…はわっ！？　あ、あのぉ…\nい、いきなり手をギュって\n握られると、恥ずかしいです…");
INSERT INTO `unit_comments` VALUES (/*id*/1121005, /*unit_id*/112101, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/3.4, /*change_face*/6, /*description*/"もしよければ、甘酒を飲みます？\n…え、私と分け合いっこ！？\nあ、ああ、あの、仕事中なので！\nわわ、私は、だ、大丈夫れしゅ！！");
INSERT INTO `unit_comments` VALUES (/*id*/1121006, /*unit_id*/112131, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"おはようございますっ！\n今日は参拝に来たんですか？\n私は神殿のお掃除中です\nほら、きれいになりましたよ～♪");
INSERT INTO `unit_comments` VALUES (/*id*/1121007, /*unit_id*/112131, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"もうすぐ休憩時間なので\n私もご一緒していいですか？\nこの神殿の見どころを\n色々教えて頂いたんです！");
INSERT INTO `unit_comments` VALUES (/*id*/1121008, /*unit_id*/112131, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"それじゃあ、張り切って続きを…\nえ、危ない？　何がですか？\n…はわっ！　崩れちゃう！？\nわ、ちょ、まっ、きゃー！！");
INSERT INTO `unit_comments` VALUES (/*id*/1121009, /*unit_id*/112131, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"恋愛運に書かれていたんですけど\n今年は「身近なところに縁あり」\nらしくて…あっ！？　い、いえ！\nべ、別に深い意味はないですよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1121010, /*unit_id*/112131, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなたの恋愛運はどうでした？\n…え？　失敗から始まる縁？\nそれってもしかして、私と…\nはっ！　な、なんでもないです！");
INSERT INTO `unit_comments` VALUES (/*id*/1121011, /*unit_id*/112101, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/5.1, /*change_face*/6, /*description*/"お誕生日おめでとうございます！\n私もあなたにプレゼントを…\nって、あれ…？　ない！？\nわ、忘れちゃいましたぁ…！");
INSERT INTO `unit_comments` VALUES (/*id*/1121012, /*unit_id*/112131, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日はあなたのお誕生日ですよね\nおめでとうございます！\nこれから良い一年を過ごせる\nおまじないをかけちゃいますね♪");
INSERT INTO `unit_comments` VALUES (/*id*/1122001, /*unit_id*/112201, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/6.4, /*change_face*/4, /*description*/"へ、変身したら、\n髪型まで変わってる！？\n一体どういう理論で…はあ\nもう、考えないようにしよう…");
INSERT INTO `unit_comments` VALUES (/*id*/1122002, /*unit_id*/112201, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/5.9, /*change_face*/5, /*description*/"魔法探偵ミスティ★カスミか…\nちょっとだけ楽しいかも♪\n…って、助手くん！？　\nい、今のは忘れてくれたまえ！");
INSERT INTO `unit_comments` VALUES (/*id*/1122003, /*unit_id*/112201, /*use_type*/1, /*voice_id*/3, /*face_id*/4, /*change_time*/5.1, /*change_face*/1, /*description*/"うぅ、周りの視線が気になる…\nん…助手くん、前に立ってくれる\nのかい？　ありがたいよ\nふふ。お姫様気分だね");
INSERT INTO `unit_comments` VALUES (/*id*/1122004, /*unit_id*/112201, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/3.0, /*change_face*/4, /*description*/"ミスティ、セオリー、\nトゥルーリィ！\n…はぁ。だんだんと\n慣れてきているのが複雑だね…");
INSERT INTO `unit_comments` VALUES (/*id*/1122005, /*unit_id*/112201, /*use_type*/1, /*voice_id*/5, /*face_id*/5, /*change_time*/6.3, /*change_face*/1, /*description*/"歩いているだけで子供に囲まれる\nなんて…魔法少女って人気なんだ\n助手くん、君も魔法少女のこと\n好きだったりするのかな？");
INSERT INTO `unit_comments` VALUES (/*id*/1122006, /*unit_id*/112231, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ミスティセオリートゥルーリィ！\nマジカルリボン、スパイラル！\n魔法探偵ミスティ★カスミ\n魔法の力で捜査開始！");
INSERT INTO `unit_comments` VALUES (/*id*/1122007, /*unit_id*/112231, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"…や、やっぱり恥ずかしいね\nあまりジロジロ見てはダメだよ？\nこ、こら！　近い、近いったら！\nもう、これは現行犯で確保だね！");
INSERT INTO `unit_comments` VALUES (/*id*/1122008, /*unit_id*/112231, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"体の奥からすごい力を感じるよ\nけれど、この格好で動き回ると\nその、スカートとかヒラヒラして\n落ち着かないんだよね…");
INSERT INTO `unit_comments` VALUES (/*id*/1122009, /*unit_id*/112231, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"助手くん、どうかな？\n魔法少女らしく見えるかね？\nこんなはじけた姿、君の前でしか\n見せられないよ。まったく…");
INSERT INTO `unit_comments` VALUES (/*id*/1122010, /*unit_id*/112231, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"魔法少女と、パートナー\n探偵と、助手\n私たちはどうやら、離れられない\n運命のようだね。…ふふっ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1122011, /*unit_id*/112201, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/5.9, /*change_face*/2, /*description*/"今日は助手くんの誕生日だよね\n魔法少女の役目を\n早く終わらせてくるから、二人で\n一緒にお祝いしようじゃないか♪");
INSERT INTO `unit_comments` VALUES (/*id*/1122012, /*unit_id*/112231, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"…魔法の力で捜査開始！\nた、誕生日のお祝いとして、\n君のためだけに変身して\nみたんだけど…ど、どうかな？");
INSERT INTO `unit_comments` VALUES (/*id*/1123001, /*unit_id*/112301, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"私、魔法少女になったんです\n私もお姉ちゃんみたいに\nたくさん人助けできたらいいな\nなんて…えへへ");
INSERT INTO `unit_comments` VALUES (/*id*/1123002, /*unit_id*/112301, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/5.4, /*change_face*/2, /*description*/"魔法少女になってから\nあまり体力のことで\n悩まなくなったんです\nなんだか出来すぎた物語みたい…");
INSERT INTO `unit_comments` VALUES (/*id*/1123003, /*unit_id*/112301, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/2.8, /*change_face*/5, /*description*/"魔法少女って憧れだったんです\n見た目は、その…\nちょっと恥ずかしいですけど");
INSERT INTO `unit_comments` VALUES (/*id*/1123004, /*unit_id*/112301, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/5.0, /*change_face*/4, /*description*/"早く一人前の魔法少女に\nならなきゃって思うんですけど…\n読書の時間がなくなっちゃう…！");
INSERT INTO `unit_comments` VALUES (/*id*/1123005, /*unit_id*/112301, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/5.0, /*change_face*/2, /*description*/"ランドソルの街を歩いてたら\n子供たちに囲まれちゃいました\nあの子たちを守るためにも\nもっともっと頑張らなくちゃ…");
INSERT INTO `unit_comments` VALUES (/*id*/1123006, /*unit_id*/112331, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ピュアなハートにロックオン\nマジカルアロー、クリティカル！\n魔法狩人ピュアリー★シオリ\nあなたの心に愛の矢を！");
INSERT INTO `unit_comments` VALUES (/*id*/1123007, /*unit_id*/112331, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ランドソルの平和は私が守ります\n体からあふれる愛と希望の力…\nこの一射に愛をこめて！");
INSERT INTO `unit_comments` VALUES (/*id*/1123008, /*unit_id*/112331, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ここは私に任せてください！\nアンチビーストを倒して\nみなさんに笑顔を取り戻します！");
INSERT INTO `unit_comments` VALUES (/*id*/1123009, /*unit_id*/112331, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"魔法少女になったおかげで、少し\n自信がついたかもしれません\nこの街を守る覚悟ができました\nあなたのことも…");
INSERT INTO `unit_comments` VALUES (/*id*/1123010, /*unit_id*/112331, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"アンチビーストが街を襲うかぎり\n魔法少女は戦い続けます\nそばにいてくださいね…あなたの\n存在が、私の力になりますから！");
INSERT INTO `unit_comments` VALUES (/*id*/1123011, /*unit_id*/112301, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"今日、お誕生日ですよね\nふふ、ちゃんと覚えてますよ\n魔法で盛大にお祝いしなきゃ");
INSERT INTO `unit_comments` VALUES (/*id*/1123012, /*unit_id*/112331, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうございます\n今後もお祝いさせてくださいね\nこれからも、ずっと…");
INSERT INTO `unit_comments` VALUES (/*id*/1124001, /*unit_id*/112401, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/3.8, /*change_face*/2, /*description*/"千里の道も一歩から、ですよね\n素敵なアイドルを目指して\nレッスン頑張ります！");
INSERT INTO `unit_comments` VALUES (/*id*/1124002, /*unit_id*/112401, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/4.6, /*change_face*/4, /*description*/"冒険者としての実力、ですか？\n実はあんまり自信がないんです\n運動神経は普通ですし\nそこまで活躍は…うぅ…");
INSERT INTO `unit_comments` VALUES (/*id*/1124003, /*unit_id*/112401, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/2.6, /*change_face*/1, /*description*/"今からダンスの練習なんです！\nどこかおかしいところがないか\n見てもらってもいいですか？");
INSERT INTO `unit_comments` VALUES (/*id*/1124004, /*unit_id*/112401, /*use_type*/1, /*voice_id*/4, /*face_id*/5, /*change_time*/9.6, /*change_face*/2, /*description*/"鼻歌を口ずさむ時、たまに…\nすごく懐かしい、大切な曲を\n歌ってる気持ちになるんです\n不思議ですよね、えへへ");
INSERT INTO `unit_comments` VALUES (/*id*/1124005, /*unit_id*/112401, /*use_type*/1, /*voice_id*/5, /*face_id*/3, /*change_time*/3.0, /*change_face*/4, /*description*/"やぁー！　とぉー！\nう～ん、剣を振る時って\nもっと頭から叩くように\nする方がいいんでしょうか…？");
INSERT INTO `unit_comments` VALUES (/*id*/1124006, /*unit_id*/112431, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"これ、夢じゃないんですよね…？\n私にこんな力があったなんて…\nまるで、魔法をかけられた\nおとぎ話の主人公みたいです…！");
INSERT INTO `unit_comments` VALUES (/*id*/1124007, /*unit_id*/112431, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"魔法でお姫様に変身…じゃなくて\nなんだか勇者になったみたいです\nでも、自分の力で切り開くって\n意味では、ピッタリかもですね！");
INSERT INTO `unit_comments` VALUES (/*id*/1124008, /*unit_id*/112431, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私は、普通の女の子ですけど…\n自分らしい、私だけのキラキラが\nあるんだって胸を張って言えます\nだって私は、アイドルですから！");
INSERT INTO `unit_comments` VALUES (/*id*/1124009, /*unit_id*/112431, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"これ、伝説の剣を召喚！\nみたいにできたりしませんかね？\n念じたらもしかして…う～ん…！\n…で、できないみたいです…");
INSERT INTO `unit_comments` VALUES (/*id*/1124010, /*unit_id*/112431, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"そうだ！　私もリンちゃんみたいな\n必殺技を使うのはどうでしょう？\nそうですね～…勇気！　元気！\nピンキー・ライブリィ！　…とか？");
INSERT INTO `unit_comments` VALUES (/*id*/1124011, /*unit_id*/112401, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/4.7, /*change_face*/2, /*description*/"みなさんから、今日があなたの\nお誕生日だって聞きました\nおめでとうございます！\nきっといい一年になりますよ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1124012, /*unit_id*/112431, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうございます！\n色んなことでお世話になったので\nそのお礼も兼ねたプレゼントです\nどうぞ、受け取ってください♪");
INSERT INTO `unit_comments` VALUES (/*id*/1125001, /*unit_id*/112501, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/5.4, /*change_face*/1, /*description*/"アイドルに向いてるのかどうか\n自分じゃよく分からないけど…\nやると決めたからには頑張るよ");
INSERT INTO `unit_comments` VALUES (/*id*/1125002, /*unit_id*/112501, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/4.9, /*change_face*/6, /*description*/"冒険者としての腕なら\n結構自信があるんだけどね\nアイドルって全然違うことだし\n今後どうなるか想像できないな");
INSERT INTO `unit_comments` VALUES (/*id*/1125003, /*unit_id*/112501, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/2.8, /*change_face*/2, /*description*/"アイドルは体力も必要…\nなら、剣の素振りでもすれば\n冒険者としての修行にもなるよね");
INSERT INTO `unit_comments` VALUES (/*id*/1125004, /*unit_id*/112501, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/11.0, /*change_face*/3, /*description*/"ラズール・レオ！　…違うかな？\nもっとこう、相手を貫くように…\nもう一回！　ラズール・レオ！");
INSERT INTO `unit_comments` VALUES (/*id*/1125005, /*unit_id*/112501, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/6.7, /*change_face*/2, /*description*/"ちょっと疲れたし休もうかな…\nキミもチョコレート食べる？\n甘いものを食べると\n元気が出てくるよね");
INSERT INTO `unit_comments` VALUES (/*id*/1125006, /*unit_id*/112531, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誰が相手だろうと退かないよ\n私は立ち止まらないって決めた…\nだから、倒して先に進むだけ！\n蒼の剣を受けよ！　はぁーっ！！");
INSERT INTO `unit_comments` VALUES (/*id*/1125007, /*unit_id*/112531, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私と戦ったのが失敗だったね\n見た目で油断したんだろうけど…\nこう見えても私、結構強いんだ\n自分の甘い判断を恨むんだね");
INSERT INTO `unit_comments` VALUES (/*id*/1125008, /*unit_id*/112531, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"…アイドルを目指してるのに\nこんなことしてて大丈夫かな？\nえ、それはそれで需要がある？\n本当かな、だといいんだけど…");
INSERT INTO `unit_comments` VALUES (/*id*/1125009, /*unit_id*/112531, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"そろそろ、何か新しい魔法を\n考えてみるのもアリかもね\n名前は…ラピス、ターコイズ…\nうーん、どういうのがいいかな…");
INSERT INTO `unit_comments` VALUES (/*id*/1125010, /*unit_id*/112531, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"呪文を唱えてる理由が知りたい？\nなんていうんだろう、魔法の形を\nイメージしやすくなるんだよね\n…それにほら、カッコいいでしょ");
INSERT INTO `unit_comments` VALUES (/*id*/1125011, /*unit_id*/112501, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/7.5, /*change_face*/1, /*description*/"今日、キミの誕生日なんだって？\nみんなから聞いたよ、おめでとう\n今からお祝いするって言ってたし\n一緒に行こうか");
INSERT INTO `unit_comments` VALUES (/*id*/1125012, /*unit_id*/112531, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日はキミの誕生日だよね\nはいこれ、プレゼント\nあんまり大した物じゃないけど…\nお世話になったお礼ってことで");
INSERT INTO `unit_comments` VALUES (/*id*/1126001, /*unit_id*/112601, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/4.8, /*change_face*/2, /*description*/"冒険とアイドル、どっちも\n全力投球で頑張っちゃうよ！\nミオちゃんの応援よろしく～☆");
INSERT INTO `unit_comments` VALUES (/*id*/1126002, /*unit_id*/112601, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/5.9, /*change_face*/1, /*description*/"ほらほら、早く出かけようよ！\nワクワクの冒険が待ってるぞ～！\nあ、その後はレッスンするから\nちゃーんと手伝ってね☆");
INSERT INTO `unit_comments` VALUES (/*id*/1126003, /*unit_id*/112601, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/2.6, /*change_face*/1, /*description*/"よっ、ほっ…！\nえへへ、自主練やってるんだー\n最高のアイドルに少しでも\n近づけるように、頑張らなきゃ！");
INSERT INTO `unit_comments` VALUES (/*id*/1126004, /*unit_id*/112601, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/9.0, /*change_face*/6, /*description*/"大きな声で挨拶するのは\nアイドルの基本中の基本！\nせーの、おいっす！\nこらこら声が小さいぞ～？");
INSERT INTO `unit_comments` VALUES (/*id*/1126005, /*unit_id*/112601, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/6.6, /*change_face*/4, /*description*/"魔物料理って結構おいしいのに\nあんまり一般的じゃないよね～\n確かに最初は勇気がいるけど\n食べたらアリなのになぁ…");
INSERT INTO `unit_comments` VALUES (/*id*/1126006, /*unit_id*/112631, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"さぁ、歌って踊れるアイドル\nミオちゃんのショーが始まるよ！\nキラキラ輝くこの魔法で、\nみんなをファンにしちゃうから☆");
INSERT INTO `unit_comments` VALUES (/*id*/1126007, /*unit_id*/112631, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"踊りながら魔法を使ってると\nテンションが上がって楽しいの！\nこういうところでもアイドルの\n経験は活かさなくっちゃね☆");
INSERT INTO `unit_comments` VALUES (/*id*/1126008, /*unit_id*/112631, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この魔法陣、キレイでしょ？\nたくさん描くと星も増えていって\n流星がきらめく感じになるの\n私も気に入ってるんだ！");
INSERT INTO `unit_comments` VALUES (/*id*/1126009, /*unit_id*/112631, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"そうだ！　ミオちゃんがキミの\nリクエストに答えてあげよう！\nどんな魔法陣を描いてほしい？\nパパっとやってみちゃうよ～☆");
INSERT INTO `unit_comments` VALUES (/*id*/1126010, /*unit_id*/112631, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"笑顔はパワーのみなもと☆\n大変なことはたくさんあるけど\nいつでも笑顔を忘れない\nそれがミオちゃんなのだー！");
INSERT INTO `unit_comments` VALUES (/*id*/1126011, /*unit_id*/112601, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/5.7, /*change_face*/1, /*description*/"パンパカパーン、おめでとー！\n今日はキミの誕生日なんでしょ？\nほら、おめでたい日なんだし\nみんなでレッツパーリーだよっ！");
INSERT INTO `unit_comments` VALUES (/*id*/1126012, /*unit_id*/112631, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日、おっめでと～！\nまた一つ大人になったキミへ\nミオちゃんからのプレゼント！\nちゃーんと大事にしてよね☆");
INSERT INTO `unit_comments` VALUES (/*id*/1127001, /*unit_id*/112701, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/7.4, /*change_face*/4, /*description*/"何の因果か労働に勤しむあたし\nま、ほどほどにがんばるよ\nあ～帰りたい");
INSERT INTO `unit_comments` VALUES (/*id*/1127002, /*unit_id*/112701, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/3.5, /*change_face*/5, /*description*/"働いた後のあんパンは美味しいね\nまあ、働いてなくても\n美味しいんだけど");
INSERT INTO `unit_comments` VALUES (/*id*/1127003, /*unit_id*/112701, /*use_type*/1, /*voice_id*/3, /*face_id*/3, /*change_time*/3.6, /*change_face*/4, /*description*/"ねえ、次の休みはいつ？\nげぇ～、マジで…\n働き過ぎて死んじゃうよ～");
INSERT INTO `unit_comments` VALUES (/*id*/1127004, /*unit_id*/112701, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/5.9, /*change_face*/2, /*description*/"働き出してからもあんたには\nなにかと助けられっぱなしだね\n気付いてないかもだけど\n結構頼りにしてるんだよ？");
INSERT INTO `unit_comments` VALUES (/*id*/1127005, /*unit_id*/112701, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/5.1, /*change_face*/5, /*description*/"仕事も遊びも、気付けば\nいつもあんたと一緒だね\nそばに誰かいてくれるって\n案外いいもんだね");
INSERT INTO `unit_comments` VALUES (/*id*/1127006, /*unit_id*/112731, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"森の中はあたしの独壇場！\nリスの怖さを見せてあげるよ\nさあ、疲れる前に終わらすよ～");
INSERT INTO `unit_comments` VALUES (/*id*/1127007, /*unit_id*/112731, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ん～？　魔物退治のお仕事？\nはいはい、さくっとやっちゃうよ\nあんたは勝利のあんパンの\n用意でもしといてね～");
INSERT INTO `unit_comments` VALUES (/*id*/1127008, /*unit_id*/112731, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あたし、なかなかキマってない？\nカッコよくない？\nまた人気出ちゃうよ～\n困るな～マジで～");
INSERT INTO `unit_comments` VALUES (/*id*/1127009, /*unit_id*/112731, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"戦いに慣れてきたとはいえ\nやっぱりあんたが隣にいると\n心強いし、がんばれるよ\n…あんがとね");
INSERT INTO `unit_comments` VALUES (/*id*/1127010, /*unit_id*/112731, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"色々あってあたしの日常は\n変わっちゃったけど、あんたは\n変わらずにいてくれるんだね\nそういうの、結構うれしいな");
INSERT INTO `unit_comments` VALUES (/*id*/1127011, /*unit_id*/112701, /*use_type*/2, /*voice_id*/6, /*face_id*/6, /*change_time*/6.6, /*change_face*/7, /*description*/"え、あんた今日誕生日なの？\n忙しくて用意できてないよ…\nと、とりあえず今はこの\nキレイなドングリで！");
INSERT INTO `unit_comments` VALUES (/*id*/1127012, /*unit_id*/112731, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日おめでと～。ゲームと\nあんパンでお祝いするよ～\nさあ、ゴロゴロしながら思い切り\n楽しもうね～");
INSERT INTO `unit_comments` VALUES (/*id*/1128001, /*unit_id*/112801, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/3.0, /*change_face*/3, /*description*/"今のオラは牧場の守護神だべ！\nみんなには指一本ふれさせねぇ！");
INSERT INTO `unit_comments` VALUES (/*id*/1128002, /*unit_id*/112801, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/3.3, /*change_face*/1, /*description*/"強くなると農作業もはかどるべ\n戦いは好きじゃねぇけど\n力も使い方次第で人を\n幸せにできるべさ");
INSERT INTO `unit_comments` VALUES (/*id*/1128003, /*unit_id*/112801, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/6.3, /*change_face*/2, /*description*/"この格好、子供たちに\nウケがいいべ！　ネタでとった\n笑いじゃなくても\nやっぱ笑顔はうれしいべさ！");
INSERT INTO `unit_comments` VALUES (/*id*/1128004, /*unit_id*/112801, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/5.4, /*change_face*/5, /*description*/"よ、鎧姿が珍しいからって\nそんなにじっと見られると\nは、恥ずかしいべ…");
INSERT INTO `unit_comments` VALUES (/*id*/1128005, /*unit_id*/112801, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/7.4, /*change_face*/6, /*description*/"オラたちお笑いだけじゃなくて\n戦いも息ピッタリだべ！\nもういっそ婿に来て…\nな、なんでもねぇべさ！");
INSERT INTO `unit_comments` VALUES (/*id*/1128006, /*unit_id*/112831, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"見た目で判断すると\nいてぇ目にあうど？\n体はちっこいけど\n心に秘めた愛は無限大だべ！");
INSERT INTO `unit_comments` VALUES (/*id*/1128007, /*unit_id*/112831, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今だけは笑い無しだべ！\n牧場の平和を乱すヤツは\nオラの突きで月まで\n吹っ飛ばすべよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1128008, /*unit_id*/112831, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"日頃の労働のおかげか\n戦いでも案外動けるもんだべな\nよーし、このまま一気に\n終わらせちまうべ！");
INSERT INTO `unit_comments` VALUES (/*id*/1128009, /*unit_id*/112831, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"本当は戦いなんて怖くて\n仕方ねぇべ。でも隣に\nあんちゃんがいてくれんなら\nオラはいくらでも強くなれんべ！");
INSERT INTO `unit_comments` VALUES (/*id*/1128010, /*unit_id*/112831, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あんま強くなると、嫁の貰い手が\nなくなっちまうな\nそ、そん時はあんちゃんに…\nや、やっぱり何でもねぇべ！");
INSERT INTO `unit_comments` VALUES (/*id*/1128011, /*unit_id*/112801, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/5.2, /*change_face*/2, /*description*/"お誕生日おめでとうだべ！\nケーキも料理も用意してっけど\nまずは搾りたてのミルクで\n乾杯だべさ！");
INSERT INTO `unit_comments` VALUES (/*id*/1128012, /*unit_id*/112831, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"よく来たなあんちゃん！\n今日は牧場を貸し切りで\nパーティーだべ！\nいっぱい楽しんでくんろ！");
INSERT INTO `unit_comments` VALUES (/*id*/1129001, /*unit_id*/112901, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/8.7, /*change_face*/6, /*description*/"この服を着てると、自分が\n物語の主人公になったみたいです\n馬子も大爆笑、ってやつですね！\n…え？　何か間違えてましたか？");
INSERT INTO `unit_comments` VALUES (/*id*/1129002, /*unit_id*/112901, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/9.5, /*change_face*/4, /*description*/"いいなぁ～、このお洋服…\n見れば見るほどよくできてる\nちょっと脱いで仕立ての確認を～\nってお兄ちゃん、見ちゃダメ～！");
INSERT INTO `unit_comments` VALUES (/*id*/1129003, /*unit_id*/112901, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/7.7, /*change_face*/6, /*description*/"おっとと、危ない危ない\nいつもみたいにぴょんぴょん\n動き回るとスカートがふわ～って\n…誰にも見られてませんよね？");
INSERT INTO `unit_comments` VALUES (/*id*/1129004, /*unit_id*/112901, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/5.6, /*change_face*/5, /*description*/"いつもと違うお洋服…\nお兄ちゃん、褒めてくれるかな？\nかわいいね、なんて言われたら\n私…ドキドキしちゃいますよ～！");
INSERT INTO `unit_comments` VALUES (/*id*/1129005, /*unit_id*/112901, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/4.7, /*change_face*/2, /*description*/"ちょっと動かないでくださいね～\n何してるのって？　えへへ、\nお兄ちゃんのサイズを測って\nおそろいの衣装を作るんですよ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1129006, /*unit_id*/112931, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私、物語のヒロインみたいです♪\nもちろんヒーローはお兄ちゃん！\n私がピンチの時は、\nかっこよく助けてくださいね！");
INSERT INTO `unit_comments` VALUES (/*id*/1129007, /*unit_id*/112931, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"いつもお兄ちゃんの後ろに隠れて\nばかりだった私はもういません！\nこれからはお兄ちゃんの隣で…\n一緒に、歩いていきますからね♪");
INSERT INTO `unit_comments` VALUES (/*id*/1129008, /*unit_id*/112931, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お兄ちゃん見てくださいよ！\nこの服、とってもかわいいです！\nえ？　そうだね、って…\nて、照れちゃいますよ…えへへ");
INSERT INTO `unit_comments` VALUES (/*id*/1129009, /*unit_id*/112931, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この服のおかげなのか、\nとびきり調子がいい気がします！\nバッチリ狙いをつけますよ～\n目標はお兄ちゃんのハートです♪");
INSERT INTO `unit_comments` VALUES (/*id*/1129010, /*unit_id*/112931, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"こ～んなかわいい格好で\nアプローチしたら、お兄ちゃんも\nドキドキしますかね…よぉしっ！\n勇気を出してっ、お兄ちゃ～ん♪");
INSERT INTO `unit_comments` VALUES (/*id*/1129011, /*unit_id*/112901, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/4.9, /*change_face*/2, /*description*/"今日はお兄ちゃんの誕生日！\nおめでとうございます！\nえへへ、またお兄ちゃんが\n格好よくなっちゃいましたね…♪");
INSERT INTO `unit_comments` VALUES (/*id*/1129012, /*unit_id*/112931, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハッピーバースデーお兄ちゃん！\nお誕生日ケーキもバッチリです！\nあとは…えっと、その…うんっ！\n私が食べさせてあげちゃいます！");
INSERT INTO `unit_comments` VALUES (/*id*/1130001, /*unit_id*/113001, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/3.7, /*change_face*/5, /*description*/"この服…\n本物のウサギさんみたいで、\n私にはかわいすぎだよぉ…");
INSERT INTO `unit_comments` VALUES (/*id*/1130002, /*unit_id*/113001, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/5.0, /*change_face*/6, /*description*/"…ウ、ウサギさんだぴょん♪\nなんちゃって…え？\nせ、先輩、いつからそこに…\n今のは忘れてくださいぃぃ…！");
INSERT INTO `unit_comments` VALUES (/*id*/1130003, /*unit_id*/113001, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/9.3, /*change_face*/5, /*description*/"ウ、ウサギさんは幸運の象徴\nですので、私が先輩のそばに\nずっと居れば…その…や、\nやっぱりなんでもないですぅ！");
INSERT INTO `unit_comments` VALUES (/*id*/1130004, /*unit_id*/113001, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/4.1, /*change_face*/2, /*description*/"なんだかいつもより体が\n軽いような…これも、\nアリスちゃんの力が宿った、\nこの服のおかげかな♪");
INSERT INTO `unit_comments` VALUES (/*id*/1130005, /*unit_id*/113001, /*use_type*/1, /*voice_id*/5, /*face_id*/3, /*change_time*/11.7, /*change_face*/5, /*description*/"アリスちゃんの力で強くなった\n今の私なら、先輩を誘えるかも！\nせ、先輩！　一緒にご飯でも…\nや、やっぱりまだ無理ぃぃ！");
INSERT INTO `unit_comments` VALUES (/*id*/1130006, /*unit_id*/113031, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ま、魔物と戦うのは怖いけど、\n私だって、\nアリスちゃんの力を受けた一人！\nやるときはやるんですぅ！");
INSERT INTO `unit_comments` VALUES (/*id*/1130007, /*unit_id*/113031, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"体が軽い…まるで、本物の\nウサギさんになったみたい…\nえへへ、今の私なら、\n先輩に褒めてもらえるかも♪");
INSERT INTO `unit_comments` VALUES (/*id*/1130008, /*unit_id*/113031, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"せ、先輩には\n指一本触れさせません！\n私が守りまひゅ…！？\nうう…またかんじゃったよぉ…");
INSERT INTO `unit_comments` VALUES (/*id*/1130009, /*unit_id*/113031, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"せ、先輩たちに\n頼ってばっかりじゃダメ！\n私だって頑張らないと！\nファイトよアユミぃぃぃ！");
INSERT INTO `unit_comments` VALUES (/*id*/1130010, /*unit_id*/113031, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"時間を操作する力…\nこの力で、ずっと先輩を後ろから\n見て…じゃ、じゃなくてぇ！\nみんなの役に立ってみせますぅ！");
INSERT INTO `unit_comments` VALUES (/*id*/1130011, /*unit_id*/113001, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/8.0, /*change_face*/4, /*description*/"せ、先輩！！\nえっと…その、お、お誕生日、\nおめでとうごじゃいまひゅ！\nうう、またかんじゃったよぉ…");
INSERT INTO `unit_comments` VALUES (/*id*/1130012, /*unit_id*/113031, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうございます！\nこれ、プレゼントですぅ！\n…つ、ついに、\nかまないでちゃんと言えました♪");
INSERT INTO `unit_comments` VALUES (/*id*/1131001, /*unit_id*/113101, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/9.2, /*change_face*/2, /*description*/"風が通り抜けて風鈴が鳴ると\nああ、今年も夏が来たなぁ…\nって感じたりするじゃないか\nあの空気が、好きなんだよね");
INSERT INTO `unit_comments` VALUES (/*id*/1131002, /*unit_id*/113101, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/5.6, /*change_face*/5, /*description*/"こういうヒラヒラした格好は\nあんまりしないんだよねぇ…\nいつもと勝手が違うから\nちょっと気恥ずかしいね");
INSERT INTO `unit_comments` VALUES (/*id*/1131003, /*unit_id*/113101, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/5.9, /*change_face*/2, /*description*/"お前さん、スイカを食べるとき\n何かをかけたりする派かい？\nアタシはそのまま食べるのが\n好きなんだよね");
INSERT INTO `unit_comments` VALUES (/*id*/1131004, /*unit_id*/113101, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/3.7, /*change_face*/2, /*description*/"お前さん、夏の支度は万全かい？\nアタシはこの通りさ\n一緒にバカンスってやつを\n楽しむとしようか");
INSERT INTO `unit_comments` VALUES (/*id*/1131005, /*unit_id*/113101, /*use_type*/1, /*voice_id*/5, /*face_id*/4, /*change_time*/4.2, /*change_face*/1, /*description*/"こう暑いと、かき氷が食べたいね\nお前さんも一緒にどうだい？\n色んな種類があるみたいだし\n別々の味を買おうじゃないか");
INSERT INTO `unit_comments` VALUES (/*id*/1131006, /*unit_id*/113131, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"潮風を感じたり波音を聞いたり…\n普段は忙しくて、こんな時間\n取れたりしないからね\n夏を満喫しようじゃないか");
INSERT INTO `unit_comments` VALUES (/*id*/1131007, /*unit_id*/113131, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"もし急ぎの用事がないんなら\nここでゆっくりしていきなよ\n何をするわけでもないけど\nたまにはこういうのも、ね");
INSERT INTO `unit_comments` VALUES (/*id*/1131008, /*unit_id*/113131, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"おや、お前さん。散歩かい？\nアタシはのんびりしてたとこだよ\nきれいな海を眺めながら\nゆっくりするのは一番の贅沢さ");
INSERT INTO `unit_comments` VALUES (/*id*/1131009, /*unit_id*/113131, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お前さんと一緒にいるとさ\nなんていうか安心できるんだよね\n自然体の自分になれるというか\n肩肘を張らなくていいんだ");
INSERT INTO `unit_comments` VALUES (/*id*/1131010, /*unit_id*/113131, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"これからもお前さんと\n一緒に居られたらうれしいよ\n…ふふ、なんだいその顔は\n相変わらず読めないね、まったく");
INSERT INTO `unit_comments` VALUES (/*id*/1131011, /*unit_id*/113101, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/7.7, /*change_face*/2, /*description*/"誕生日おめでとう\nあんまり大したものじゃないけど\nお祝いの品を用意しといたよ\nほら、受け取っておくれ");
INSERT INTO `unit_comments` VALUES (/*id*/1131012, /*unit_id*/113131, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日はお前さんの誕生日だよね\nお祝いとして、アタシの手料理を\nたくさん用意しておいたんだよ\nさ、向こうで一緒に食べようか");
INSERT INTO `unit_comments` VALUES (/*id*/1132001, /*unit_id*/113201, /*use_type*/1, /*voice_id*/1, /*face_id*/4, /*change_time*/5.4, /*change_face*/3, /*description*/"我が真名を勝手に晒しているとは\nなんという装束なのだ…\nこ、こら、シグルドよ！\nあまり胸元を見るんじゃない！");
INSERT INTO `unit_comments` VALUES (/*id*/1132002, /*unit_id*/113201, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"はーっはっはっは！\n我ら盟友の絆、この海でも\n変わることはないぞ！");
INSERT INTO `unit_comments` VALUES (/*id*/1132003, /*unit_id*/113201, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/2.4, /*change_face*/1, /*description*/"マフラーをしている理由？\nふっ、カッコいいからに\n決まっているではないか！\n他の理由などあるわけがない！");
INSERT INTO `unit_comments` VALUES (/*id*/1132004, /*unit_id*/113201, /*use_type*/1, /*voice_id*/4, /*face_id*/4, /*change_time*/13.1, /*change_face*/3, /*description*/"な、なぁ、シグルドよ\n貴様は私の水着を見てどう思う？\n…似合ってる？　いや、もっとこう\nかわいいとか…な、なんでもない");
INSERT INTO `unit_comments` VALUES (/*id*/1132005, /*unit_id*/113201, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/2.3, /*change_face*/1, /*description*/"日焼け止めはちゃんと塗ったか？\nもしまだ塗っていないなら\n私が貴様に塗ってやろう！");
INSERT INTO `unit_comments` VALUES (/*id*/1132006, /*unit_id*/113231, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"シグルドよ、何をしているのだ？\n今こそ日光の力を集める機会\n我らの力を高めるためにも\nバカンスを満喫するのだ～っ！");
INSERT INTO `unit_comments` VALUES (/*id*/1132007, /*unit_id*/113231, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ふふふ。海に来たことで私は\n新たな力を得ることができた！\nこの強さを目に焼き付けるがいい\n行くぞ！　アターック！！");
INSERT INTO `unit_comments` VALUES (/*id*/1132008, /*unit_id*/113231, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"暑さが辛くないのか、だと？\nふっ。私を誰だと思っている…\n我が闇の力をもってすれば\n夏の日差しなど敵ではない！");
INSERT INTO `unit_comments` VALUES (/*id*/1132009, /*unit_id*/113231, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"最初は、この水着も\n恥ずかしいと思っていたのだが…\n貴様との絆の証だからな\n別の水着など考えられんな！");
INSERT INTO `unit_comments` VALUES (/*id*/1132010, /*unit_id*/113231, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"シグルド、貴様と過ごす夏は\n最高に愉快だ！\n来年もまた来よう！\nその次もまた一緒だ、いいな！");
INSERT INTO `unit_comments` VALUES (/*id*/1132011, /*unit_id*/113201, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/5.8, /*change_face*/1, /*description*/"誕生日、おめでとう！\n今日は私から闇の祝福を与えよう\nこの力があれば貴様には様々な\n幸運が訪れる。感謝するがよい！");
INSERT INTO `unit_comments` VALUES (/*id*/1132012, /*unit_id*/113231, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日は貴様の誕生日か…\nよし、これをプレゼントしよう！\n私が考えた貴様の名乗り口上だ\nさぁ、今から練習をしてみるぞ！");
INSERT INTO `unit_comments` VALUES (/*id*/1133001, /*unit_id*/113301, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/6.4, /*change_face*/2, /*description*/"どうどう？　この水着！\nいつもの魔法少女とは違う\n新たなナナカちゃんの魅力、\n君に教えてあげようじゃないか！");
INSERT INTO `unit_comments` VALUES (/*id*/1133002, /*unit_id*/113301, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/3.1, /*change_face*/2, /*description*/"こういうダンサー風の\n魔法少女もいいでしょ？\nぐふふ、妄想が捗りますな～♪");
INSERT INTO `unit_comments` VALUES (/*id*/1133003, /*unit_id*/113301, /*use_type*/1, /*voice_id*/3, /*face_id*/3, /*change_time*/6.0, /*change_face*/1, /*description*/"ミステリアスな水着美少女\n彼女の正体は、敵か味方か…？\nみたいな登場しそうじゃない！？\n物語中盤で仲間になる系だよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1133004, /*unit_id*/113301, /*use_type*/1, /*voice_id*/4, /*face_id*/5, /*change_time*/6.2, /*change_face*/6, /*description*/"この格好、君もちょっとくらい\nドキッとしちゃうんじゃな～い？\n…ここまでアピールしてるのに\n顔が普通なの、逆にすごいわ");
INSERT INTO `unit_comments` VALUES (/*id*/1133005, /*unit_id*/113301, /*use_type*/1, /*voice_id*/5, /*face_id*/4, /*change_time*/8.0, /*change_face*/5, /*description*/"も～、ちょっとはナナカちゃんを\n意識したっていいのにな～\nえ？　どうしてなのかって…\nさぁ、どうしてだろうね～♪");
INSERT INTO `unit_comments` VALUES (/*id*/1133006, /*unit_id*/113331, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"え！？　そのビンなにって…\nここ、これは君との夏の思い出を\n海に流してみようかなって…\nなにを書いたのかは絶対に秘密！");
INSERT INTO `unit_comments` VALUES (/*id*/1133007, /*unit_id*/113331, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"もし、無人島に流されちゃっても\n君と一緒だったら\n助けなんか来なくてもいいな…\nって、ナシナシ！　今のナシ！");
INSERT INTO `unit_comments` VALUES (/*id*/1133008, /*unit_id*/113331, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"一度しか言わないよ？\n…ナナカちゃんの水着姿を\nじっくり見ていいのは\n君だけだからね…？");
INSERT INTO `unit_comments` VALUES (/*id*/1133009, /*unit_id*/113331, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ちょ、ちょっと～、そんな真顔に\nなられるとさ…えと、その…\nドキっとしちゃうよ…");
INSERT INTO `unit_comments` VALUES (/*id*/1133010, /*unit_id*/113331, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ギャルゲーなら照れた反応も\n王道のリアクションだけど…\n君が相手だと、なんでだろ…\n上手にできないね…");
INSERT INTO `unit_comments` VALUES (/*id*/1133011, /*unit_id*/113301, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/6.3, /*change_face*/1, /*description*/"おたおめ～♪\nというわけで、君にはこの本を\nプレゼントしちゃいまーす！\n私のオススメ、選んでおいたぞ☆");
INSERT INTO `unit_comments` VALUES (/*id*/1133012, /*unit_id*/113331, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日おめでとーっ！\nさぁ、ナナカちゃんお手製の\n変身グッズをプレゼントだよん☆\nこれで君も魔法少女になるのだ！");
INSERT INTO `unit_comments` VALUES (/*id*/1134001, /*unit_id*/113401, /*use_type*/1, /*voice_id*/1, /*face_id*/7, /*change_time*/2.2, /*change_face*/1, /*description*/"ファイトファイト～♪\nキミが元気いっぱい\nがんばれるように\n私がいっぱい応援するよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1134002, /*unit_id*/113401, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/2.4, /*change_face*/1, /*description*/"今日もお疲れさま！\nいっぱいがんばった\n後は、ゆっくり休憩して\n疲れを取らなきゃだよね！");
INSERT INTO `unit_comments` VALUES (/*id*/1134003, /*unit_id*/113401, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/6.5, /*change_face*/6, /*description*/"動きやすいしかわいいし\nこの水着、と～っても\nいい感じだね！\nえ？　キミも着てみたいの？");
INSERT INTO `unit_comments` VALUES (/*id*/1134004, /*unit_id*/113401, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/5.1, /*change_face*/2, /*description*/"キミのこと、他の誰よりも\nおっきな声で応援するね！\nどんな時でも、ちゃんとキミに\n聞こえるように！");
INSERT INTO `unit_comments` VALUES (/*id*/1134005, /*unit_id*/113401, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/6.2, /*change_face*/5, /*description*/"全力でがんばってるキミを\n見てたら、なんだかドキドキ\nしちゃった…張り切って\n応援しすぎたからかな…？");
INSERT INTO `unit_comments` VALUES (/*id*/1134006, /*unit_id*/113431, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"きらーん☆\nこれがハツネちゃんの本気だよ！\nキミが元気にがんばれるように\n私もいっぱい応援するからね！");
INSERT INTO `unit_comments` VALUES (/*id*/1134007, /*unit_id*/113431, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"潮風を感じながら\n体を動かすのって、\nすっごく気持ちいいね！\nキミも一緒に、やってみようよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1134008, /*unit_id*/113431, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"超能力を使っちゃったから、\nちょっと眠くなってきちゃった…\nけど…がんばるキミのために…！\n私もがんばるよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1134009, /*unit_id*/113431, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"諦めないでがんばるキミって\nすっごくかっこいいかも…\nあぁ～っ、ごめんなんでもない！\nひとりごとだから気にしないで！");
INSERT INTO `unit_comments` VALUES (/*id*/1134010, /*unit_id*/113431, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"キミががんばり続ける限り\n私もずっと応援してるよ！\nいつでもそばにいるからね♪");
INSERT INTO `unit_comments` VALUES (/*id*/1134011, /*unit_id*/113401, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/7.5, /*change_face*/7, /*description*/"今日ってキミのお誕生日だよね！\nえへへ、忘れるわけないよ～！\nパーティの準備もばっちりだし、\nいっぱい楽しく過ごそうね！");
INSERT INTO `unit_comments` VALUES (/*id*/1134012, /*unit_id*/113431, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハッピーバースデー☆\nお祝いに、今日は朝から晩まで、\nキミをずっと応援するのだ～！");
INSERT INTO `unit_comments` VALUES (/*id*/1135001, /*unit_id*/113501, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/3.8, /*change_face*/6, /*description*/"よしよし。今日も頑張ったわね♪\nあら、顔が真っ赤ね？\nいつもより恥ずかしい？\nん～？　どうしてかしら…？");
INSERT INTO `unit_comments` VALUES (/*id*/1135002, /*unit_id*/113501, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/9.9, /*change_face*/2, /*description*/"水分補給はちゃんとしてる？\n海はとっても楽しいけれど\n体調には気をつけないとね\nはい、先生特製のドリンクよ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1135003, /*unit_id*/113501, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/8.9, /*change_face*/6, /*description*/"さ、次はお洗濯しなきゃ…\nえ？　手伝ってくれるの？\nありがとう、助かるわ♪\nあっ！　そっちの洗濯物は私の…");
INSERT INTO `unit_comments` VALUES (/*id*/1135004, /*unit_id*/113501, /*use_type*/1, /*voice_id*/4, /*face_id*/5, /*change_time*/11.6, /*change_face*/2, /*description*/"あなたの体、引き締まってるわね\n男の子って感じで格好いいわ♪\nもう子ども扱いできないかも…\nなんて、ね？");
INSERT INTO `unit_comments` VALUES (/*id*/1135005, /*unit_id*/113501, /*use_type*/1, /*voice_id*/5, /*face_id*/5, /*change_time*/8.3, /*change_face*/2, /*description*/"二人で波打ち際を歩いてみない？\n…ロマンチックな雰囲気を\n味わってみたくて…\nふふ、子どもっぽかったかしら？");
INSERT INTO `unit_comments` VALUES (/*id*/1135006, /*unit_id*/113531, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"みんなー、ファイトよーっ！\nふふ、先生の応援どうかしら？\n元気になったのなら、うれしいわ\nさぁ、もうひと頑張りしましょ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1135007, /*unit_id*/113531, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"夏の海で体を動かすのって\nとても気持ちがいいのね\n応援だけじゃなくて、先生も\n一緒に参加しちゃおうかしら？");
INSERT INTO `unit_comments` VALUES (/*id*/1135008, /*unit_id*/113531, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"水着なんて森では着ないから\nちょっとだけ恥ずかしいわ\nやっぱり肌を出し過ぎてる気が…\n…もう、あまり見つめちゃダメよ");
INSERT INTO `unit_comments` VALUES (/*id*/1135009, /*unit_id*/113531, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ね、海の楽しみ方…\n先生に教えてもらえないかしら？\nできれば、あなたと一緒に\n夏を満喫したいなって…ふふ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1135010, /*unit_id*/113531, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"うふふ、青春っていいわね～\nあら、先生も青春しようって？\nそうね、あなたとなら…\n甘酸っぱい青春、できそうね♪");
INSERT INTO `unit_comments` VALUES (/*id*/1135011, /*unit_id*/113501, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/8.7, /*change_face*/2, /*description*/"なんだかソワソワしてるわね？\nそれもそうよね、今日はあなたの\nお誕生日だもの。先生と一緒に\nいっぱいお祝いしちゃいましょ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1135012, /*unit_id*/113531, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日はあなたのお誕生日ね！\nずっと頑張ってきたいい子には\n先生から特別なお祝いよ♪\nほら、いい子いい子…♪");
INSERT INTO `unit_comments` VALUES (/*id*/1136001, /*unit_id*/113601, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/1.9, /*change_face*/5, /*description*/"どうしたんだい？\n…そんなにじっくりと見られると\n恥ずかしいじゃないか…");
INSERT INTO `unit_comments` VALUES (/*id*/1136002, /*unit_id*/113601, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/5.7, /*change_face*/2, /*description*/"直接日差しを浴びるのは\n鎧越しとは違った暑さだね…\nこれはこれでいいものだよ");
INSERT INTO `unit_comments` VALUES (/*id*/1136003, /*unit_id*/113601, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/3.8, /*change_face*/2, /*description*/"思い切り身体を動かせるのも\nいいものだね…\nこれなら存分に泳げるよ");
INSERT INTO `unit_comments` VALUES (/*id*/1136004, /*unit_id*/113601, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/3.0, /*change_face*/2, /*description*/"いつもより無防備な格好だが…\n君がそばにいてくれるおかげで\n心強いよ");
INSERT INTO `unit_comments` VALUES (/*id*/1136005, /*unit_id*/113601, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/1.7, /*change_face*/2, /*description*/"こうしていると\nいつもより君を近くに\n感じられる気がするよ");
INSERT INTO `unit_comments` VALUES (/*id*/1136006, /*unit_id*/113631, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"どうしたんだい少年\nそんなにじっと見て…\nみ、見ないでくれとは言わないが\nいつもとは違った恥ずかしさだよ");
INSERT INTO `unit_comments` VALUES (/*id*/1136007, /*unit_id*/113631, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"日差しが強くなってきたね\n熱中症になると危ないし…\nき、君さえよければだが、\n一緒にパラソルに入らないかい？");
INSERT INTO `unit_comments` VALUES (/*id*/1136008, /*unit_id*/113631, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"君の前でならありのままの\n自分でいられる気がするよ\n…どうしてだろうね？");
INSERT INTO `unit_comments` VALUES (/*id*/1136009, /*unit_id*/113631, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"もう少しゆっくりしていこうか\nたまにはこういうのもいいだろう\nここには君と私しか\nいないのだから…");
INSERT INTO `unit_comments` VALUES (/*id*/1136010, /*unit_id*/113631, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"夏は人を大胆にする、なんて\n信じてはいなかったのだけれど…\n今なら分かる気がするよ");
INSERT INTO `unit_comments` VALUES (/*id*/1136011, /*unit_id*/113601, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/7.1, /*change_face*/2, /*description*/"今日は君の誕生日だったね\nこの格好では大した祝福も\nできないが…祝えてうれしいよ\n誕生日おめでとう");
INSERT INTO `unit_comments` VALUES (/*id*/1136012, /*unit_id*/113631, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日おめでとう、少年\n…そんなにじっと顔を見られると\nどうにも照れてしまうが…\n君が望むなら、受け入れるさ");
INSERT INTO `unit_comments` VALUES (/*id*/1137001, /*unit_id*/113701, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/10.6, /*change_face*/5, /*description*/"お兄ちゃん、見てください！\nエンジェルの服、どうですか？\nアカリ…かわいいですか？\nやんっ、見つめすぎですよ～♪");
INSERT INTO `unit_comments` VALUES (/*id*/1137002, /*unit_id*/113701, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/6.5, /*change_face*/5, /*description*/"このたくさんのハートの飾り\nアカリの想いが詰まってるんです\nどんな想いか分かりますか？\nねぇ、お兄ちゃん…♪");
INSERT INTO `unit_comments` VALUES (/*id*/1137003, /*unit_id*/113701, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/4.5, /*change_face*/1, /*description*/"アカリ、エンジェルみたいに\nみんなを幸せにしたいです！\nお兄ちゃん、特訓してください！\nビシバシ、しごいてくださいね！");
INSERT INTO `unit_comments` VALUES (/*id*/1137004, /*unit_id*/113701, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/7.2, /*change_face*/1, /*description*/"エンジェルの力で、サックスが\n上手になったりは…しませんよね\nううん、自分で努力してこそです\nアカリ…もっと頑張ります！");
INSERT INTO `unit_comments` VALUES (/*id*/1137005, /*unit_id*/113701, /*use_type*/1, /*voice_id*/5, /*face_id*/4, /*change_time*/6.1, /*change_face*/5, /*description*/"お兄ちゃん、アカリなんだか\nドキドキしてます…\nお兄ちゃんの愛の力を\n感じてるから、ですかね♪");
INSERT INTO `unit_comments` VALUES (/*id*/1137006, /*unit_id*/113731, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お兄ちゃん、アカリの愛の力\n受け止めてくださいね！\nアカリしか見えなくなるくらい\nメロメロにしちゃいますからっ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1137007, /*unit_id*/113731, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"フワフワな羽が気になりますか？\nどうぞ…触ってもいいですよ\nでも、優しくしてくださいね…？\n敏感なんですから…♪");
INSERT INTO `unit_comments` VALUES (/*id*/1137008, /*unit_id*/113731, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この格好、お姉ちゃんとお揃いで\nとってもかわいいですよね？\nアカリ、お気に入りなんです\nお姉ちゃんは照れてますけど…♪");
INSERT INTO `unit_comments` VALUES (/*id*/1137009, /*unit_id*/113731, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お兄ちゃん、元気ないんですか？\nだったらアカリに任せてください\n頭をナデナデしてあげますね…\nよしよし、元気になぁれ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1137010, /*unit_id*/113731, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"えへへ、アカリの大切な想い\nお兄ちゃんに届きましたか？\nそれとも、もう一回…しますか？\nもっとすごいのあげちゃいます♪");
INSERT INTO `unit_comments` VALUES (/*id*/1137011, /*unit_id*/113701, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/4.2, /*change_face*/5, /*description*/"お兄ちゃん、お誕生日\nおめでとうございますっ！\nなんだかアカリもうれしくて\nウズウズしてきちゃいます♪");
INSERT INTO `unit_comments` VALUES (/*id*/1137012, /*unit_id*/113731, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お兄ちゃん、お誕生日の\nお祝いしちゃいますよ！\nプレゼントはア・カ・リ♪\nな～んて、ドキドキしましたか？");
INSERT INTO `unit_comments` VALUES (/*id*/1138001, /*unit_id*/113801, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/5.5, /*change_face*/1, /*description*/"さて、今日もエンジェルとして\n困っている人を助けに行かないと\nもちろん、アンタにも\n付き合ってもらうわよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1138002, /*unit_id*/113801, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/2.5, /*change_face*/2, /*description*/"エンジェルらしさ…\nあなたの願いを\nかなえて差し上げます☆\nこ、こんな感じかな…？");
INSERT INTO `unit_comments` VALUES (/*id*/1138003, /*unit_id*/113801, /*use_type*/1, /*voice_id*/3, /*face_id*/4, /*change_time*/6.4, /*change_face*/6, /*description*/"うう…やっぱりこういう格好は、\n私には似合わな…え？\nか、かわいいって…ば、ばかぁ！\nそういう恥ずかしいこと言うな！");
INSERT INTO `unit_comments` VALUES (/*id*/1138004, /*unit_id*/113801, /*use_type*/1, /*voice_id*/4, /*face_id*/4, /*change_time*/8.2, /*change_face*/6, /*description*/"困ってる人に話しかける…\n人見知りの私には難しいかも\nま、まあ、アンタも\n手伝ってくれるし、頑張るけどね");
INSERT INTO `unit_comments` VALUES (/*id*/1138005, /*unit_id*/113801, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/4.6, /*change_face*/1, /*description*/"エンジェルとして困ってる人を\n救うかぁ…これがきっかけで、\nちゃんと人と\n話せるようになれるといいな");
INSERT INTO `unit_comments` VALUES (/*id*/1138006, /*unit_id*/113831, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"こ、こういうかわいいのって\nアカリの得意分野なのに…\nでも、今なら私だって…\nし、しっかり見てなさいよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1138007, /*unit_id*/113831, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"手の位置とかこのへんで\n姿勢ももう少しこう…\nな、何よ、やると決めたからには\n完璧を目指すわよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1138008, /*unit_id*/113831, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"何か子供たちがこのポーズの\nまねしてくれてるのよね\nちょっと気恥ずかしいけど\n悪くない気分ね");
INSERT INTO `unit_comments` VALUES (/*id*/1138009, /*unit_id*/113831, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"みんなの役に立つのって\n想像以上に大変なことだわ\nアンタはそれをずっとしてたのね\n…ちょっと見直したわ");
INSERT INTO `unit_comments` VALUES (/*id*/1138010, /*unit_id*/113831, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ア、アンタにはいつも助けられて\nばかりだから、アンタだけに\n特別なポーズ、見せてあげるわ\nこ、光栄に思いなさい！");
INSERT INTO `unit_comments` VALUES (/*id*/1138011, /*unit_id*/113801, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/8.0, /*change_face*/6, /*description*/"ア、アンタの誕生日とかどうでも\nいいんだけど、一応お祝いは\nしてあげるわ\nエ、エンジェルとしてね！？");
INSERT INTO `unit_comments` VALUES (/*id*/1138012, /*unit_id*/113831, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"た、誕生日おめでとう。今日は\nたまたまエンジェルの仕事が\n休みだから、私が祝ってあげるわ\nか、感謝しなさいよね！");
INSERT INTO `unit_comments` VALUES (/*id*/1139001, /*unit_id*/113901, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/5.7, /*change_face*/3, /*description*/"騎士さん、ハロウィンですね…\n少し付き合ってほしいんですけど\nかっ、勘違いしないでください！\n仕事の手伝いですよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1139002, /*unit_id*/113901, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/6.7, /*change_face*/1, /*description*/"なんですか、ジロジロ見て…\nもしかして騎士さんも仮装\nしたいんですか？　なら…\n仕方ないので作ってあげますよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1139003, /*unit_id*/113901, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/8.4, /*change_face*/2, /*description*/"それっ！　えーいっ！\n何してるのって、糸で縛る練習に\n決まってるじゃないですか\n標的はもちろん…分かりますね？");
INSERT INTO `unit_comments` VALUES (/*id*/1139004, /*unit_id*/113901, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/9.1, /*change_face*/5, /*description*/"ハロウィンと言えば仮装ですよね\nどうですか、私の衣装は？\n力作なのでどうぞ褒めてください\nかわいい？　…で、でしょうっ？");
INSERT INTO `unit_comments` VALUES (/*id*/1139005, /*unit_id*/113901, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/3.4, /*change_face*/1, /*description*/"よし、お菓子の準備はオーケー♪\n騎士さんのことだから、お菓子を\n欲しがるに違いありませんからね\n手作りだけど…それは秘密です！");
INSERT INTO `unit_comments` VALUES (/*id*/1139006, /*unit_id*/113931, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"えっと、騎士さん\nどうせ暇ですよね？\nなら私と一緒にハロウィンを…\nもうっ、いいから行きますよっ！");
INSERT INTO `unit_comments` VALUES (/*id*/1139007, /*unit_id*/113931, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ふふ、きれいでしょこの飾り付け♪\nん？　どこを見てるんです？\n…わ、私じゃなくて飾り付けを\n見てください！　もうっ！");
INSERT INTO `unit_comments` VALUES (/*id*/1139008, /*unit_id*/113931, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"騎士さん、覚悟してください！\n今の私はこわ～い吸血鬼です！\nガブッと噛みついて、えっと…\nやっぱいいです！　今のナシで！");
INSERT INTO `unit_comments` VALUES (/*id*/1139009, /*unit_id*/113931, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"色んな仮装を見てると\n自分でも作りたくなってきますね\n騎士さんはどんなのが好きです？\n…参考に聞いてるだけですからね");
INSERT INTO `unit_comments` VALUES (/*id*/1139010, /*unit_id*/113931, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"騎士さんと二人でハロウィン…\n周りからどう見えるんだろう…？\nまぁ、どうだっていいですけど！\n私は別に、気にしませんよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1139011, /*unit_id*/113901, /*use_type*/2, /*voice_id*/6, /*face_id*/6, /*change_time*/9.1, /*change_face*/5, /*description*/"…ついさっき思い出しましたけど\n今日は騎士さんの誕生日でしたね\nはいこれ、誕生日プレゼントです\nあっ…これは今買ったんですよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1139012, /*unit_id*/113931, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"えっと、騎士さん\n誕生日おめでとうございます！\n吸血鬼からの誕生日プレゼント\n存分にありがたがってください！");
INSERT INTO `unit_comments` VALUES (/*id*/1140001, /*unit_id*/114001, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/6.2, /*change_face*/2, /*description*/"ト、トリックオアトリート…\n実はこういうことには不慣れでね\nよければ、キミが私の\n師匠になってくれるかな");
INSERT INTO `unit_comments` VALUES (/*id*/1140002, /*unit_id*/114001, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/8.8, /*change_face*/2, /*description*/"子供から大人までみんな\nハロウィンを楽しんでいるね…\n私はちゃんと馴染めているかな？\nばっちり？　ならよかった");
INSERT INTO `unit_comments` VALUES (/*id*/1140003, /*unit_id*/114001, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/9.7, /*change_face*/4, /*description*/"ハロウィンのお菓子のやりとり…\nええと、ハッピーハロウィン…\nお菓子をどうぞ…って\nうまくできてる？");
INSERT INTO `unit_comments` VALUES (/*id*/1140004, /*unit_id*/114001, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/5.6, /*change_face*/2, /*description*/"賑やかなハロウィンの街並み…\n眺めているだけで\n楽しいものだね\nさあ、私たちも出かけよう");
INSERT INTO `unit_comments` VALUES (/*id*/1140005, /*unit_id*/114001, /*use_type*/1, /*voice_id*/5, /*face_id*/5, /*change_time*/5.3, /*change_face*/2, /*description*/"キミと一緒に\nハロウィンを過ごすのは\n気恥ずかしいんだけど…\nうれしいよ");
INSERT INTO `unit_comments` VALUES (/*id*/1140006, /*unit_id*/114031, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"トリックオアトリート\nお菓子をくれないと…\nとり憑いてしまうよ？\nふふ、なんてね");
INSERT INTO `unit_comments` VALUES (/*id*/1140007, /*unit_id*/114031, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"さあ、手を出して？\n幽霊たちと一緒に\nハロウィンのダンスを踊ろうよ");
INSERT INTO `unit_comments` VALUES (/*id*/1140008, /*unit_id*/114031, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ツムギが作ってくれたこの衣装\nとても素敵だね…ふふ、\n幽霊たちも、似合ってるって\n言ってくれているのかな？");
INSERT INTO `unit_comments` VALUES (/*id*/1140009, /*unit_id*/114031, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"キミ、休んでいる暇はないよ？\nハロウィンを\n遊び尽くさなくちゃね\nほら、一緒に行こう！");
