INSERT INTO `unit_comments` VALUES (/*id*/1140011, /*unit_id*/114001, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/5.6, /*change_face*/2, /*description*/"誕生日おめでとう。キミの成長が\n感じられて私もうれしいよ\n来年のキミが今から楽しみだ");
