INSERT INTO `unit_comments` VALUES (/*id*/1141001, /*unit_id*/114101, /*use_type*/1, /*voice_id*/1, /*face_id*/3, /*change_time*/2.6, /*change_face*/2, /*description*/"ホワイトタイガー出動ッス！\nさあ、今日も平和を守るため\n一緒にパトロールにいくッスよ！");
