INSERT INTO `unit_comments` VALUES (/*id*/1141006, /*unit_id*/114131, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"どうッスか、この勇姿！\nどんな悪も\nホワイトタイガーからは\n逃げられないッスよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1141007, /*unit_id*/114131, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あんたと一緒につくった\nこのカッコイイキメポーズで、\n今日も平和を守るッスよ！");
