INSERT INTO `unit_comments` VALUES (/*id*/1141011, /*unit_id*/114101, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/7.8, /*change_face*/2, /*description*/"今日はあんたの誕生日ッスね！\nお祝いに今日のヒーローごっこの\nトラタイガー役を譲って…\nえ？　自分がやっていいッスか？");
INSERT INTO `unit_comments` VALUES (/*id*/1141012, /*unit_id*/114131, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうッス！\n今日のトラタイガーごっこは、\n自分とあんたで\nダブルタイガーッスよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1142001, /*unit_id*/114201, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/6.8, /*change_face*/2, /*description*/"魔法少女と提督\nそれらが一つになった私こそ\n愛と希望の戦士\n魔法提督ラブリー★モニカだ！");
INSERT INTO `unit_comments` VALUES (/*id*/1142002, /*unit_id*/114201, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/4.2, /*change_face*/1, /*description*/"この格好にもすっかり\n慣れてしまったな\n子どもっぽいかと思っていたが\nこういうのも案外…悪くない");
INSERT INTO `unit_comments` VALUES (/*id*/1142003, /*unit_id*/114201, /*use_type*/1, /*voice_id*/3, /*face_id*/4, /*change_time*/5.6, /*change_face*/1, /*description*/"ずっと剣を扱ってきたからか\n魔法にはまだ慣れないな…\nしかし剣先から光線が出るのは\nなぜかワクワクするな…！");
INSERT INTO `unit_comments` VALUES (/*id*/1142004, /*unit_id*/114201, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/3.5, /*change_face*/5, /*description*/"輝け、愛の力！\nちょっとかわいすぎるか？\n…今からでも全砲門、発射！\nとかにした方がいいだろうか…");
INSERT INTO `unit_comments` VALUES (/*id*/1142005, /*unit_id*/114201, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"困ったことがあれば\nなんでも私に言ってくれ\n魔法提督は困っている者を\n決して見捨てないのだ！");
INSERT INTO `unit_comments` VALUES (/*id*/1142006, /*unit_id*/114231, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ラブリーキュアリーブレイブリー\nマジカルレベルインフィニット！\n魔法提督ラブリー★モニカ\n空に代わって指揮を執る！");
INSERT INTO `unit_comments` VALUES (/*id*/1142007, /*unit_id*/114231, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"戦いの指揮は私に任せろ！\n愛と希望に満ちた戦略で\n敵を討ち果たして見せよう\n最後の決めポーズまで完璧だ！");
INSERT INTO `unit_comments` VALUES (/*id*/1142008, /*unit_id*/114231, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"魔法少女は子どもたちとの\n触れ合いも大切にせねばな！\n今日も一緒に提督ごっこだ！\nなんだ、貴公も一緒にやるか？");
INSERT INTO `unit_comments` VALUES (/*id*/1142009, /*unit_id*/114231, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"魔法提督の指揮を見せてやろう！\n貴公、今すぐ街中のお菓子を\n買い占めてくるのだ！\n…すまん、冗談だ");
INSERT INTO `unit_comments` VALUES (/*id*/1142010, /*unit_id*/114231, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"人々の平和を守るという使命\n不安がないかと言えばウソになる\nだが、貴公がいてくれるなら\n私はどんな苦難にも立ち向かえる");
INSERT INTO `unit_comments` VALUES (/*id*/1142011, /*unit_id*/114201, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/8.5, /*change_face*/6, /*description*/"貴公、今日は誕生日らしいな\nみなまで言うな、お祝いに\nお菓子を用意してある。決して\n私が食べたいからではないぞ？");
INSERT INTO `unit_comments` VALUES (/*id*/1142012, /*unit_id*/114231, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日おめでとう。貴公のほしい\nものを言うがいい…え、私と\n一緒に過ごしたい？　そ、そうか\n欲がないな、貴公は");
INSERT INTO `unit_comments` VALUES (/*id*/1143001, /*unit_id*/114301, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/3.7, /*change_face*/2, /*description*/"このキラキラした衣装、\nキュートな武器…\nえへへ、やっぱり\n魔法少女っていいなぁ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1143002, /*unit_id*/114301, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/3.7, /*change_face*/1, /*description*/"まさか、私が\n魔法少女になれるなんて！\n…おっと、このことは\nみんなに内緒にしないとね♪");
INSERT INTO `unit_comments` VALUES (/*id*/1143003, /*unit_id*/114301, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/4.6, /*change_face*/1, /*description*/"魔法少女はみんなに\n夢を与える存在…\nその名に恥じぬように、\n私も精進しないとね！");
INSERT INTO `unit_comments` VALUES (/*id*/1143004, /*unit_id*/114301, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/5.8, /*change_face*/2, /*description*/"シャイニー、\nフェザリー、グローリー！\nマジカルソード、シャープネス！\nよし、今のポーズは完璧だったね");
INSERT INTO `unit_comments` VALUES (/*id*/1143005, /*unit_id*/114301, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/10.1, /*change_face*/5, /*description*/"変身したら髪型まで変わるとはね\nさすがに長いから結んで…え？　\n似合ってるって…じゃ、じゃあ、\nこのままにしとこうかな…");
INSERT INTO `unit_comments` VALUES (/*id*/1143006, /*unit_id*/114331, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"シャイニーフェザリーグローリー\nマジカルソード、シャープネス！\n魔法剣士シャイニー★トモ！\n煌めく剣技で、悪を断つ！");
INSERT INTO `unit_comments` VALUES (/*id*/1143007, /*unit_id*/114331, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"魔法少女になったとしても、\nミクマ流剣術に死角はない\n…なんなら、\nその身でためしてみる？");
INSERT INTO `unit_comments` VALUES (/*id*/1143008, /*unit_id*/114331, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"半人前の私にどこまでできるか\n分からないけど、諦めることは\n絶対にしない。それが、\n私なりの魔法少女の在り方だよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1143009, /*unit_id*/114331, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私は魔法少女であると同時に、\nミクマ流の剣士…悪いけど、\n真剣勝負というならば、\n手加減はできないからね♪");
INSERT INTO `unit_comments` VALUES (/*id*/1143010, /*unit_id*/114331, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"変身できるのは、あなたのおかげ\nでもあるんだよね…だったら、\nこれからはず～っと一緒に居て\nもらわないと…な～んてね♪");
INSERT INTO `unit_comments` VALUES (/*id*/1143011, /*unit_id*/114301, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/6.4, /*change_face*/2, /*description*/"今日はあなたの誕生日だよね\nだったら、私がケーキを焼いて\nあげよっか？　こう見えて、\n料理には自信があるんだよ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1143012, /*unit_id*/114331, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日おめでとう！\n魔法少女の役目が\n終わったあとで、\n二人でお祝いしようね♪");
