INSERT INTO `unit_comments` VALUES (/*id*/1144002, /*unit_id*/114401, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/3.1, /*change_face*/1, /*description*/"買い出しなら私の出番ですわね\n料理にツリーにプレゼントに…\n価格交渉ならお手のものですわ！");
INSERT INTO `unit_comments` VALUES (/*id*/1144003, /*unit_id*/114401, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/2.8, /*change_face*/2, /*description*/"クリスマスには\nプレゼントが付き物！\nもちろん準備は万端ですわ！");
INSERT INTO `unit_comments` VALUES (/*id*/1144004, /*unit_id*/114401, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/5.0, /*change_face*/5, /*description*/"この衣装、新しく\n仕立てたのですがいかがですか？　\nえ、似合ってる…ほめて\nいただけてよかったですわ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1144005, /*unit_id*/114401, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/4.3, /*change_face*/2, /*description*/"あなたさまとクリスマスを共に\n過ごせてうれしいですわ！\nさあ、さっそくパーティーの\n準備をいたしましょう♪");
INSERT INTO `unit_comments` VALUES (/*id*/1144006, /*unit_id*/114431, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"さあ、楽しい聖夜の晩餐会を\n始めるといたしましょう！\nほら、あなたさまも\n席にお座りくださいな♪");
INSERT INTO `unit_comments` VALUES (/*id*/1144007, /*unit_id*/114431, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"おーっほっほっほ！　この私に\nかかれば、料理など造作も\nありませんわ！　さあ、遠慮せず\n召し上がってくださいませ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1144008, /*unit_id*/114431, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"料理とは面白いものですわね\nあなたさまには、別の手料理も\n召し上がっていただきたいですし\n今後も続けていきたいですわ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1144009, /*unit_id*/114431, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"せっかくのお料理ですもの\n温かいうちにいただきましょう？\nふふ、安心なさってくださいまし\nお代わりはたーんとありますわ！");
