INSERT INTO `unit_comments` VALUES (/*id*/1144011, /*unit_id*/114401, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/5.8, /*change_face*/1, /*description*/"今日はあなたさまの\n誕生日ですもの\nささやかに、とは言いません\n盛大にお祝いいたしましょう！");
INSERT INTO `unit_comments` VALUES (/*id*/1144012, /*unit_id*/114431, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日のお祝いに手料理を\nご用意してみましたわ！\n是非とも\n召し上がってくださいませ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1145001, /*unit_id*/114501, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/4.0, /*change_face*/1, /*description*/"お金をかけなくても素敵な\nクリスマスが過ごせるってこと\n見せてあげるわ！");
INSERT INTO `unit_comments` VALUES (/*id*/1145002, /*unit_id*/114501, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/6.8, /*change_face*/2, /*description*/"子どもってクリスマスが好きよね\nまあ、あたしも人のこと\n言えないけど…何だか\n楽しい気分になれるのよね");
INSERT INTO `unit_comments` VALUES (/*id*/1145003, /*unit_id*/114501, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/4.3, /*change_face*/2, /*description*/"子どもたちが笑顔で\nクリスマスを\n過ごせること\nそれが一番の幸せよ");
INSERT INTO `unit_comments` VALUES (/*id*/1145004, /*unit_id*/114501, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/3.4, /*change_face*/2, /*description*/"せっかくのクリスマスなのに\n暇そうね。仕方ない、少しだけ\n付き合ってあげるわ。何か\n温かい物でも飲みに行きましょ？");
INSERT INTO `unit_comments` VALUES (/*id*/1145005, /*unit_id*/114501, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/3.3, /*change_face*/5, /*description*/"え、ドレスが似合ってる？\nそ、そう…ありがと…\nあんたに褒めてもらうのって…\nうれしいわ");
INSERT INTO `unit_comments` VALUES (/*id*/1145006, /*unit_id*/114531, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"さあ、熱く楽しいクリスマスを\n始めるわよ！\n寒がってる暇なんて\nないんだからね♪");
INSERT INTO `unit_comments` VALUES (/*id*/1145007, /*unit_id*/114531, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"少しエレガントじゃないかも\nしれないけど、許してね？\nあたし、目いっぱいクリスマスを\n楽しんじゃうんだから♪");
INSERT INTO `unit_comments` VALUES (/*id*/1145008, /*unit_id*/114531, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あんたと一緒のクリスマス…\n結構いい感じね♪\n気の許せる相手と\n過ごせるのって…幸せよね");
INSERT INTO `unit_comments` VALUES (/*id*/1145009, /*unit_id*/114531, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"外にいるのに何だか暑いって\n思うのは、もしかして…\nあんたが隣にいるから\nなのかしら…？");
INSERT INTO `unit_comments` VALUES (/*id*/1145010, /*unit_id*/114531, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"高級レストランで豪華なディナー\nなんてのは柄じゃないし\nあんたがいてくれれば、それが…\nその…さ、最高のクリスマスよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1145011, /*unit_id*/114501, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/5.4, /*change_face*/5, /*description*/"ハッピーバースデー！\n子どもたちからあんたに\nプレゼントがあるみたい\nあたしも…一応用意してあるわ");
INSERT INTO `unit_comments` VALUES (/*id*/1145012, /*unit_id*/114531, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ええと…誕生日おめでとう\nこれから一緒にプレゼントを\n見に行かない？\nデ、デートじゃないわよ、もう！");
INSERT INTO `unit_comments` VALUES (/*id*/1146001, /*unit_id*/114601, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/2.1, /*change_face*/1, /*description*/"お姉さんに任せなさい！\nキミのクリスマスを\n最高のものにしてあげるわよ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1146002, /*unit_id*/114601, /*use_type*/1, /*voice_id*/2, /*face_id*/5, /*change_time*/2.8, /*change_face*/6, /*description*/"キミと二人のクリスマスかぁ…\nき、緊張なんかしてないわよ？\n私がリードしてあげるんだから！");
INSERT INTO `unit_comments` VALUES (/*id*/1146003, /*unit_id*/114601, /*use_type*/1, /*voice_id*/3, /*face_id*/4, /*change_time*/3.8, /*change_face*/5, /*description*/"ちょっと派手じゃないかしら\nこの格好…ま、まあ、\nキミが気に入ってくれてるなら\nいいんだけどね？");
INSERT INTO `unit_comments` VALUES (/*id*/1146004, /*unit_id*/114601, /*use_type*/1, /*voice_id*/4, /*face_id*/4, /*change_time*/5.7, /*change_face*/1, /*description*/"一人のクリスマスに\n慣れちゃってたから\nキミと過ごすクリスマス…\nちょっとドキドキしちゃうかも");
INSERT INTO `unit_comments` VALUES (/*id*/1146005, /*unit_id*/114601, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"メリークリスマース！\nえへへ、大丈夫よ～\n酔ってなんかないから～♪");
INSERT INTO `unit_comments` VALUES (/*id*/1146006, /*unit_id*/114631, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"さあ、最っ高の\nクリスマスが始まるわよ♪\nキミも早くおいで～！");
INSERT INTO `unit_comments` VALUES (/*id*/1146007, /*unit_id*/114631, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"キミも飲み物どうかしら？\n一緒に乾杯しましょ♪\nはーい、メリークリスマース！");
INSERT INTO `unit_comments` VALUES (/*id*/1146008, /*unit_id*/114631, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"おかわりはいかが？\n遠慮しなくてもいいわよ\nクリスマスパーティーなんだもの\n思いっきり楽しまなくちゃ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1146009, /*unit_id*/114631, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"どう？　楽しんでるかしら？\n…もしよかったらなんだけど\nこの後二人で…\nや、やっぱりなんでもないわ！");
INSERT INTO `unit_comments` VALUES (/*id*/1146010, /*unit_id*/114631, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"キミと過ごすクリスマス…\nとっても素敵ね\nこんな幸せな時間が\nずっと続けばいいのに…");
INSERT INTO `unit_comments` VALUES (/*id*/1146011, /*unit_id*/114601, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/4.8, /*change_face*/2, /*description*/"今日はキミの誕生日だもの\nお祝いしないともったいないわ！\nそれじゃあ、かんぱ～い☆");
INSERT INTO `unit_comments` VALUES (/*id*/1146012, /*unit_id*/114631, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日、おめでとう！\n後で二人きりでパーティー\nしちゃおっか♪");
INSERT INTO `unit_comments` VALUES (/*id*/1147001, /*unit_id*/114701, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/5.9, /*change_face*/1, /*description*/"はーっはっはっは！\n新年の初笑いは悪党らしく\n盛大にだ！　オマエも一緒に\nやらないか？　気持ちいいぞ！");
INSERT INTO `unit_comments` VALUES (/*id*/1147002, /*unit_id*/114701, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/5.3, /*change_face*/3, /*description*/"悪党に休みなしだ！\n正月だろうとアタシは戦うぞ！\nさあ、文句があるヤツは\n全員かかってこい！");
INSERT INTO `unit_comments` VALUES (/*id*/1147003, /*unit_id*/114701, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/7.8, /*change_face*/8, /*description*/"一年の計はなんとかっていうだろ\nだからアタシは、悪党らしく\n正月から悪いことをするぞ！\nま、年中悪さしてるけどな！");
INSERT INTO `unit_comments` VALUES (/*id*/1147004, /*unit_id*/114701, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/8.3, /*change_face*/6, /*description*/"出店がいっぱいだな！　これじゃ\nお年玉がいくらあっても\n足りないぞ！　なあ、オマエのは\nまだ…な、なんでもない！");
INSERT INTO `unit_comments` VALUES (/*id*/1147005, /*unit_id*/114701, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/3.9, /*change_face*/2, /*description*/"まさかオマエと新年を\n過ごすことになるなんて…\nでも悪い気はしないな！\nなんか美味いもんでも食うぞ！");
INSERT INTO `unit_comments` VALUES (/*id*/1147006, /*unit_id*/114731, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"アタシが天楼覇断剣で\n全部真っ二つにしてやる！\n空だろうが海だろうが\n知ったことか！");
INSERT INTO `unit_comments` VALUES (/*id*/1147007, /*unit_id*/114731, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"飾りをつけた天楼覇断剣も\n意外と悪くないぞ！\nぶった切れればなんだって\nいいんだけどな！");
INSERT INTO `unit_comments` VALUES (/*id*/1147008, /*unit_id*/114731, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"気持ちのいい景色だな！\nどこかでアイツも…\nオクトーも、こんな風に\n日の出を見てるのかな…");
INSERT INTO `unit_comments` VALUES (/*id*/1147009, /*unit_id*/114731, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今年もオマエにはいい事あるぞ！\nなんてったってアタシと\nこの天楼覇断剣が一緒だからな！\nどんな未来も切り拓いてやる！");
INSERT INTO `unit_comments` VALUES (/*id*/1147010, /*unit_id*/114731, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"プリンセスナイトの力って\n初日の出に似てると思わないか？\n温かくて、柔らかくて…それに\nなんか力がみなぎってくるだろ！");
INSERT INTO `unit_comments` VALUES (/*id*/1147011, /*unit_id*/114701, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/5.4, /*change_face*/2, /*description*/"誕生日おめでとう！\nオマエのお祝いができて\nアタシもうれしいぞ！\nもっともっと祝わせろ！");
INSERT INTO `unit_comments` VALUES (/*id*/1147012, /*unit_id*/114731, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハッピーバースデー！\n今日はオマエの言う事を\nなんでも聞いてやるぞ！\nアタシに遠慮はするなよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1150001, /*unit_id*/115001, /*use_type*/1, /*voice_id*/1, /*face_id*/3, /*change_time*/6.1, /*change_face*/2, /*description*/"この私を待たせるとは\n良い度胸をしていますね\n…冗談ですよ\n待ち合わせ時刻、丁度です");
INSERT INTO `unit_comments` VALUES (/*id*/1150002, /*unit_id*/115001, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/4.0, /*change_face*/1, /*description*/"正月の街は騒々しいですね…\nですが、これはこれで\n観測するデータには\n事欠かないとも言えますか");
INSERT INTO `unit_comments` VALUES (/*id*/1150003, /*unit_id*/115001, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/8.7, /*change_face*/2, /*description*/"せっかくの正月です\n貴方も人の事ばかりにかまけず\n自分のための時間を持ちなさい\nお姉さんからのアドバイスですよ");
INSERT INTO `unit_comments` VALUES (/*id*/1150004, /*unit_id*/115001, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/5.1, /*change_face*/6, /*description*/"これを飲みなさい\nそこで売っていた甘酒です\n貴方に体調を崩されては\n私としても困りますから");
INSERT INTO `unit_comments` VALUES (/*id*/1150005, /*unit_id*/115001, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/9.8, /*change_face*/2, /*description*/"年下に見えるお姉さんは\n好きですか？\nええ、貴方の好みを聞いていますが\n…そう。それなら良いのです");
INSERT INTO `unit_comments` VALUES (/*id*/1150006, /*unit_id*/115031, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あけましておめでとうございます\n今年も私の様々な姿を\n貴方に見せてあげますよ…\nそのままがいい？　…そうですか");
INSERT INTO `unit_comments` VALUES (/*id*/1150007, /*unit_id*/115031, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"雪…こうして触れてみると\n儚いものなのですね\nあっという間に形が変わる…\n少し、親近感を覚えます");
INSERT INTO `unit_comments` VALUES (/*id*/1150008, /*unit_id*/115031, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"舞い散る雪を眺めながら\n温かい甘酒を飲むというのも\nなかなか乙なものですね\n貴方も付き合いなさい");
INSERT INTO `unit_comments` VALUES (/*id*/1150009, /*unit_id*/115031, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"外出など普段はしないのですが…\n気が向いたので初詣に行きますよ\n…ええ、他意など一切ない\n単なる気まぐれです");
INSERT INTO `unit_comments` VALUES (/*id*/1150010, /*unit_id*/115031, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"女性の晴れ姿を見た際には、まず\n言うべき事があると思いますが？\n…ふふ、よくできました\nもっと褒めてもいいのですよ？");
INSERT INTO `unit_comments` VALUES (/*id*/1150011, /*unit_id*/115001, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/7.7, /*change_face*/2, /*description*/"今日は貴方の誕生日ですね\n私の独断で贈り物を用意しました\nありがたく受け取りなさい");
INSERT INTO `unit_comments` VALUES (/*id*/1150012, /*unit_id*/115031, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日おめでとうございます\nまた一つ成長した貴方を\n見せてくれることを\n期待していますよ");
INSERT INTO `unit_comments` VALUES (/*id*/1155001, /*unit_id*/115501, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/8.2, /*change_face*/2, /*description*/"どんな困難が行先を阻もうとも\nわたくしはこの手で明日を\n掴んでみせます。わたくしの\n決意を見ていてくださいまし");
INSERT INTO `unit_comments` VALUES (/*id*/1155002, /*unit_id*/115501, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"主さまのおそばにいられる幸せは\n当たり前のものではなく\nとても特別なことなのだと\n感じておりますよ");
INSERT INTO `unit_comments` VALUES (/*id*/1155003, /*unit_id*/115501, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/4.7, /*change_face*/2, /*description*/"主さま、なにかお困りのことは\nございませんか？　これからも\n変わらず、わたくしになんなりと\nお申し付けくださいまし♪");
INSERT INTO `unit_comments` VALUES (/*id*/1155004, /*unit_id*/115501, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"この身に新たな力を宿せたのは\n全て主さまのおかげでございます\n恩返しできるよう、一生をかけて\nガイドさせていただきます");
INSERT INTO `unit_comments` VALUES (/*id*/1155005, /*unit_id*/115501, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/8.8, /*change_face*/5, /*description*/"主さまをお慕いする気持ちには\nこの真っ白な装束と同じく\n一切の曇りもございません\nわたくしの全ては、主さまに…♪");
INSERT INTO `unit_comments` VALUES (/*id*/1155006, /*unit_id*/115531, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"わたくしは独りではございません\nかけがえのない仲間のため…\n絆を紡いだ主さまのために\nこの身の全てを捧げます");
INSERT INTO `unit_comments` VALUES (/*id*/1155007, /*unit_id*/115531, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"精霊たちが、わたくしたちの絆を\n祝福してくれているようです\n笑顔あふれる素晴らしい仲間に\n心からの加護を！");
INSERT INTO `unit_comments` VALUES (/*id*/1155008, /*unit_id*/115531, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"猛き風の精霊よ…\n未来に向かって歩まんとする\nわたくしたちの背中に\n祝福の追い風を！");
INSERT INTO `unit_comments` VALUES (/*id*/1155009, /*unit_id*/115531, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"絆を繋ぎとめてくださった\n主さまの優しいお手手…\nその温もりは、この胸に灯る\n希望の明かりでございます");
INSERT INTO `unit_comments` VALUES (/*id*/1155010, /*unit_id*/115531, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"主さまの優しくて\n耳をくすぐるそのお声で\nどうかこれからもわたくしの名を\n呼んでくださいまし♪");
INSERT INTO `unit_comments` VALUES (/*id*/1155011, /*unit_id*/115501, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"今日は主さまがお生まれになった\nとてもおめでたい日ですね\n精霊たちとともに、心を込めて\nお祝いをさせてくださいまし♪");
INSERT INTO `unit_comments` VALUES (/*id*/1155012, /*unit_id*/115531, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうございます\nまた一つ大人になられましたね\n未来に向かって歩むその隣で\nこれからもわたくしはずっと…♪");
INSERT INTO `unit_comments` VALUES (/*id*/1156001, /*unit_id*/115601, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/9.0, /*change_face*/7, /*description*/"精霊の意思を感じ取る…\nやっぱり難しいなぁ…\n騎士クンもやってみる？\n…あれ？　寝ちゃった！？");
INSERT INTO `unit_comments` VALUES (/*id*/1156002, /*unit_id*/115601, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/8.4, /*change_face*/7, /*description*/"精霊って、色んな子がいるんだね\n優しい子だけじゃなくて、\nいたずら好きな子も…ふにゅ！\nうぅ～…ほっぺ突っつかないで～");
INSERT INTO `unit_comments` VALUES (/*id*/1156003, /*unit_id*/115601, /*use_type*/1, /*voice_id*/3, /*face_id*/7, /*change_time*/9.6, /*change_face*/8, /*description*/"この服、刺激が強くないかな…？\nだってほら、こんなに布が\n少なくて…はっ！\nあんまり近くで見ないで～！");
INSERT INTO `unit_comments` VALUES (/*id*/1156004, /*unit_id*/115601, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/13.3, /*change_face*/6, /*description*/"精霊を使役してる時って\n感覚が普段より冴えるの\n今も風や光の変化を感じるよ…\n騎士クンは…お腹減ってきた？");
INSERT INTO `unit_comments` VALUES (/*id*/1156005, /*unit_id*/115601, /*use_type*/1, /*voice_id*/5, /*face_id*/4, /*change_time*/7.4, /*change_face*/7, /*description*/"…うん、騎士クンとの距離が\n中々縮まらな…え、騎士クン！？\nこ、これはその、精霊と話を\nしてて…い、今すぐ忘れて～！");
INSERT INTO `unit_comments` VALUES (/*id*/1156006, /*unit_id*/115631, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"騎士クン…この子を一緒に\n育ててくれて、ありがとう\nわたしね、とってもうれしかった");
INSERT INTO `unit_comments` VALUES (/*id*/1156007, /*unit_id*/115631, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"大丈夫…伝わってるよ\nあなたの思いも、あなたの力も…\nずっと元気でいてね。約束だよ");
INSERT INTO `unit_comments` VALUES (/*id*/1156008, /*unit_id*/115631, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"騎士クンのためなら、\nいつでも呼んで…だって\nこの子もわたしも、\n同じ気持ちだよ");
INSERT INTO `unit_comments` VALUES (/*id*/1156009, /*unit_id*/115631, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お父さん…ちがっ！　違うの！\nこの子が今、騎士クンのこと\nそう呼んでて…もぉ～！\nさっきのは忘れて～！");
INSERT INTO `unit_comments` VALUES (/*id*/1156010, /*unit_id*/115631, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"人と精霊の気持ちが通じ合うなら\nこの気持ちも、騎士クンに\n通じたらいいのにな…あれ…？\n今、何も言ってない…よね…！？");
INSERT INTO `unit_comments` VALUES (/*id*/1156011, /*unit_id*/115601, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/6.4, /*change_face*/2, /*description*/"お誕生日おめでとう、騎士クン！\n精霊もおめでとうだって\nふふっ、やっぱり騎士クンは\nみんなに慕われてるんだね♪");
INSERT INTO `unit_comments` VALUES (/*id*/1156012, /*unit_id*/115631, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとう！\nあ、あのね？　精霊から\n祝いの舞を教わったの\n踊ってみても…いい？");
INSERT INTO `unit_comments` VALUES (/*id*/1157001, /*unit_id*/115701, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/8.3, /*change_face*/6, /*description*/"助手くん、準備はできたかい？\n今度の事件現場は海だよ！\n二人で一緒にバカンス\n…じゃなかった、調査に出発だ！");
INSERT INTO `unit_comments` VALUES (/*id*/1157002, /*unit_id*/115701, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/9.1, /*change_face*/6, /*description*/"海に持っていくものといえば\n手帳にルーペ、望遠鏡…\nあとは浮き輪にサーフボード…\nあれ…なにをしに行くんだっけ？");
INSERT INTO `unit_comments` VALUES (/*id*/1157003, /*unit_id*/115701, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/5.2, /*change_face*/2, /*description*/"海か…みんなに聞かれたら\nついていくって言いだしそうだ…\nだから、内緒にしておこうね？\n私と君だけの秘密、だよ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1157004, /*unit_id*/115701, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/6.4, /*change_face*/5, /*description*/"この髪型、動きやすくていいね\n…助手くんも気に入ってくれた？\nじゃ、じゃあしばらくはこれで…\n深い意味はないんだよっ！？");
INSERT INTO `unit_comments` VALUES (/*id*/1157005, /*unit_id*/115701, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/6.9, /*change_face*/5, /*description*/"事件、すぐに解決できるといいね\nえっ？　なんでって…それは…\n…助手くんと遊びたいからだよ！\nそれくらい見抜きたまえ、もう！");
INSERT INTO `unit_comments` VALUES (/*id*/1157006, /*unit_id*/115731, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"助手くんっ、見たまえ、海だよ！\nなんだい？　その驚いた眼差しは\n私だってはしゃぎたいときもある\n…助手くんと海に来たときとかね");
INSERT INTO `unit_comments` VALUES (/*id*/1157007, /*unit_id*/115731, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"波打ち際の名探偵…\nう～ん、いい響きだ♪\nさぁ助手くん、青き水平線に\n隠されし真実、見つけに行こう！");
INSERT INTO `unit_comments` VALUES (/*id*/1157008, /*unit_id*/115731, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"助手くん、君の観察力を試そう\n私は今、普段と違う格好だろう？\nなにか気づいたこととか…えっ？\n水着、似合ってる？　えへへ…♪");
INSERT INTO `unit_comments` VALUES (/*id*/1157009, /*unit_id*/115731, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"世界にはまだまだ謎があふれてる\nこうして色々なところを巡って\n真実を見つけるのも…いいかもね\nもちろん助手くんと一緒に、だよ");
INSERT INTO `unit_comments` VALUES (/*id*/1157010, /*unit_id*/115731, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"いつも私は君のことを助手くんと\n呼んでいるだろう？　でも今は、\n遊びに来ているわけだから…\nその…やっぱりなんでもないっ！");
INSERT INTO `unit_comments` VALUES (/*id*/1157011, /*unit_id*/115701, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/3.7, /*change_face*/2, /*description*/"さて助手くん、\n呼ばれた理由は分かるかな？\nふふ、私にはお見通しなのさ…！\n誕生日、おめでとうっ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1157012, /*unit_id*/115731, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"助手くん、誕生日だよね\nこれを受け取って欲しい\nこのプレゼントに込められた意味\n…解き明かしてみたまえ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1158001, /*unit_id*/115801, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/4.6, /*change_face*/1, /*description*/"まさか私が貴族様の舞踏会に\n出られるなんて…きちんと\nレッスンがんばらなきゃ！");
INSERT INTO `unit_comments` VALUES (/*id*/1158002, /*unit_id*/115801, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/4.2, /*change_face*/2, /*description*/"人間の姿になれるだけでも\nすっごく幸せなのに、こんなに\nすてきなドレスを着られるなんて\n本当に夢みたいだわ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1158003, /*unit_id*/115801, /*use_type*/1, /*voice_id*/3, /*face_id*/4, /*change_time*/4.4, /*change_face*/1, /*description*/"淑女ってどういう人のことを\nいうのかしら…森で木を切る\n淑女がいてもいいわよね？");
INSERT INTO `unit_comments` VALUES (/*id*/1158004, /*unit_id*/115801, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/6.6, /*change_face*/2, /*description*/"牧場での暮らしは本当に幸せで\nこれ以上を望む必要なんて\nないけど、やっぱり華やかな\n世界も憧れちゃうわね");
INSERT INTO `unit_comments` VALUES (/*id*/1158005, /*unit_id*/115801, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/2.7, /*change_face*/2, /*description*/"ドレス姿が似合ってる？\nふふ、ありがとう。あなたに\nそう言ってもらえれば\n安心だわ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1158006, /*unit_id*/115831, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"アン・ドゥ・トロワ♪\nまさか私がきれいなドレスで\n踊れるなんて…キラキラした\n夢の中にいるみたいだわ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1158007, /*unit_id*/115831, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"こんなに素敵な場所で\nあなたと踊れるなんて…本当に\nお話の中のお姫様になったみたい\nあなたも王子様みたいで素敵よ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1158008, /*unit_id*/115831, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"素敵なドレスを着て優雅に踊る…\nすっごく楽しいけど、あなたや\n牧場のみんなと居る時間も\n負けないくらい素敵な時間よね♪");
INSERT INTO `unit_comments` VALUES (/*id*/1158009, /*unit_id*/115831, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなたがいてくれたから\n私は明るく前向きでいられたの\nこれからは私が誰かのそんな人に\nなれたらいいな…");
INSERT INTO `unit_comments` VALUES (/*id*/1158010, /*unit_id*/115831, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"魔法はいつか終わっちゃう…\nでも魔法みたいに素敵な時間は\n自分の力で作っていけるのよね\n私、これからもがんばるわ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1158011, /*unit_id*/115801, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/2.3, /*change_face*/1, /*description*/"お誕生日おめでとう！\nあなたのために森で花を摘んで\nきたんだけど、受け取って\nもらえるかしら？");
INSERT INTO `unit_comments` VALUES (/*id*/1158012, /*unit_id*/115831, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなたの特別な日をこうして\n一緒に迎えられるのはとっても\nうれしいわ♪　また来年も一緒に\n過ごしたいな…なんて");
INSERT INTO `unit_comments` VALUES (/*id*/1159001, /*unit_id*/115901, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/0.0, /*change_face*/5, /*description*/"いつもズボンだから、\nスカートってのは落ち着かねえな\nま、まあ、こういう系の服も\nきらいじゃねえけどさ…");
INSERT INTO `unit_comments` VALUES (/*id*/1159002, /*unit_id*/115901, /*use_type*/1, /*voice_id*/2, /*face_id*/4, /*change_time*/0.0, /*change_face*/5, /*description*/"パトロールに行きてえけど、\nこの服で行ったら目立つよなぁ\nえ、似合ってるから大丈夫って…\nそ、そういう問題じゃねえよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1159003, /*unit_id*/115901, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/2, /*description*/"この服、結構動きやすいんだな\nへへっ、これなら、\n思いっきり暴れられそうだぜ！");
INSERT INTO `unit_comments` VALUES (/*id*/1159004, /*unit_id*/115901, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/2, /*description*/"お嬢さまみたいな格好してる\nわけだし、しゃれた料理でも\n作ってみるか…もちろん、\nおまえの分も作ってやるよ");
INSERT INTO `unit_comments` VALUES (/*id*/1159005, /*unit_id*/115901, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/0.0, /*change_face*/1, /*description*/"どんな格好だろうと、\nあたしの剣の腕は変わらねえぜ\nなんなら、\n今から稽古つけてやろうか？");
INSERT INTO `unit_comments` VALUES (/*id*/1159006, /*unit_id*/115931, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"こんな格好してるやつが、\n戦えるわけないって？\n…だったら、\nその身で確かめてみるんだな！");
INSERT INTO `unit_comments` VALUES (/*id*/1159007, /*unit_id*/115931, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"優雅に舞い…そして、\n狼のように敵をかみ砕く！\nあたしなりの華麗なる\n剣術ってやつを見せてやるよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1159008, /*unit_id*/115931, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この服だと、お嬢さまになった\n気分だな…華麗に舞いますわ♪\nな～んて…！？　おまえいつから\nそこに…み、見るんじゃねぇ～！");
INSERT INTO `unit_comments` VALUES (/*id*/1159009, /*unit_id*/115931, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"なんだよ、急にじーと見てきて…\nそ、そういう服も似合うねって…\n急になに言ってんだよ！\nまあでも、あ、ありがとな…");
INSERT INTO `unit_comments` VALUES (/*id*/1159010, /*unit_id*/115931, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ここなら、思いっきり体を\n動かせそうだし、訓練してくか\nおまえも一緒にどうだ？\n二人ならもっと楽しめると思うぜ");
INSERT INTO `unit_comments` VALUES (/*id*/1159011, /*unit_id*/115901, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/2, /*description*/"今日はおまえの誕生日だよな\nせっかくだし、\nなんか飯でも作ってやるよ\nリクエストがあったら言ってくれ");
INSERT INTO `unit_comments` VALUES (/*id*/1159012, /*unit_id*/115931, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"きょ、今日って誕生日だよな？\nおまえには世話になってるし、\nその…ケ、ケーキ焼いてきた\nから、さっさと受け取れ！");
INSERT INTO `unit_comments` VALUES (/*id*/1160001, /*unit_id*/116001, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/8.2, /*change_face*/2, /*description*/"マホマホ王国のお茶会へようこそ\nマホ姫が王子はんのために、\n愛を込めておもてなししますわ");
INSERT INTO `unit_comments` VALUES (/*id*/1160002, /*unit_id*/116001, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/3.5, /*change_face*/2, /*description*/"日差しは苦手どすけど、\n春のひなたはぽかぽかしとって、\n心地ええなぁ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1160003, /*unit_id*/116001, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/10.1, /*change_face*/2, /*description*/"花の香り、小鳥のさえずり、\nそして王子はんと過ごす時間\nふふっ、ここは夢の国やね♪");
INSERT INTO `unit_comments` VALUES (/*id*/1160004, /*unit_id*/116001, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/6.5, /*change_face*/5, /*description*/"王子はんに見てほしくて、\n衣装を新調したんよ\n王子はん、うちはきれいどすか♪");
INSERT INTO `unit_comments` VALUES (/*id*/1160005, /*unit_id*/116001, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/4.8, /*change_face*/2, /*description*/"礼儀作法のことならうちに任し\nなんならうち、王子はんのために\nつきっきりで教えまひょか♪");
INSERT INTO `unit_comments` VALUES (/*id*/1160006, /*unit_id*/116031, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"王子はんに喜んでほしくて\nたくさんお菓子作ったんや\nああ、お礼なんていいんよ\n王子はんの笑顔だけで充分や♪");
INSERT INTO `unit_comments` VALUES (/*id*/1160007, /*unit_id*/116031, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"テーブルマナーはお互いが\n仲よく過ごすための心配りなんよ\nでも一番大切なのは\n一緒に楽しい時間を過ごすことや");
INSERT INTO `unit_comments` VALUES (/*id*/1160008, /*unit_id*/116031, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"王子はんと一緒におると\nうちの知らない世界が見えて\n楽しいわぁ。王子はんの世界も\nきっとワンダーランドなんやねぇ");
INSERT INTO `unit_comments` VALUES (/*id*/1160009, /*unit_id*/116031, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"童話の幸せな結末\nその続きはだれも分からへんけど\nうちと王子はんならずっとずっと\n幸せに暮らせそうやねぇ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1160010, /*unit_id*/116031, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日はマホマホ王国のお茶会や\nマホ姫の特別なおもてなしで\n王子はんをたくさんたくさん\n甘やかしますわぁ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1160011, /*unit_id*/116001, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/4.4, /*change_face*/1, /*description*/"お誕生日おめでとう～\nうち王子はんのために\nケーキを用意したんよ\nおいしく召し上がってなぁ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1160012, /*unit_id*/116031, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日は王子はんのお誕生日やね！\nそういうわけで…じゃーん！\nお花のシャワーや！\nふふっ、きれいやろう？");
INSERT INTO `unit_comments` VALUES (/*id*/1162001, /*unit_id*/116201, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/9.0, /*change_face*/4, /*description*/"テレフェスっていうか、\nまあ体育とかスポーツ的なのは\n得意な部類だよね、個人競技は\n団体競技は…協調性が課題だわ");
INSERT INTO `unit_comments` VALUES (/*id*/1162002, /*unit_id*/116201, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/0.0, /*change_face*/6, /*description*/"いいけどユニ先輩のフェス服、\nアレ何なの。似合ってるけど\nチエルのも無駄にデコってて、\nアレ何なの。似合ってるけど");
INSERT INTO `unit_comments` VALUES (/*id*/1162003, /*unit_id*/116201, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/6.1, /*change_face*/6, /*description*/"いんだけどさ、最近テレ女の空気\n全体的にちょっとアレじゃない？\nや、なんか校風崩れっていうの？\nルーセンティズム入ってきてね？");
INSERT INTO `unit_comments` VALUES (/*id*/1162004, /*unit_id*/116201, /*use_type*/1, /*voice_id*/4, /*face_id*/8, /*change_time*/10.1, /*change_face*/6, /*description*/"そういやあんたもどっかの学校で\nスポーツ経験あるらしいね\nそうなんだ…へー、いいじゃん\nえ、海辺で野球？　何それ冗談？");
INSERT INTO `unit_comments` VALUES (/*id*/1162005, /*unit_id*/116201, /*use_type*/1, /*voice_id*/5, /*face_id*/5, /*change_time*/8.5, /*change_face*/1, /*description*/"え、ちょ何…ガン見やめてよ\nわりかし肌出てンの思いだして、\n急にモジついて来ちゃうじゃん…\nあ？　寒そう？　いいえ、別に");
INSERT INTO `unit_comments` VALUES (/*id*/1162006, /*unit_id*/116231, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"よっ、と楽勝…！\nつーかさ、障害物競走なんて、\nフツー子供のやるもんでしょ…\nマジでこの学校アタオカだわ");
INSERT INTO `unit_comments` VALUES (/*id*/1162007, /*unit_id*/116231, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ちょー、いつもの外野うるさい\nハズめの声援やめてくんない\nあと、うち不良ではないから\nいや毒婦でもねーわ");
INSERT INTO `unit_comments` VALUES (/*id*/1162008, /*unit_id*/116231, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"そんな、うちの応援とかいいから\nあんたも自分の競技がんばりー？\n次なに出んの。あ？　二人三脚？\nえ、誰と。別にいんだけど、誰と");
INSERT INTO `unit_comments` VALUES (/*id*/1162009, /*unit_id*/116231, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"なんか悪いね、あんたに弟の\n面倒まで見てもらっちゃって…\nそだ、このあと家族とおべんと\n食べんだけど良ければ一緒にど？");
INSERT INTO `unit_comments` VALUES (/*id*/1162010, /*unit_id*/116231, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハ？　いや、格好いいとかそんな\n真顔で言うフツー？　はっず\nや、べつに言うなってわけじゃ…\n言いたきゃそれは…言いなよ");
INSERT INTO `unit_comments` VALUES (/*id*/1162011, /*unit_id*/116201, /*use_type*/2, /*voice_id*/6, /*face_id*/6, /*change_time*/7.7, /*change_face*/1, /*description*/"あんた今日、誕生日だって？\nなんで言わんの、そーゆーこと…\nなんも用意してなかったじゃん\nじゃあ飴ちゃん多めにあげるわ");
INSERT INTO `unit_comments` VALUES (/*id*/1162012, /*unit_id*/116231, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あんた今日、誕生日だよね\nああいや、さっきチエルに聞いて\nそだ、偶然ケーキ作ってきたから\nコレあげるわ。いや偶然だけど");
INSERT INTO `unit_comments` VALUES (/*id*/1163001, /*unit_id*/116301, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/4.1, /*change_face*/3, /*description*/"先輩、大変です！\n若者のちぇる離れが深刻です！\nちぇる語普及委員の一人として\nどうお考えなんですか！");
INSERT INTO `unit_comments` VALUES (/*id*/1163002, /*unit_id*/116301, /*use_type*/1, /*voice_id*/2, /*face_id*/7, /*change_time*/6.8, /*change_face*/1, /*description*/"先輩、大変です！　今ならなんと\nこの「幸せになれるちぇる」が\n１万ルピでお手元に。どうです？\nちぇるは肉眼では見えませんけど");
INSERT INTO `unit_comments` VALUES (/*id*/1163003, /*unit_id*/116301, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/8.3, /*change_face*/4, /*description*/"クロエ先輩がチエルのフェス姿\n可愛いって言ってくれないんです\n…え、先輩の前では言ってた？\nやだー！　直接言ってほしいー！");
INSERT INTO `unit_comments` VALUES (/*id*/1163004, /*unit_id*/116301, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/10.4, /*change_face*/6, /*description*/"そういえば先輩もどっかの学校で\nスポーツ経験あるらしいですね\nそうなんだ…ふうん意外な一面…\nえ、海辺で野球？　何それ冗談？");
INSERT INTO `unit_comments` VALUES (/*id*/1163005, /*unit_id*/116301, /*use_type*/1, /*voice_id*/5, /*face_id*/7, /*change_time*/5.4, /*change_face*/2, /*description*/"ちぇる～ん☆　どうですか、この\nチエル仕様のフェス用ウェア！\nえっ？　そんなに似合ってます？\nですよね、知ってます。えへへ");
INSERT INTO `unit_comments` VALUES (/*id*/1163006, /*unit_id*/116331, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ちぇる～ん！　ちぇる　ちぇろぱ\nちぇるぴぴぱ　ちぇぴらぺぽー？\nうんうん、だよねーおっけー！\nじゃあリズムを上げて行くよー！");
INSERT INTO `unit_comments` VALUES (/*id*/1163007, /*unit_id*/116331, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"みんなー！\nちえるん単推しなのは分かるけど\n後ろの地味な二人にも\n少しは歓声送ってあげてねー！");
INSERT INTO `unit_comments` VALUES (/*id*/1163008, /*unit_id*/116331, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"やっぱりフェスの最後はお約束、\nヒロインたちのライブだよねー！\n会場のみんなも配信のみんなも…\nえ、これ魔石配信してないの？");
INSERT INTO `unit_comments` VALUES (/*id*/1163009, /*unit_id*/116331, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ちぇるミナすこすこ侍の先輩も\nファンサで釣ってあげますよー！\nそうれ、投げキッＣＨＵ～☆\nって、何よそ見してるんですか！");
INSERT INTO `unit_comments` VALUES (/*id*/1163010, /*unit_id*/116331, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"歌って踊れるスーパーアイドル、\nこれが世界の美少女ちえるんです\nどうです先輩、惚れ直しました？\nえ、真顔で頷かれても…困る…");
INSERT INTO `unit_comments` VALUES (/*id*/1163011, /*unit_id*/116301, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/7.3, /*change_face*/1, /*description*/"お誕生日おめでとうございます！\n可愛い後輩から可愛いちぇるを\nプレゼント！　ちぇる～ん☆\n良かったですね、一生ものですよ");
INSERT INTO `unit_comments` VALUES (/*id*/1163012, /*unit_id*/116331, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"先輩、ハピバ～！\nじゃあ今日は特別なんでチエルが\n一緒にケーキ食べてあげますね！\nお祝いだから割り勘でいいですよ");
INSERT INTO `unit_comments` VALUES (/*id*/1165001, /*unit_id*/116501, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"はーっはっはっは！\nビッグボス・イノリ様の登場です\nお兄さんは今日から側近として\nあたしをサポートするですよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1165002, /*unit_id*/116501, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/10.4, /*change_face*/3, /*description*/"あたしの知名度が広まるよう\nお兄さんはあたしを担いで…え？\n肩車すればいいのかって…\nそういう意味じゃねーですよ！！");
INSERT INTO `unit_comments` VALUES (/*id*/1165003, /*unit_id*/116501, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"あたしがランドソルで一番の\nボスになったら、カルミナを\n呼んでパーティーです！\nお兄さんも呼んであげますから！");
INSERT INTO `unit_comments` VALUES (/*id*/1165004, /*unit_id*/116501, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/11.0, /*change_face*/3, /*description*/"このポーズとこのポーズ\nどっちがかっこいいですか！？\n…どっちもかわいい？\nガ、ガキ扱いするなですよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1165005, /*unit_id*/116501, /*use_type*/1, /*voice_id*/5, /*face_id*/5, /*change_time*/7.9, /*change_face*/3, /*description*/"お兄さんの仕事は\nそのお人好し力を\nあたしのために使うことです！\nよそ見は禁止ですよ！！");
INSERT INTO `unit_comments` VALUES (/*id*/1165006, /*unit_id*/116531, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"どうですか、このポーズ！\n…え？　台の上はあぶない？\nお兄さんは分かってないですね\n高い所に登るのがボスの鉄則です");
INSERT INTO `unit_comments` VALUES (/*id*/1165007, /*unit_id*/116531, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お兄さんには見えますか！\n空に輝くあの一番星が！\nあれこそギャングスターです！\n今は昼？　心の目で見るですよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1165008, /*unit_id*/116531, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あたしがボスになったら\nイノリのお菓子工場を作らせます\nお小遣いとお菓子の両取りですよ\nくぅ〜！　夢が広がるです！");
INSERT INTO `unit_comments` VALUES (/*id*/1165009, /*unit_id*/116531, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あたしの計画ではお兄さんは側近\nカヤぴぃは右腕にして、ふふっ\nボスは雑用係にしてやるですよ！\nぴっ！？　今寒気がしたような…");
INSERT INTO `unit_comments` VALUES (/*id*/1165010, /*unit_id*/116531, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ふっ、この服どうですか？\nかっこいいって言っても…\nはぁ！？　か、かわいいって\n言うのは、やめやがれですー！");
INSERT INTO `unit_comments` VALUES (/*id*/1165011, /*unit_id*/116501, /*use_type*/2, /*voice_id*/6, /*face_id*/6, /*change_time*/2.9, /*change_face*/2, /*description*/"今日はお兄さんの誕生日？\n一億年前から知ってましたよ！\nタコスおごるので、お兄さんは\nあたしにタコスおごるですよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1165012, /*unit_id*/116531, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうです！\nあたしは部下想いのボスですから\nわがままを聞いてあげなくも…\nってなんですその本気の目は！？");
INSERT INTO `unit_comments` VALUES (/*id*/1166001, /*unit_id*/116601, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/6.4, /*change_face*/1, /*description*/"全く、喧嘩屋のオレに\nこんな格好までさせやがって\nまあ、やるからにはキッチリ\nやるさ。半端なのは嫌いだからな");
INSERT INTO `unit_comments` VALUES (/*id*/1166002, /*unit_id*/116601, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/7.5, /*change_face*/6, /*description*/"この格好、\n案外動きやすいんだよな\nこれならケンカも…\nや、やらねえよ！　…多分");
INSERT INTO `unit_comments` VALUES (/*id*/1166003, /*unit_id*/116601, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/4.9, /*change_face*/6, /*description*/"本日はご利用いただき\nありがとうございまーす！\n…自分で言うのもなんだけど\n全然似合わねーな！");
INSERT INTO `unit_comments` VALUES (/*id*/1166004, /*unit_id*/116601, /*use_type*/1, /*voice_id*/4, /*face_id*/4, /*change_time*/13.5, /*change_face*/6, /*description*/"この仕事は笑顔が大事\n分かっちゃいるけど難しいぜ…\nに、にこぉ…って\n何見てんだよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1166005, /*unit_id*/116601, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/6.0, /*change_face*/2, /*description*/"キミがついてきてくれてると\nなんだか一緒に旅してる\nみたいな気がする\n結構楽しいもんだな");
INSERT INTO `unit_comments` VALUES (/*id*/1166006, /*unit_id*/116631, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"よく来たな。人気スポットから\n危険地帯まで、どんなトコでも\nガイドしてやるよ\nさあ、たぎるような旅に出発だ！");
INSERT INTO `unit_comments` VALUES (/*id*/1166007, /*unit_id*/116631, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"現地でトラブルがあったら\n迷わずオレを呼びな！\nランドソルで一番荒事に強い\nガイドだからな！");
INSERT INTO `unit_comments` VALUES (/*id*/1166008, /*unit_id*/116631, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"キミの頼みならどんな危険な\n場所だって連れてってやるよ\nドラゴン族渾身のガイドで\n存分に楽しませてやるからな！");
INSERT INTO `unit_comments` VALUES (/*id*/1166009, /*unit_id*/116631, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"キミがいなけりゃ、この仕事も\nこんなに上手くいかなかった\nだろうな。やっぱダチってヤツは\n最高だぜ！");
INSERT INTO `unit_comments` VALUES (/*id*/1166010, /*unit_id*/116631, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"なあ、いつか二人で\n旅に行こうぜ！　仕事とか\n関係なく、好きなとこ行くんだ\nキミとならきっと楽しいだろうな");
INSERT INTO `unit_comments` VALUES (/*id*/1166011, /*unit_id*/116601, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/2.0, /*change_face*/2, /*description*/"今日誕生日だろ？\nほら、プレゼントだ\nこの前土産物屋に行った時に\nいいなって思ってさ！");
INSERT INTO `unit_comments` VALUES (/*id*/1166012, /*unit_id*/116631, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"よう、誕生日おめでとう！\n今日はオレが厳選した店に\n連れてってやるよ。年に一度の\n大事な日だ、パーッと祝おうぜ！");
INSERT INTO `unit_comments` VALUES (/*id*/1167001, /*unit_id*/116701, /*use_type*/1, /*voice_id*/1, /*face_id*/3, /*change_time*/5.4, /*change_face*/2, /*description*/"…よし！　…よし！　…よし！\nうん、発声練習終わり…！\n今日も一日、ベルトコンベアと\nにらめっこです…！");
INSERT INTO `unit_comments` VALUES (/*id*/1167002, /*unit_id*/116701, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"日が昇る前から\n工場の前に立ってますよ…！\n無遅刻無欠勤を目指します！\nえっへん！");
INSERT INTO `unit_comments` VALUES (/*id*/1167003, /*unit_id*/116701, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/5.7, /*change_face*/2, /*description*/"ランチは工場の先輩から\n誘われるまで倉庫の隅っこで…\nあなたも一緒にどうですか…？\n静かで過ごしやすいですよ…♪");
INSERT INTO `unit_comments` VALUES (/*id*/1167004, /*unit_id*/116701, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"工作の技術は\nマイフレンドくん作りで\n身に付けました…！\n工場でも通用するといいなあ…♪");
INSERT INTO `unit_comments` VALUES (/*id*/1167005, /*unit_id*/116701, /*use_type*/1, /*voice_id*/5, /*face_id*/4, /*change_time*/6.8, /*change_face*/6, /*description*/"うう…人生初めてのアルバイト…\n不安で消えてなくなりそうです…\nで、でも…あなたも一緒だから\nきっと大丈夫ですよね…！");
INSERT INTO `unit_comments` VALUES (/*id*/1167006, /*unit_id*/116731, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"『だいじょぶマイフレンドくん\nＶ４』のカスタマイズも…\nバイト仲間を作ってぼっち卒業も\nこのバイトで叶えてみせます…！");
INSERT INTO `unit_comments` VALUES (/*id*/1167007, /*unit_id*/116731, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ぼっちの私に、誰かと一緒に\n作業する日が来るなんて…！\nまさか夢…！？\n絶対そうに決まってます〜！");
INSERT INTO `unit_comments` VALUES (/*id*/1167008, /*unit_id*/116731, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"バイト先でもＢＢ団の使命は\n忘れませんよ…！\nぼっち卒業を目指して\nバイト仲間作りにも励みます…！");
INSERT INTO `unit_comments` VALUES (/*id*/1167009, /*unit_id*/116731, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"工具や機械の使い方も\n慣れてきました…！\nけど、バイト先の人との会話は\n全然慣れません…ううっ…");
INSERT INTO `unit_comments` VALUES (/*id*/1167010, /*unit_id*/116731, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ＢＢ団でそろって\n一緒にバイトができるなんて…！\nおもちゃも、バイト仲間も\n作りましょうね…えへへ…♪");
INSERT INTO `unit_comments` VALUES (/*id*/1167011, /*unit_id*/116701, /*use_type*/2, /*voice_id*/6, /*face_id*/6, /*change_time*/7.9, /*change_face*/2, /*description*/"あなたのお誕生日を祝う言葉を\nわ、私が…！？　し、幸せすぎて\nちょっと怖いですが…せーのっ\nお誕生日おめでとうございます！");
INSERT INTO `unit_comments` VALUES (/*id*/1167012, /*unit_id*/116731, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日はあなたのお誕生日をお祝い\nさせてください！　歌に、花束の\n贈呈…それと100通の手紙です\n…では心を込めて歌います！");
INSERT INTO `unit_comments` VALUES (/*id*/1168001, /*unit_id*/116801, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/5.4, /*change_face*/2, /*description*/"たい焼きいかがかにゃ～！\n…あ、キミいいところに！\n手伝ってくれたら特別に\nたい焼きの賄いをあげるにゃ！");
INSERT INTO `unit_comments` VALUES (/*id*/1168002, /*unit_id*/116801, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/5.8, /*change_face*/1, /*description*/"にゃはは♪\nこのたい焼きメーカーがあれば\n売り上げ爆増だにゃ！\n貯金箱が貯まっていくにゃ！");
INSERT INTO `unit_comments` VALUES (/*id*/1168003, /*unit_id*/116801, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/3.8, /*change_face*/2, /*description*/"お客さんが美味しそうに\nたい焼きを食べてるにゃ！\nやっぱりたい焼きは\nみんなを幸せにするにゃ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1168004, /*unit_id*/116801, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/6.0, /*change_face*/1, /*description*/"キミ、じっとあたしを見てるけど\nこの服装気に入ったにゃ？\nなんなら…特別価格で\nポーズしてあげるにゃ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1168005, /*unit_id*/116801, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/8.7, /*change_face*/2, /*description*/"忙しくても笑顔は忘れにゃい！\n商売人の鉄則にゃ！\nほら、キミも笑顔にゃ！\n…うん、その方が男前にゃ！");
INSERT INTO `unit_comments` VALUES (/*id*/1168006, /*unit_id*/116831, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"忙しくても大丈夫にゃ♪\n猫の手に代わって\n自動たい焼きメーカーが\nあたしをサポートしてくれるにゃ");
INSERT INTO `unit_comments` VALUES (/*id*/1168007, /*unit_id*/116831, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"たい焼きを買ってくれたお礼に\nスマイルもサービスするにゃ！\nウェイトレスのバイトも\n出来そうな気がするにゃ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1168008, /*unit_id*/116831, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"たい焼き屋の商売が忙しくても\n義賊活動でピンチになっても\n背中を預ける人がいれば平気にゃ\nこれからもよろしくにゃ、相棒！");
INSERT INTO `unit_comments` VALUES (/*id*/1168009, /*unit_id*/116831, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"キミへ賄いのたい焼きにゃ！\nサイズが大きいのは\nいつも助けてもらってるお礼って\nことで、受け取るにゃ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1168010, /*unit_id*/116831, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お客さんの笑顔が\nあたしの夢の源にゃ！\nそれでキミの笑顔は…\nあたしの元気の源にゃ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1168011, /*unit_id*/116801, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/5.6, /*change_face*/1, /*description*/"誕生日おめでとうにゃ！\n特別にたい焼きケーキを\n作ってきたにゃ！\n食べさせてあげようかにゃ？");
INSERT INTO `unit_comments` VALUES (/*id*/1168012, /*unit_id*/116831, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日はキミの誕生日にゃ！\n特別に0ルピで今日はあたしを\n貸し出ししてあげるにゃ！\nなんならデートするかにゃ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1801001, /*unit_id*/180101, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/4.2, /*change_face*/2, /*description*/"チェンジ、プリンセスフォーム！\n…くぅぅー！　やっぱり変身って\nすっごくワクワクするよね！\n騎士くん、一緒にポーズとって！");
INSERT INTO `unit_comments` VALUES (/*id*/1801002, /*unit_id*/180101, /*use_type*/1, /*voice_id*/2, /*face_id*/4, /*change_time*/0.0, /*change_face*/4, /*description*/"ううぅ…変身した後は\nお腹が減るって言ってたけど、\nほんとなんだね…\nご飯まだかなぁ…お腹空いたな");
INSERT INTO `unit_comments` VALUES (/*id*/1801003, /*unit_id*/180101, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/7.8, /*change_face*/2, /*description*/"この力で、世界中の人たちを\n助けにいくよ！　騎士くんも\n何かあったら言ってね？\n困った時はお互いさまさま♪");
INSERT INTO `unit_comments` VALUES (/*id*/1801004, /*unit_id*/180101, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/11.0, /*change_face*/2, /*description*/"えっと…騎士くん\n聞いていいかな？　このカッコ…\n…え？　似合ってる…？\nえへへっ、ほめられちゃった♪");
INSERT INTO `unit_comments` VALUES (/*id*/1801005, /*unit_id*/180101, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/10.1, /*change_face*/2, /*description*/"騎士くん、ちょっとほっぺ触るね\nどうどう？　肉球、\nいつもよりあったかいでしょ？\nこれぞ、プリンセス肉球だー♪");
INSERT INTO `unit_comments` VALUES (/*id*/1801006, /*unit_id*/180131, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"うにゃにゃにゃにゃー！\nパワーが溢れて止まらない！\nこれがあたしと騎士くんの！\nプリンセスフォームの力だぁー！");
INSERT INTO `unit_comments` VALUES (/*id*/1801007, /*unit_id*/180131, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"すごい力だよね…だけど、\nあたし、使いこなしてみせるよ！\n大切な人たちに、\nもう傷ついて欲しくないもん！");
INSERT INTO `unit_comments` VALUES (/*id*/1801008, /*unit_id*/180131, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あたしの姿が変わっても、\n騎士くんはそのままなんだ…あ！\nこれと同じ服作る？　そしたら\n一緒にプリンセスフォームだよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1801009, /*unit_id*/180131, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"変身するとね？\n胸の奥が燃えてるみたいに\n熱くなるんだ…\n騎士くん、あたしに何かした？");
INSERT INTO `unit_comments` VALUES (/*id*/1801010, /*unit_id*/180131, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この力は、騎士くんと一緒に\nいないと使えないんだよね？\nじゃあ、ずーっといちゃおっかな\n…じょ、冗談！　冗談だよー！");
INSERT INTO `unit_comments` VALUES (/*id*/1801011, /*unit_id*/180101, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/5.9, /*change_face*/1, /*description*/"お誕生日おめでとう、騎士くん！\nお祝いになんでも食べて！\n串焼き…リンゴ飴…うーん、\nそうだ！　全部食べちゃおー！");
INSERT INTO `unit_comments` VALUES (/*id*/1801012, /*unit_id*/180131, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハッピーバースデー、騎士くん！\nあたしにしてほしいこと、ない？\n今日は騎士くんのためだけに\n超頑張っちゃうよ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1802001, /*unit_id*/180201, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/3.5, /*change_face*/7, /*description*/"チェンジ・プリンセスフォーム…\nあれ！？　変身しちゃってる！？\n騎士クン！　今のはちょっと、\n言ってみたかっただけで…！");
INSERT INTO `unit_comments` VALUES (/*id*/1802002, /*unit_id*/180201, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/9.5, /*change_face*/2, /*description*/"変身したらお腹が減るけど\n何かを頑張った後のごはんって\n美味しいから、楽しみだよね\n今日は何を食べようかな…♪");
INSERT INTO `unit_comments` VALUES (/*id*/1802003, /*unit_id*/180201, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/13.3, /*change_face*/7, /*description*/"この力を使ってる時、騎士クンの\n格好は変わらないんだね…\nその…わたしがプリンセスなら\n王子様みたいに…なんでもない！");
INSERT INTO `unit_comments` VALUES (/*id*/1802004, /*unit_id*/180201, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/5.3, /*change_face*/5, /*description*/"騎士クンのためなら、わたしは\nどんなことが起きても平気だよ\n騎士クンと一緒にいられて、\n騎士クンが笑顔でいてくれたら…");
INSERT INTO `unit_comments` VALUES (/*id*/1802005, /*unit_id*/180201, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/3.3, /*change_face*/7, /*description*/"騎士クンと二人でパワーアップ…\nううぅ～…すごくうれしくて、\n胸の奥の気持ちが溢れ出そう\nこれも力の影響なのかな…");
INSERT INTO `unit_comments` VALUES (/*id*/1802006, /*unit_id*/180231, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"たとえこの先に、何があっても…\n騎士クンを、みんなを守りたいの\nわたしの、全てをかけて！\nこの思いは…誰にも負けない！　");
INSERT INTO `unit_comments` VALUES (/*id*/1802007, /*unit_id*/180231, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"これだけの大きな魔力、\n本当に使いこなせるのかな…\n…ううん、わたしはもう迷わない\nこの瞬間に、全力を出し切る！");
INSERT INTO `unit_comments` VALUES (/*id*/1802008, /*unit_id*/180231, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"戦うのは、わたしにまかせて！\n今の幸せも、未来の幸せも\n壊させたりしないよ…！\n今度こそ！　必ず！");
INSERT INTO `unit_comments` VALUES (/*id*/1802009, /*unit_id*/180231, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"騎士クンのあったかい力\n前よりもすごく強くなってる…\nわたしの気持ちも、同じだよ\n騎士クンの気持ちは…どう？");
INSERT INTO `unit_comments` VALUES (/*id*/1802010, /*unit_id*/180231, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"戦いや普段の時だけじゃなくて\nもっと色んな時間を騎士クンと\nずっっと一緒に過ごしたい…\n…まずは、食事に誘わなくちゃ！");
INSERT INTO `unit_comments` VALUES (/*id*/1802011, /*unit_id*/180201, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/6.5, /*change_face*/5, /*description*/"お誕生日おめでとう！\n今日はずっと騎士クンを\nお祝いしてあげたいの\nだから、そばにいてもいい…？");
INSERT INTO `unit_comments` VALUES (/*id*/1802012, /*unit_id*/180231, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"騎士クン、お誕生日おめでとう！\n…お祝いの気持ちだけじゃなくて\nもう一つ、伝えたいことがあるの\n…やっぱり言えないよぉ～！");
INSERT INTO `unit_comments` VALUES (/*id*/1804001, /*unit_id*/180401, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/4.5, /*change_face*/1, /*description*/"オイッスー☆\n一緒にお散歩、行きませんか？\n王女様なんて呼んじゃだめですよ\n今のわたしはペコリーヌです☆");
INSERT INTO `unit_comments` VALUES (/*id*/1804002, /*unit_id*/180401, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/10.9, /*change_face*/2, /*description*/"いつもと違うこの姿、\nあなたはどう思いますか？\n強そう？　う～ん、そっちですか\nかわいい？　えっへへ、それです");
INSERT INTO `unit_comments` VALUES (/*id*/1804003, /*unit_id*/180401, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/7.1, /*change_face*/2, /*description*/"王宮から抜けだして会っていると\nなんだかドキドキしますね♪\nお付きの護衛もいませんから\nあなたが守ってくださいね☆");
INSERT INTO `unit_comments` VALUES (/*id*/1804004, /*unit_id*/180401, /*use_type*/1, /*voice_id*/4, /*face_id*/4, /*change_time*/4.2, /*change_face*/1, /*description*/"わたしがごはんのことばかり\n考えてると思っていませんか？\n半分は正解ですけど、もう半分は\n…あなたのことですよ☆");
INSERT INTO `unit_comments` VALUES (/*id*/1804005, /*unit_id*/180401, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/7.7, /*change_face*/6, /*description*/"やっぱりあなたと囲む食卓が\n一番ですね☆　いっそのこと\n王宮で一緒に住みませんか？\nやっぱり、まずいですかね～？");
INSERT INTO `unit_comments` VALUES (/*id*/1804006, /*unit_id*/180431, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"超全力全開☆\n私は守られるだけの王女様じゃ\nありませんよ？　この国と…\nあなたのために、戦います！");
INSERT INTO `unit_comments` VALUES (/*id*/1804007, /*unit_id*/180431, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この姿、とっても強いんですけど\nその分お腹が減るんですよ～…\nでも、それってつまり…\nもっとごはんが食べられますね☆");
INSERT INTO `unit_comments` VALUES (/*id*/1804008, /*unit_id*/180431, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"力が溢れ出て、止まりません！\nこの力で、わたしの大切なものを\nぜ～んぶ守ってみせますね\n今度は何もなくしたりしません！");
INSERT INTO `unit_comments` VALUES (/*id*/1804009, /*unit_id*/180431, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなたの力をこれまでより\n強く感じます…温かくて、\n胸がキュンキュンしちゃいます…\nえへへ、やばいですね☆");
INSERT INTO `unit_comments` VALUES (/*id*/1804010, /*unit_id*/180431, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お腹が空きましたね！\nさぁ、わたしと一緒にごはんを\n食べに行きましょう♪\n今日も明日も仲良くごはんです☆");
INSERT INTO `unit_comments` VALUES (/*id*/1804011, /*unit_id*/180401, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/8.1, /*change_face*/5, /*description*/"あなたの誕生日となれば、\n国を挙げてお祝いしなきゃですね\nそれとも二人きりがいいですか？\n…えへへ、照れちゃいますね☆");
INSERT INTO `unit_comments` VALUES (/*id*/1804012, /*unit_id*/180431, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日はあなたの誕生日ですね\nおめでとうございます！　\nそれと…ありがとうございます\nこうしてわたしと出会ってくれて");
INSERT INTO `unit_comments` VALUES (/*id*/1805001, /*unit_id*/180501, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/6.1, /*change_face*/2, /*description*/"姿形が変わっても\nわたくしはわたくしのまま…\nですが、これまで以上に\n主さまにお仕えいたします");
INSERT INTO `unit_comments` VALUES (/*id*/1805002, /*unit_id*/180501, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/4.5, /*change_face*/2, /*description*/"主さまが変身するところも\n見とうございますね\nわたくしに続いて\n掛け声をどうぞ。せーの…");
INSERT INTO `unit_comments` VALUES (/*id*/1805003, /*unit_id*/180501, /*use_type*/1, /*voice_id*/3, /*face_id*/5, /*change_time*/5.3, /*change_face*/6, /*description*/"プリンセス耳かきに\nプリンセスふーふー…\nあ、いえ。この姿でのサポートは\n一味違ったものにしてみようかと");
INSERT INTO `unit_comments` VALUES (/*id*/1805004, /*unit_id*/180501, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/5.5, /*change_face*/1, /*description*/"この姿はまるでおとぎ話の\n妖精のようでございます\nいつも主さまの隣で\n舞っていたいものです");
INSERT INTO `unit_comments` VALUES (/*id*/1805005, /*unit_id*/180501, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/2.7, /*change_face*/2, /*description*/"『プリンセス・フォーム』…\nいつもより力が湧いてくるのは\n心の奥底で、主さまとの絆を\n強く感じるからでしょうか");
INSERT INTO `unit_comments` VALUES (/*id*/1805006, /*unit_id*/180531, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"わたくしの願いは、ただ一つ…\n末永く主さまの隣にいることです\nどんな困難もこの力で乗り越えて\n必ずや叶えてみせます！");
INSERT INTO `unit_comments` VALUES (/*id*/1805007, /*unit_id*/180531, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"わたくしたちの旅がどんな結末を\n迎えようとも、主さまのために\n戦います。迷いはありません\nこの力が全ての証明でございます");
INSERT INTO `unit_comments` VALUES (/*id*/1805008, /*unit_id*/180531, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"もっと強く、もっと高く…\n絆が生んだこの羽で\n主さまと未来に羽ばたきます\nいつまでも…どこまでも一緒に…");
INSERT INTO `unit_comments` VALUES (/*id*/1805009, /*unit_id*/180531, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"主さまが傷つくところを\nこれ以上見たくはありません\nいつも笑っていていただくために\nわたくしはこの力でお守りします");
INSERT INTO `unit_comments` VALUES (/*id*/1805010, /*unit_id*/180531, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"以前にも増して主さまを近くに\n感じています\n主さまも、わたくしを感じて\nおいででしょうか…？");
INSERT INTO `unit_comments` VALUES (/*id*/1805011, /*unit_id*/180501, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"わたくしは今日という日を心から\nお待ちしておりました。主さまの\nお誕生日を、全身全霊をかけて\nお祝いいたしますね");
INSERT INTO `unit_comments` VALUES (/*id*/1805012, /*unit_id*/180531, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうございます\nなにか特別にしてほしいことは\nございますか？　遠慮せずに\nなんでもお申し付けくださいまし");
INSERT INTO `unit_comments` VALUES (/*id*/1806001, /*unit_id*/180601, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/4.0, /*change_face*/1, /*description*/"あたしもなれたのね\nプリンセスフォームに…！\nねえ、見てなさいよ？\nここからのあたし、すごいから！");
INSERT INTO `unit_comments` VALUES (/*id*/1806002, /*unit_id*/180601, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/2.4, /*change_face*/2, /*description*/"あたしの魔力にびっくりした？\nしょうがないわね～\n仕方がないから\n自慢させてあげるわ！");
INSERT INTO `unit_comments` VALUES (/*id*/1806003, /*unit_id*/180601, /*use_type*/1, /*voice_id*/3, /*face_id*/4, /*change_time*/3.6, /*change_face*/1, /*description*/"プリンセスフォームって\n空を飛べるのよね？\nあんたにも付き合ってもらうわよ\n秘密の特訓！");
INSERT INTO `unit_comments` VALUES (/*id*/1806004, /*unit_id*/180601, /*use_type*/1, /*voice_id*/4, /*face_id*/5, /*change_time*/6.7, /*change_face*/1, /*description*/"あんたみたいな底抜けのお人よし\nあたしが守ってあげなきゃ\n誰が守ってくれるってのよ\nありがたく思いなさいよね♪");
INSERT INTO `unit_comments` VALUES (/*id*/1806005, /*unit_id*/180601, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/6.9, /*change_face*/2, /*description*/"この力で自分の居場所を…ううん\nみんなの居場所を守りたいわ\n…こんな風に思えるように\nなったのは、あんたのおかげかも");
INSERT INTO `unit_comments` VALUES (/*id*/1806006, /*unit_id*/180631, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"チェンジ・プリンセスフォーム！\nこの力で、あんたと一緒に\nどんな限界だって\n超えてみせるわ！");
INSERT INTO `unit_comments` VALUES (/*id*/1806007, /*unit_id*/180631, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あたしはもう迷わないし\nあきらめない！\nあんたがくれたこの力で\nどこまでもあがき続けてやるわ！");
INSERT INTO `unit_comments` VALUES (/*id*/1806008, /*unit_id*/180631, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ふふ～ん♪\n優秀なあたしの完っ璧な大魔法で\nどんなやつだって\nぶっ飛ばしちゃうんだから！");
INSERT INTO `unit_comments` VALUES (/*id*/1806009, /*unit_id*/180631, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"プリンセスフォームって\n心の繋がり…なのよね？\nつまり、あんたとあたしの心が\n深く…って、こっち見んな！");
