INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/206500, /*unit_name*/"コマンド・ウキキー", /*prefab_id*/206500, /*motion_type*/0, /*se_type*/0, /*move_speed*/300, /*search_area_width*/100, /*atk_type*/1, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【物理】近接攻撃\nウキキーがより成長した姿。幼少より大岩を投げ\n続けたその太腕は、絶大な打撃を繰り出す。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/206700, /*unit_name*/"ヒヨリのシャドウ", /*prefab_id*/206700, /*motion_type*/0, /*se_type*/1, /*move_speed*/270, /*search_area_width*/200, /*atk_type*/1, /*normal_atk_cast_time*/2.295, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"ヒヨリの姿をした特殊な魔物\n\n・目の前の敵１キャラに物理大ダメージを与え、\n　その周りの狭い範囲の敵すべてにも\n　物理中ダメージを与える。\n・目の前の敵１キャラに物理中ダメージを\n　与える。\n・目の前の敵１キャラに物理小ダメージを与え、\n　さらに自分の物理攻撃力を中アップさせる。");
