INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301201, /*unit_name*/"クリスタルゴーレム", /*prefab_id*/301201, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.3, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【物理】ルナの魔力の結晶でその身を覆う巨大ゴーレム。\n身に纏う結晶は堅牢な盾であり、全てを穿つ矛となる。\n・クリスタルゴーレムが毒、猛毒、呪い、火傷の\n　効果を受けると、行動速度が大ダウンする。\n・自身のデバフ効果をすべて解除し、\n　物理防御力と魔法防御力を大アップする。\n　さらに、自分以外の味方の物理防御力と\n　魔法防御力を中アップさせる。\n・敵全体に物理中ダメージを与え、スタンさせる。\n・前方範囲内の敵に物理大ダメージを与え、\n　味方全体の物理攻撃力と魔法攻撃力を\n　小アップさせる。\n　さらに、自身のＴＰを中回復する。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301202, /*unit_name*/"アイスゴーレム", /*prefab_id*/301202, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.3, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【物理】凍てつく大地の洞窟に\n潜む巨大ゴーレム。身に纏う\n結晶は堅牢な盾にも矛にもなる。\n\n・アイスゴーレムが毒、猛毒、呪\n　い、火傷の効果を受けると、行\n　動速度が大ダウンする。\n・自身のデバフ効果をすべて解除\n　し、物理防御力と魔法防御力を\n　中アップする。\n・敵全体に物理中ダメージを与え\n　る。\n・前方範囲内の敵に物理大ダメー\n　ジを与える。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301300, /*unit_name*/"グラットン", /*prefab_id*/301300, /*motion_type*/0, /*se_type*/4, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/3.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"暴食の凶獣として悪名轟く、天秤座の魔星獣。\n奪った魂の善悪をはかり、味の違いを愉しむ。\n\n・敵からの攻撃による麻痺、スタン、束縛、\n　行動速度低下効果への耐性を持たない。\n・敵全体に魔法大ダメージを与え、さらに自\n　身の物理攻撃力と魔法攻撃力を特大アップ。\n・後方範囲内の敵に魔法中ダメージ。\n・前方範囲内の敵に魔法中ダメージを与え、Ｔ\n　Ｐを小ダウン。さらに自身のＴＰを中アップ\n　させる。");
