INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303100, /*unit_name*/"トウミングマ", /*prefab_id*/303100, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/3.5, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【物理】氷穴最奥に引き篭り\n生涯冬眠すると決めた巨大熊。\n長い歳月を夢うつつの中で過ごし\n獲物を狩るその一瞬、覚醒する。\n\n・敵全体に魔法大ダメージを与え、\n　物理攻撃力を下げる。\n\n・前方3キャラに物理小ダメージを\n　与える。さらに、前方1キャラを\n　大きく吹き飛ばし、氷結状態\n　にして移動速度を下げる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303200, /*unit_name*/"マダムピック", /*prefab_id*/303200, /*motion_type*/0, /*se_type*/6, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【物理】吹雪と共に現れ、\n縄張りを荒らす者には\n大群を呼び寄せ襲い掛かる\n雪国イチのゴージャスマダム。\n\n・前方4キャラに魔法中ダメージを\n　与える。\n\n・ランダムな2キャラに魔法\n　小ダメージを与え、\n　凍結状態にする。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303300, /*unit_name*/"ガーゴイル", /*prefab_id*/303300, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.1, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【物理】朽ちた遺跡に残り\n主亡き地を護る、謎の鳥像。\n街も命令も、全て砂と消えた今は\n誰彼構わず炎を吐き襲い掛かる。\n\n・前方5キャラに魔法中ダメージを\n　与え、火傷状態にする。さらに、\n　魔法防御力を下げる。\n\n・ランダムな3キャラに\n　魔法小ダメージを与え、\n　敵全体を暗闇状態にする。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303301, /*unit_name*/"ダークガーゴイル", /*prefab_id*/303301, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.1, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【物理】暴食の君主に仕える、近衛の黒鉄像。\n冥府を彷徨いし魂を巧みに操り、主に捧げる。\n\n・ユニオンバーストは、敵全体に魔法中ダメージ\n　を与え、火傷状態にし、さらに魔法防御力を中\n　ダウン。\n　このスキルによるダメージでは、敵のＴＰが回\n　復しない。\n・敵全体に魔法中ダメージを与え、物理攻撃力を\n　大ダウン、魔法攻撃力を中ダウン。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303302, /*unit_name*/"ダークガーゴイル", /*prefab_id*/303302, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.1, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【物理】暴食の君主に仕える、近衛の黒鉄像。\n冥府を彷徨いし魂を巧みに操り、主に捧げる。\n\n・ユニオンバーストは、敵全体に魔法中ダメージ\n　を与え、火傷状態にし、さらに魔法防御力を中\n　ダウン。\n　このスキルによるダメージでは、敵のＴＰが回\n　復しない。\n・敵全体に魔法中ダメージを与え、物理攻撃力と\n　魔法攻撃力を中ダウン。\n　キャラのＨＰが80%以下の場合、ノックバッ\n　クさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303303, /*unit_name*/"サンドガーゴイル", /*prefab_id*/303303, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.1, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【物理】塔の魔力を帯び命を宿した砂岩像の魔物。\n砂塵舞う地で敵を待ち、灼炎を吐いて襲い掛かる。\n\n・敵全体に魔法大ダメージを与え、火傷状態に\n　する。さらに、魔法防御力を中ダウンさせる。\n・前方３キャラに魔法中ダメージを与え、敵全体\n　を暗闇状態にする。\n・目の前の敵１キャラに物理大ダメージを与える。\n　さらに、敵全体のうち火傷状態の敵には魔法\n　大ダメージを与える。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303400, /*unit_name*/"スナノヅチ", /*prefab_id*/303400, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.9, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【物理】生存競争激しい砂漠の\n頂点に君臨する、巨大トカゲ。\n毒を使い、砂を活かす戦法は、\n過酷な環境で得たサバイバル術。\n\n・敵全体に物理中ダメージを与え、\n　行動速度を下げる。\n　さらに、味方全体の物理攻撃力を\n　上げる。\n\n・前方1キャラを中心にした小範囲\n　に物理小ダメージを与え、毒状態\n　にする。さらに、物理防御力を\n　下げる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303401, /*unit_name*/"イワノヅチ", /*prefab_id*/303401, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.9, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【物理】ルナの魔力を帯び変異した巨大トカゲ。\n硬い棘状のうろこが、敵の体力と鎧を削り落とす。\n\n・敵全体に物理大ダメージを与え、さらにＴＰを\n　下げる。\n・前方2キャラに物理中ダメージを与え、毒状態\n　にする。\n・前方3キャラに物理中ダメージを与える。\n　さらに前方1キャラに物理大ダメージを与え、\n　物理防御力と魔法防御力を中ダウンさせる。");
