INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303500, /*unit_name*/"ムシュフシュ", /*prefab_id*/303500, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【物理】吸い続けた他者の\n魔力で、妖術を体得した魔物。\n敵に付けた傷から魔力を奪い、\nさらなる力を得ようとする。\n\n・敵全体に魔法中ダメージを与え、\n　魔法攻撃をする敵すべての\n　ＴＰを下げる。\n\n・前方1キャラを中心にした小範囲\n　に魔法小ダメージを与え、\n　魔法防御力を下げる。");
