INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/306400, /*unit_name*/"ワイルドクロウ", /*prefab_id*/306400, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/3.5, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【物理】荒れ山の捕食者として君臨\nする、凶悪な怪鳥獣。\n狩りを続けることでつくりあげた\n膂力と剛爪が、侵入者をつけ狙う。\n\n・敵全体に物理大ダメージを与え、\n　ＴＰ上昇をダウンさせる。\n　発動するたびにダメージがアップ\n　する。この効果は最大３回まで\n　累積する。\n\n・後ろから２番目の敵を中心にした\n　小範囲に物理小ダメージを与え、\n　麻痺状態にする。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/306500, /*unit_name*/"トライバルオーク", /*prefab_id*/306500, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/0.9, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【物理】辺境の森のオークたちの\n首魁。独自の民族性の奇怪な\nリズムを撒き散らす。その怪音は、\n敵対者を混迷の渦に突き落とす。\n\n・敵全体に物理大ダメージを与え、\n　物理防御力と魔法防御力をダウン\n　さらに自身の物理攻撃力と\n　魔法攻撃力をアップする。\n\n・前方４キャラに魔法小ダメージを\n　与え、敵のＨＰが70％以下の\n　場合、混乱状態にする。");
