INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/308204, /*unit_name*/"オオドクマンドラゴラ", /*prefab_id*/308204, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【MODE2】猛毒の胞子が降り注ぎ、大地は\n荒野と化す。\n数多の骸を苗床に、猛毒の大輪が鮮やかに\n咲き誇る。\n\n下記の行動を順番に繰り返す。\n・地面に埋まっている間、ダメージを受け付け\n　ない状態になる。\n・敵全体を強力な毒状態にする。\n・自身にかかっているステータスダウン効果を\n　解除したあと、自身の物理防御力と魔法防御\n　力を極大ダウン。");
