INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/309306, /*unit_name*/"トナカイ・ヴェンデッタ", /*prefab_id*/309300, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【ＭＯＤＥ３】手負いの巨獣が、反撃の狼煙を\n上げる。憎悪と憤怒に身を落とし、残虐の限りを\n尽くす。\n\n・ユニオンバーストは、召喚キャラを消滅させた\n　あと、敵全体にキャラの最大ＨＰに応じた\n　物理割合ダメージを与える。\n　このダメージを受けたあとにＨＰが35％を\n　切ったキャラを一定時間トナカイに変え、\n　さらに物理クリティカルと魔法クリティカルを\n　下げる。トナカイになったキャラは、通常攻撃\n　しか使用できなくなる。\n　一定時間、自身の物理防御力を大きく下げる。\n・一番前のキャラを中心として、一定範囲に\n　キャラの最大ＨＰに応じた物理割合ダメージを\n　与える。さらに自身の物理攻撃力をアップ\n　させる。\n・一番前のキャラに物理ダメージを与えて\n　ノックバックさせ、さらにスタンさせる。\n・バトル開始直後に、さらに攻撃が激しく\n　凶暴になる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/309400, /*unit_name*/"サーヴァント・ブレド（物理）", /*prefab_id*/309400, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【物理】覇瞳皇帝が生み出した動く力の貯蔵庫。\n膂力の権能を司り、極大の剣戟で全てを斬破する。\n\n・スタンと吹き飛ばし効果が有効。\n\n・ユニオンバーストは、敵全体にキャラの\n　最大ＨＰに応じた物理割合ダメージを与える。\n\n・目の前の敵を中心に、範囲内の敵すべてに\n　キャラの最大ＨＰに応じた物理割合\n　ダメージを与える。\n\n・サーヴァント・ブレド（物理）自身の\n　ＨＰが40％を切ると、さらに攻撃が\n　激しく凶暴になる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/309500, /*unit_name*/"サーヴァント・マギ（魔法）", /*prefab_id*/309500, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/2, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【魔法】覇瞳皇帝が生み出した動く力の貯蔵庫。\n魔道の権能を司り、無尽の魔力で全てを滅却する。\n\n・ユニオンバーストは、敵全体にキャラの\n　最大ＨＰに応じた魔法割合ダメージを与える。\n　さらに、物理攻撃力と魔法攻撃力を小ダウン。\n\n・ランダムな１キャラに魔法ダメージを与え、\n　さらに呪い、麻痺状態にしてＴＰを大ダウン。\n\n・サーヴァント・マギ（魔法）自身の\n　ＨＰが40％を切ると、さらに攻撃が\n　激しく凶暴になる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/309600, /*unit_name*/"サーヴァント・ロクス（複部位）", /*prefab_id*/309600, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【物理】覇瞳皇帝が生み出した動く力の貯蔵庫。\n双肩の権能を司り、広闊たる領域で全てを屠る。\n\n・ユニオンバーストは、目の前の敵を中心に、\n　範囲内の敵すべてにキャラの最大ＨＰに\n　応じた物理割合ダメージを与え、さらに\n　目の前の敵に物理小ダメージを与える。\n\n・目の前の敵を中心にキャラの\n　最大ＨＰに応じた物理割合ダメージを\n　与える。\n\n・サーヴァント・ロクス（複部位）自身の\n　ＨＰが40％を切ると、さらに防御力が\n　上がり頑強になる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/309700, /*unit_name*/"サーヴァント・ウォル（防衛）", /*prefab_id*/309700, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/2, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【魔法】覇瞳皇帝が生み出した動く力の貯蔵庫。\n守護の権能を司り、不侵の壁で全てを拒絶する。\n\n・ユニオンバーストは、自身の物理防御力、\n　魔法防御力を大アップ。\n\n・目の前の敵を中心に、範囲内の敵すべてに\n　魔法ダメージ。さらに、自身の物理防御力、\n　魔法防御力を小アップ。\n\n・サーヴァント・ウォル（防衛）自身の\n　ＨＰが40％を切ると、さらに防御力が\n　上がり頑強になる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/309800, /*unit_name*/"覇瞳皇帝", /*prefab_id*/309800, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/3.6, /*cutin*/309800, /*cutin_star6*/309800, /*visual_change_flag*/0, /*comment*/"王都終末決戦_ベースユニット");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/309801, /*unit_name*/"覇瞳皇帝", /*prefab_id*/309801, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.4, /*cutin*/309800, /*cutin_star6*/309800, /*visual_change_flag*/0, /*comment*/"【MODE１】全てを、拒絶する悲しき王女。\n自らを神とし、禍禍しき力にその身をのまれる。\nそして狂乱の刃をアストルムの全てに向ける。\n欺瞞の王女を守護する壁を、数多の刃で打ち破れ。\n\n・バリアを張り、敵の攻撃を受け付けなくなる。\n　バリアはダメージ量に関係なく、攻撃を\n　命中させるたびに徐々に耐久性を失っていき、\n　280回攻撃を命中させるとバリアは破壊\n　される。\n\n・ユニオンバーストは、敵全体に残りＨＰに\n　応じた割合特大ダメージを与える。\n\n・敵全体に、物理小ダメージと魔法\n　小ダメージを与える。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/309802, /*unit_name*/"覇瞳皇帝", /*prefab_id*/309800, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/309800, /*cutin_star6*/309800, /*visual_change_flag*/0, /*comment*/"【MODE２】小さき者の反抗に僅かな焦燥を見せる。\n希望を抱き、対向する全てに自らの理を科す。\n悲憤の剣を突き立て、その魂を塵芥へと挫く。\n仮初の神の放つ黒刃に、正しき魔法を以て\n対抗せよ。\n\n・ユニオンバーストは、敵全体に残りＨＰに\n　応じた割合特大ダメージを与える。\n\n・目の前の敵１キャラに物理特大ダメージを与え、\n　さらにその周囲の敵すべてに魔法中ダメージを\n　与え、気絶させる。気絶したキャラは、\n　次に攻撃を受けるまで行動不能になる。\n\n・敵全体に、物理小ダメージと魔法\n　小ダメージを与える。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/309803, /*unit_name*/"覇瞳皇帝", /*prefab_id*/309800, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/309800, /*cutin_star6*/309800, /*visual_change_flag*/0, /*comment*/"【MODE３】世界を創りし神の、最期の叫声。\n怨嗟が放つ拒絶の言の葉は呪詛となり、\n躰を、精神を、魂を削りすべてを闇に帰す。\n遂に決着の時。皆、剣を取りその手で明日を\n切り拓け。\n\n・ユニオンバーストは、敵全体に残りＨＰに\n　応じた割合特大ダメージを与える。\n\n・目の前の敵１キャラに物理特大ダメージを与え、\n　さらにその周囲の敵すべてに魔法中ダメージを\n　与え、気絶させる。気絶したキャラは、\n　次に攻撃を受けるまで行動不能になる。\n\n・敵全体に、物理小ダメージと魔法\n　小ダメージを与える。\n\n・最も後ろの敵１キャラを中心に、物理防御力と\n　魔法防御力を中ダウンさせるフィールドを\n　展開し、さらに魔法小ダメージを与え、\n　呪詛を付与する。呪詛による継続ダメージは、\n　毎秒ごとに増加する。\n\n・バトル開始直後に、自分の行動速度を\n　大アップさせ、さらに魔法防御力を\n　小アップさせる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/309804, /*unit_name*/"覇瞳皇帝", /*prefab_id*/309804, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/309800, /*cutin_star6*/309800, /*visual_change_flag*/0, /*comment*/"マップ表示用");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/309900, /*unit_name*/"ゴブリンメイスター", /*prefab_id*/309900, /*motion_type*/0, /*se_type*/17, /*move_speed*/270, /*search_area_width*/1100, /*atk_type*/2, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【魔法】塔の魔力を我が物に画策する小鬼学者。\n禁術で土塊の人形を操り、全ての自由を強奪する。\n\n・敵全体に魔法大ダメージを与え、\n　すべての召喚体、分身を即死させる。\n・前方広範囲に残りＨＰに応じた割合物理\n　中ダメージを与え、大きく吹き飛ばす。\n　さらに、泥人形を１体召喚する。\n　泥人形は最前衛または前から２番目のキャラに\n　物理ダメージを与え、行動速度を下げる。\n・前から２番目のキャラを中心とした小範囲に\n　物理中ダメージと猛毒、スタンを与える。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/310000, /*unit_name*/"山神様", /*prefab_id*/310000, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【物理】駆走る神と伝承される山の土着神。\n太古より好敵手を求め大地を雄々しく競奔する。\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与え、\n　ノックバックさせる。\n　先頭のキャラにはさらに追加で物理\n　特大ダメージを与え、大きく吹き飛ばして\n　スタンさせる。\n　山神様がユニオンバーストを発動するたび\n　自身の物理防御力がダウンする。\n　この効果は累積する。\n・魔法攻撃力の最も高い１キャラに、キャラの\n　最大ＨＰに応じた魔法割合ダメージを与え、\n　ＴＰを大きくダウンさせる。\n・自身のＴＰと行動速度をアップさせる。\n・山神様自身のＨＰが55％を切ると、さらに\n　攻撃が激しく凶暴になる代わりに\n　自身の物理防御力がダウンする。");
