INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/310005, /*unit_name*/"山神様", /*prefab_id*/310000, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【ＭＯＤＥ２】益荒男の疾駆に大地が揺れる。\nその様を祝賀するが如く、目出鯛は巌山を舞う。\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与え、\n　ノックバックさせる。\n　先頭のキャラにはさらに追加で物理特大\n　ダメージを与え、大きく吹き飛ばして\n　スタンさせる。\n　山神様がユニオンバーストを発動するたび\n　自身の物理防御力がダウンする。\n　この効果は累積する。\n・魔法攻撃力の最も高い２キャラに、キャラの\n　最大ＨＰに応じた魔法割合ダメージを与え、\n　ＴＰを大きくダウンさせる。\n・後方に物理防御力ダウンフィールドを展開し、\n　さらに敵全体に物理中ダメージを与える。\n　目出鯛が背中を離れている間は、山神様の\n　魔法防御力が特大ダウン。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/310006, /*unit_name*/"山神様", /*prefab_id*/310000, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【ＭＯＤＥ３】神と競いし強者への、終の試煉。\n互いの全てを以て、想い人へ駆け抜ける。\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与え、\n　ノックバックさせる。\n　先頭のキャラにはさらに追加で物理特大\n　ダメージを与え、大きく吹き飛ばして\n　スタンさせる。\n　山神様がユニオンバーストを発動するたび\n　自身の物理防御力がダウンする。\n　この効果は累積する。\n・魔法攻撃力の最も高い１キャラに、キャラの\n　最大ＨＰに応じた魔法割合ダメージを与え、\n　ＴＰを大きくダウンさせる。\n・自身のＴＰと行動速度をアップさせる。\n・バトル開始直後に、さらに攻撃が激しく\n　凶暴になる代わりに、自身の物理防御力が\n　ダウンする。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/310100, /*unit_name*/"ラースドラゴン", /*prefab_id*/310100, /*motion_type*/0, /*se_type*/14, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.6, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【物理】忘却の秘境に、住まう若き巨竜。\n禁忌に踏み入る冒険者を灼熱の業火で焼き払う。\n\n・ユニオンバーストは敵全体に物理割合ダメージ\n　を与え、さらにＴＰをダウンさせる。\n・バトル開始時に、自身のＴＰを特大ダウン。\n・前方1キャラを中心に一定範囲内のキャラに\n　魔法ダメージを与え、さらに物理防御力を\n　ダウンさせる。\n・敵全体に物理ダメージを与え、さらに火傷\n　状態にする。\n・敵全体に物理ダメージを与え、ノックバック\n　させる、さらに、ステータスアップ効果を\n　打ち消す。\n\n・全ターゲットがブレイクされると、自身のＴＰ\n　を特大ダウン。\n・ラースドラゴン自身の残りＨＰが10％を切る\n　とさらに攻撃が激しく強暴になり、ブレイク\n　されなくなる。\n・バトルがタイムアップすると、敵全体を戦闘\n　不能にする。\n");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/310200, /*unit_name*/"シャークヘッドレイ", /*prefab_id*/310200, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【物理】海蝕洞の支配者として\n君臨する、サメエイの魔物。凶猛な\n欲望を全て捕食に注ぐ海の大悪党。\n\n・敵全体に物理特大ダメージを与え\n　物理防御力と魔法攻撃力を\n　ダウンさせる。\n\n・前方範囲内の敵すべてに\n　魔法小ダメージを与え、ＴＰと\n　ＴＰ上昇率をダウンさせる。");
