INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311104, /*unit_name*/"黒の王ジャバウォック", /*prefab_id*/311101, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.1, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【ＭＯＤＥ１】瘴黒の幕を身に纏い、黒の王が再\n臨する。闇と混沌を集約した障壁は、救世主の光\nの一射をも耐えるという。\n\n・バリアを張り、敵の攻撃を受け付けなくなる。\n　バリアはダメージを与えるたびに徐々に耐久性\n　を失っていき、6500000ダメージを与え\n　るとバリアは破壊される。\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、毒状態に\n　し、さらにノックバックさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・前方範囲内のキャラに魔法中ダメージを与え、\n　魔法防御力を大ダウンさせる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311105, /*unit_name*/"黒の王ジャバウォック", /*prefab_id*/311100, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.1, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【ＭＯＤＥ２】リノ、アユミ、ヒヨリのシャドウ\nを召喚。心の闇が具現化し、自己の弱さを露見さ\nせる。希望の心を取り戻せるか？今、問われる。\n\n・目の前の敵１キャラに物理ダメージを与える。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311106, /*unit_name*/"黒の王ジャバウォック", /*prefab_id*/311100, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.1, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【ＭＯＤＥ３】黒の王が終の力を解放し、世界は\n常闇へと堕ちていく。本の殆どの頁は黒に染まり\n救世主の物語は終焉を迎えようとする。\n\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、毒状態に\n　し、さらにノックバックさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・前から２キャラに物理中ダメージを与え、物理\n　防御力とＴＰをダウンさせる。\n・前方範囲内のキャラに魔法中ダメージを与え、\n　魔法防御力を大ダウンさせる。\n　さらにキャラのＨＰが50％以下の場合、行動\n　速度をダウンさせる。\n・黒の王ジャバウォック自身のＨＰが40％を切\n　ると、さらに攻撃が激しく凶暴になる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311200, /*unit_name*/"フラワーシュバリエ", /*prefab_id*/311200, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/2.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【物理】大輪の花に擬態する魔物。\nその美しさに惹かれた、不用意な\n獲物の前に絶望の光が花開く。\n\n・前方３キャラに物理大ダメージを\n　与え、さらに敵全体に物理大\n　ダメージを与える。\n\n・前方２キャラに物理小ダメージを\n　与え、さらに自分の物理攻撃力を\n　中アップさせる。\n\n・前から１番目のキャラに物理中\n　ダメージを与え、物理防御力を\n　中ダウンさせる。\n\n・前から２番目のキャラに物理中\n　ダメージを与え、物理防御力を\n　中ダウンさせる。");
