INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311501, /*unit_name*/"暴走のイノセントボウ", /*prefab_id*/311500, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/2.4, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【魔法】過大な無垢なる願いにより暴走した聖弓。\nその矢に貫かれると、非力なエンジェルと化す。\n\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、エンジェ\n　ル状態のキャラに呪いを付与する。\n　この効果により付与された呪いのダメージでは、\n　ＴＰが回復しない。\n・ＨＰが低い３キャラに魔法割合ダメージを与え、\n　さらにＨＰが60％を切ったキャラを一定時間\n　エンジェル状態にする。\n　エンジェルになったキャラは、通常攻撃しか使\n　用できなくなり、かかっているステータスアッ\n　プ効果を解除される。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311502, /*unit_name*/"暴走のイノセントボウ", /*prefab_id*/311500, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/2.4, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【魔法】過大な無垢なる願いにより暴走した聖弓。\nその矢に貫かれると、非力なエンジェルと化す。\n\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、エンジェ\n　ル状態のキャラに呪いを付与する。\n　この効果により付与された呪いのダメージでは、\n　ＴＰが回復しない。\n・ＨＰが低い３キャラに魔法割合ダメージを与え、\n　さらにＨＰが60％を切ったキャラを一定時間\n　エンジェル状態にする。\n　エンジェルになったキャラは、通常攻撃しか使\n　用できなくなり、かかっているステータスアッ\n　プ効果を解除される。\n・ＨＰが高い２キャラに魔法中ダメージを与え、\n　行動速度をダウンさせる。\n・イノセントボウ自身のＨＰが50％を切ると、\n　魔法攻撃力がアップし、さらに攻撃が激しく凶\n　暴になる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311503, /*unit_name*/"暴走のイノセントボウ", /*prefab_id*/311500, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/2.4, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【魔法】過大な無垢なる願いにより暴走した聖弓。\nその矢に貫かれると、非力なエンジェルと化す。\n\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、エンジェ\n　ル状態のキャラに呪いを付与する。\n　この効果により付与された呪いのダメージでは、\n　ＴＰが回復しない。\n・ＨＰが低い３キャラに魔法割合ダメージを与え、\n　さらにＨＰが60％を切ったキャラを一定時間\n　エンジェル状態にする。\n　エンジェルになったキャラは、通常攻撃しか使\n　用できなくなり、かかっているステータスアッ\n　プ効果を解除される。\n・ＨＰが高い２キャラに魔法中ダメージを与え、\n　行動速度をダウンさせる。\n・イノセントボウ自身のＨＰが50％を切ると、\n　魔法攻撃力とＴＰがアップし、さらに攻撃が激\n　しく凶暴になる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311504, /*unit_name*/"暴走のイノセントボウ", /*prefab_id*/311500, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/2.4, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【ＭＯＤＥ１】歪みし願いは結晶となり、人々を\n狂わせる。その結晶はすべての進攻を封殺する。\n\n・バトル開始時に歪願のクリスタルを呼び出す。\n・歪願のクリスタルがバトル中に存在している間、\n　イノセントボウの物理防御力と魔法防御力を大\n　幅にアップさせる。\n・歪願のクリスタルが破壊されると、イノセント\n　ボウの物理防御力と魔法防御力を大幅にダウン\n　させる。\n・ＨＰが高い２キャラに魔法中ダメージを与え、\n　行動速度をダウンさせる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311505, /*unit_name*/"暴走のイノセントボウ", /*prefab_id*/311500, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/2.4, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【ＭＯＤＥ２】ヨリとアカリのシャドウを召喚。\n自分たちの負の姿に、双子の真の絆が試される。\n\n・ＨＰが低い３キャラに魔法割合ダメージを与え、\n　さらにＨＰが60％を切ったキャラを一定時間\n　エンジェル状態にする。\n　エンジェルになったキャラは、通常攻撃しか使\n　用できなくなり、かかっているステータスアッ\n　プ効果を解除される。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311506, /*unit_name*/"暴走のイノセントボウ", /*prefab_id*/311500, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/2.4, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【ＭＯＤＥ３】叶わぬ願いに囚われ、聖弓の権能\nは極致に至る。解放された力は天地を揺るがし、\n一切の邪念を滅却する。\n\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、エンジェ\n　ル状態のキャラに呪いを付与する。\n　この効果により付与された呪いのダメージでは、\n　ＴＰが回復しない。\n・ＨＰが低い３キャラに魔法割合ダメージを与え、\n　さらにＨＰが60％を切ったキャラを一定時間\n　エンジェル状態にする。\n　エンジェルになったキャラは、通常攻撃しか使\n　用できなくなり、かかっているステータスアッ\n　プ効果を解除される。\n・ＨＰが高い２キャラに魔法中ダメージを与え、\n　行動速度をダウンさせ、さらに魔法防御力を\n　ダウンさせる。\n・残り時間が35秒を切ると、魔法攻撃力とＴＰ\n　がアップし、さらに攻撃が激しく凶暴になる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311600, /*unit_name*/"叫怖のゴーストファーザー", /*prefab_id*/311600, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/4.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【魔法】人間を恐怖に慄かせるお化けたちの首領。\n手下をけしかけ、シマの拡大を密かに画策する。\n\n・ユニオンバーストは、敵全体にキャラの元々の\n　最大ＨＰに応じた魔法割合ダメージを与える。\n　さらに、敵全体のキャラの最大ＨＰを10％減\n　少させる。減少したＨＰは、バトル終了まで解\n　除できない。");
