INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311604, /*unit_name*/"叫怖のゴーストファーザー", /*prefab_id*/311600, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/4.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【ＭＯＤＥ１】美味な魂を求め、お化けたちの首\n領が再び姿を現す。残虐なファミリーを従え、\n人々を蹂躙することで街を極上の悲鳴で満たす。\n\n・バトル開始時、ゴーストミニオンズを３体召喚\n　する。\n・ゴーストミニオンズを１体倒すたびに、残り\n　ゴーストミニオンズ数が１減少する。\n　その後、ゴーストミニオンズが再度召喚される。\n・ゴーストミニオンズは、目の前の敵１キャラの\n　ＴＰを減少させ、ゴーストファーザーのＴＰを\n　回復させる。\n・ユニオンバーストは、敵全体にキャラの元々の\n　最大ＨＰに応じた魔法割合ダメージを与える。\n　さらに、敵全体のキャラの最大ＨＰを10％減\n　少させる。減少したＨＰは、バトル終了まで解\n　除できない。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311605, /*unit_name*/"叫怖のゴーストファーザー", /*prefab_id*/311600, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/999.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【ＭＯＤＥ２】シマを荒す、すべてに制裁を下す。\nひとたび彼のシマに踏み入れば、はびこる魍魎が\n命を蝕み、生きながら地獄の苦しみを与える。\n\n・バトル開始時、キャラの最大ＨＰが35％減少\n　する。減少したＨＰは、バトル終了まで解除\n　できない。\n・ユニオンバーストは、敵全体にキャラの元々の\n　最大ＨＰに応じた魔法割合ダメージを与える。\n　さらに、敵全体のキャラの最大ＨＰを10％減\n　少させる。減少したＨＰは、バトル終了まで解\n　除できない。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311606, /*unit_name*/"叫怖のゴーストファーザー", /*prefab_id*/311600, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/4.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【ＭＯＤＥ３】蓄えた力をすべて吐き出し、真の\n力を解放する。この世界を支配すべく、残虐非道\nの限りを尽くし、地獄へと染め上げる。\n\n・ユニオンバーストは、敵全体にキャラの元々の\n　最大ＨＰに応じた魔法割合ダメージを与える。\n　さらに、敵全体のキャラの最大ＨＰを10％減\n　少させる。減少したＨＰは、バトル終了まで解\n　除できない。\n・自身に物理/魔法無効バリアを展開する。\n・ゴーストファーザー自身のＨＰが37.5％を\n　切ると、さらに攻撃が激しく凶暴になる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311700, /*unit_name*/"ゴーストブラザーズ", /*prefab_id*/311700, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/999.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"ゴーストファーザーの側近の手下。\n自身の体を盾にして、一切の攻撃から首領を守る。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311701, /*unit_name*/"ゴーストブラザーズ", /*prefab_id*/311700, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/999.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"ゴーストファーザーの側近の手下。\n鍛え上げた鉄砲玉で、街に特攻をしかける。血気\n盛んに突撃し、逃げ惑う人々を恐怖の底に陥れる。\n\n・ゴーストブラザーズは攻撃の対象にならない。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311702, /*unit_name*/"ゴーストブラザーズ", /*prefab_id*/311700, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/999.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"ゴーストファーザーの側近の手下。\n鉄砲玉たちと集めた上質な精気を首領へ献上する。\n\n・ゴーストブラザーズは攻撃の対象にならない。\n・ゴーストファーザーのＴＰを回復させる。\n　ゴーストファーザーが行動不能状態のときは、\n　ＴＰを回復しない。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311703, /*unit_name*/"ゴーストブラザーズ", /*prefab_id*/311700, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/999.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"いよいよ訪れた出番に、鼻息荒く推参する。\n気合十分の最強の盾が、歯向かう相手の心を折る。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311800, /*unit_name*/"ゴーストブラザーズ", /*prefab_id*/311800, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/999.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"ゴーストファーザーの側近の手下。\n取り立てた精気を首領へ渡し、補佐を務めあげる。\n\n・ゴーストファーザーのＴＰを回復させる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311801, /*unit_name*/"ゴーストブラザーズ", /*prefab_id*/311800, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/999.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"ゴーストファーザーの側近の手下。\n首領の近辺を警護し、一分の隙も見せない。裏で\nは密かに街の人々を絶望へ追い込もうと暗躍する。\n\n・ゴーストブラザーズは攻撃の対象にならない。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311802, /*unit_name*/"ゴーストブラザーズ", /*prefab_id*/311800, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/999.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"ゴーストファーザーの側近の手下。\n部下と取り立てた極上の精気を首領へ差し出す。\n\n・ゴーストブラザーズは攻撃の対象にならない。\n・ゴーストファーザーのＴＰを回復させる。\n　ゴーストファーザーが行動不能状態のときは、\n　ＴＰを回復しない。");
