INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/312804, /*unit_name*/"魔物の姫", /*prefab_id*/312800, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【ＭＯＤＥ１】高潔なる姫君は従兵を以て対敵を\n拒絶する。さながら内なる嘆きを虚飾するが如く。\n\n・ユニオンバーストは、前方２キャラに最大ＨＰ\n　に応じた物理割合ダメージを与える。\n・目の前の敵１キャラに物理ダメージを与える。\n　この攻撃は物理攻撃力アップ・ダウン効果の\n　影響を受けやすい。\n・前方３キャラに最大ＨＰに応じた物理割合\n　ダメージを与える。\n　この攻撃は物理攻撃力アップ・ダウン効果の\n　影響を受けやすい。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/312805, /*unit_name*/"魔物の姫", /*prefab_id*/312800, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【ＭＯＤＥ２】姫君は更なる力を解き放つ。\n歪曲した強き恋慕で、全てを拒み屠らんとする。\n\n・敵からの攻撃による麻痺、スタン、束縛、凍結、\n　時間停止、行動速度低下効果への耐性を持たな\n　い。\n・ユニオンバーストは、前方２キャラに最大ＨＰ\n　に応じた物理割合ダメージを与える。\n・目の前の敵１キャラに物理ダメージを与える。\n　この攻撃は物理攻撃力アップ・ダウン効果の\n　影響を受けやすい。\n・前方３キャラに最大ＨＰに応じた物理割合\n　ダメージを与える。\n　この攻撃は物理攻撃力アップ・ダウン効果の\n　影響を受けやすい。\n・短時間無敵状態になり、敵全体のステータス\n　アップ効果を解除した後、一定時間バトルフ\n　ィールドを展開する。\n\n【バトルフィールド効果】\n・敵全体の物理攻撃力と魔法攻撃力を100％\n　ダウンした後、物理攻撃力と魔法攻撃力を\n　特大アップ。\n・魔物の姫の物理攻撃力を特大アップ。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/312806, /*unit_name*/"魔物の姫", /*prefab_id*/312800, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【ＭＯＤＥ３】大いなる悲哀に呼応して、姫君の\n進攻は苛烈を極める。乙女の熱情を以て、その\n悲しみに救いをもたらさん。\n\n・ユニオンバーストは、前方２キャラに最大ＨＰ\n　に応じた物理割合ダメージを与える。\n　力を溜めることに成功した場合、ダメージが\n　アップし、敵全体を畏怖状態にする。\n　畏怖状態のキャラは、一定時間攻撃およびＨＰ\n　回復のユニオンバーストとスキルの効果が\n　ダウン。\n・物理防御力と魔法防御力が大幅にダウンした後、\n　一定時間、力を溜める。\n　力が溜まると、ＴＰが特大回復する。\n　力を溜めている間に魔物の姫が1500000\n　以上のダメージを受けると、力を溜めることに\n　失敗し、ＴＰが大ダウン。 \n・目の前の敵１キャラに物理ダメージを与える。\n　この攻撃は物理攻撃力アップ・ダウン効果の\n　影響を受けやすい。\n・前方３キャラに最大ＨＰに応じた物理割合\n　ダメージを与える。\n　この攻撃は物理攻撃力アップ・ダウン効果の\n　影響を受けやすい。\n・魔物の姫自身のＨＰが25％を切ると、さらに\n　攻撃が激しく凶暴になる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/312900, /*unit_name*/"黒月の守護像", /*prefab_id*/312900, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【物理】天上の聖域を護る、黒月に祝福されし像。\n聖域に踏み入る、冒険者を常世の闇に葬り去る。\n\n・ユニオンバーストは、敵全体に黒月の守護像\n　の物理攻撃力に応じた物理割合ダメージを与え\n　る。\n・敵全体に物理大ダメージを与え、ステータス\n　アップ効果を解除する。\n・自身の物理攻撃力と魔法攻撃力を大アップさ\n　せる。白陽の守護像が討伐されている場合、\n　このスキルを使用する。\n・黒月の守護像のＨＰが50％を切ると、物理\n　攻撃力を大アップさせ、物理防御力を中アップ\n　する。\n・黒月の守護像のターゲットがブレイクしてい\n　る場合、物理防御力と魔法防御力が小ダウンす\n　る。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/313000, /*unit_name*/"白陽の守護像", /*prefab_id*/313000, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/"【魔法】天上の聖域を護る、白陽に祝福されし像。\n全てを浄化する白雷で、冒険者に審判を下す。\n\n・ユニオンバーストは、敵全体に白陽の守護像\n　の魔法攻撃力に応じた魔法割合ダメージを与え\n　る。\n・敵全体に魔法大ダメージを与え、ステータス\n　アップ効果を解除する。さらに白陽の守護像の\n　魔法攻撃力を大アップする。黒月の守護像が\n　討伐されている場合、このスキルを使用する。\n・味方全体の物理防御力と魔法防御力を中アッ\n　プさせる。\n・白陽の守護像のＨＰが50％を切ると、魔法\n　攻撃力を特大アップさせ、魔法防御力を小アッ\n　プする。\n・白陽の守護像のターゲットがブレイクしてい\n　る場合、物理防御力と魔法防御力が小ダウンす\n　る。");
