INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/203400, /*unit_name*/"ホオズキムシ", /*prefab_id*/203400, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/800, /*atk_type*/2, /*normal_atk_cast_time*/4.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【回復】単体回復\nお尻に溜まった蜜が、魔物の\n栄養源になる、不思議なホタル。");
