INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/204400, /*unit_name*/"プチデビル", /*prefab_id*/204400, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/750, /*atk_type*/2, /*normal_atk_cast_time*/3.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【魔法】中距離攻撃\n覚えたての魔法で手当り次第に\n攻撃する赤ちゃん悪魔。");
