INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/204701, /*unit_name*/"サンドボア", /*prefab_id*/204701, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/200, /*atk_type*/1, /*normal_atk_cast_time*/4.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近接攻撃\n砂を固めて鎧のように纏うのは、\n捨て身を恐れない一族の戦士の証。\nだが所詮は砂。魔法は防げない。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/204702, /*unit_name*/"ワイルドボア", /*prefab_id*/204702, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/170, /*atk_type*/1, /*normal_atk_cast_time*/4.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近接攻撃\n熱地に生きる、原始の大イノシシ。\n数多のマグマ岩を突き続けた先に、\n鎧をも砕く牙の一撃を体得した。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/204800, /*unit_name*/"ルインドール", /*prefab_id*/204800, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/150, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近接攻撃\nイクト・ガラディオンに付き従う古代の自動人形。\n意思を持たず、自身が瓦解するまで戦い続ける。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/205100, /*unit_name*/"ツチノコブラ", /*prefab_id*/205100, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/550, /*atk_type*/1, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】中距離攻撃\n物陰から飛びかかり魔力を吸って\n腹を満たすという魔物ヘビの変種。\n魔力が高いほど狙われやすい。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/205200, /*unit_name*/"タツノコナイト", /*prefab_id*/205200, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/650, /*atk_type*/1, /*normal_atk_cast_time*/2.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】中距離攻撃\n海で溺れた騎士の槍を奪い、\n縄張りを警備するタツノコの戦士。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/205300, /*unit_name*/"アイスチック", /*prefab_id*/205300, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/550, /*atk_type*/2, /*normal_atk_cast_time*/3.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【魔法】中距離攻撃\n氷を纏う尾羽根を飛ばして\n攻撃するトリの魔物。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/205400, /*unit_name*/"アイスフィッシュ", /*prefab_id*/205400, /*motion_type*/0, /*se_type*/12, /*move_speed*/270, /*search_area_width*/550, /*atk_type*/1, /*normal_atk_cast_time*/2.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近接攻撃\n氷河を削って巣を作り、近づく者は\n無差別に襲うサカナの魔物。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/205401, /*unit_name*/"マグマフィッシュ", /*prefab_id*/205401, /*motion_type*/0, /*se_type*/12, /*move_speed*/270, /*search_area_width*/550, /*atk_type*/1, /*normal_atk_cast_time*/2.4, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近接攻撃\n岩石をも噛み砕く強靭なアゴで\n辺りを喰い荒らすサカナの魔物。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/205500, /*unit_name*/"ランドスネーク", /*prefab_id*/205500, /*motion_type*/0, /*se_type*/14, /*move_speed*/270, /*search_area_width*/550, /*atk_type*/1, /*normal_atk_cast_time*/3.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】中距離攻撃\n穴を掘って獲物をじっと待ち、\n毒牙で確実に仕留めるヘビの魔物。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/205600, /*unit_name*/"クレイジードギー", /*prefab_id*/205600, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/100, /*atk_type*/1, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近接攻撃\n主人と共に生み出された、凶暴な改造犬。\n大きな頭を支えようと、必死な顔が殊更、怖い。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/205700, /*unit_name*/"ボニー", /*prefab_id*/205700, /*motion_type*/0, /*se_type*/15, /*move_speed*/270, /*search_area_width*/50, /*atk_type*/1, /*normal_atk_cast_time*/0.4, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"ヘルハウンド召喚用モンスター");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/206000, /*unit_name*/"トドドン", /*prefab_id*/206000, /*motion_type*/0, /*se_type*/1, /*move_speed*/270, /*search_area_width*/100, /*atk_type*/1, /*normal_atk_cast_time*/4.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近接攻撃\n身体も態度も巨漢な北海のドン。\n纏う厚い脂肪が敵からの物理攻撃\nを押止める。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/206100, /*unit_name*/"ワイズワンド", /*prefab_id*/206100, /*motion_type*/0, /*se_type*/7, /*move_speed*/270, /*search_area_width*/1080, /*atk_type*/2, /*normal_atk_cast_time*/4.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【魔法】遠距離攻撃\n浴びれば凍える冷気の魔法で、\n戦意を奪うオウムガイ型の魔物。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/206200, /*unit_name*/"ギャングオッター", /*prefab_id*/206200, /*motion_type*/0, /*se_type*/1, /*move_speed*/270, /*search_area_width*/650, /*atk_type*/1, /*normal_atk_cast_time*/3.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】中距離攻撃\n貝殻をぶつけて不意をつく、\n意地悪な北海のチンピラっこ。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/206300, /*unit_name*/"ルナポッド", /*prefab_id*/206300, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/1110, /*atk_type*/0, /*normal_atk_cast_time*/10.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【回復】単体ＴＰ回復\nルナの塔建造と共に造られた、エネルギー増幅器。\n漂う魔力を濃縮し、供給することで守護者を強化する。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300100, /*unit_name*/"ミノタウロス", /*prefab_id*/300100, /*motion_type*/0, /*se_type*/3, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.9, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】迷宮に巣食う、牡牛座の魔星獣。\n巨大な戦斧を振り回して、冒険者の命を奪う。\n\n・敵全体に物理大ダメージを与え、火傷状態\n　にする。\n・敵1キャラに物理大ダメージを与え、ノック\n　バックさせると同時にスタンさせる。\n・前方範囲内の敵に魔法中ダメージを与え、\n　麻痺させる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300200, /*unit_name*/"レサトパルト", /*prefab_id*/300200, /*motion_type*/0, /*se_type*/6, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"猛毒の尾棘で災厄を振りまく、蠍座の魔星獣。\n砂漠を護る神蟲が、信仰を失い邪神と化した。\n\n・敵全体に物理大ダメージを与える。\n　ボスがユニオンバーストを使用する毎に、\n　敵に与えるダメージがアップ。\n　この効果は、最大３回まで累積する。\n・前方範囲内の敵に物理中ダメージを与え、\n　毒状態にする。\n・自身にかかっているステータスダウン効果を\n　解除し、さらに自身の物理防御力をアップ。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300300, /*unit_name*/"メサルティム", /*prefab_id*/300300, /*motion_type*/0, /*se_type*/7, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/2, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【魔法】夢魔の館に住む、牡羊座の魔星獣。\n冒険者を夢世界に束縛し、魂を喰らう。\n\n・敵全体に魔法大ダメージを与え、束縛状態\n　の敵にはさらに魔法小ダメージを与える。\n・前方範囲内の敵に魔法中ダメージを与える。\n・敵1キャラを束縛状態にする。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300500, /*unit_name*/"リザードマン", /*prefab_id*/300500, /*motion_type*/0, /*se_type*/4, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】二刀を自在に操り、\n煙幕弾で相手の目を\nくらませる\nオオトカゲの戦士。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300600, /*unit_name*/"クリーパー", /*prefab_id*/300600, /*motion_type*/0, /*se_type*/2, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理/魔法】頭から放つ毒攻撃と\nツタによる範囲物理攻撃で\n近づく冒険者の体力を一気に奪う\n悪魔の花。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300601, /*unit_name*/"ニードルクリーパー", /*prefab_id*/300601, /*motion_type*/0, /*se_type*/2, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】特異な力で変質したクリーパー。\n無数のトゲを持つ蔓を巻き付け、動きを奪う。\n\n・前方範囲内の敵に物理中ダメージを与え、\n　拘束状態にする。\n・前方範囲内の敵の物理防御力を下げ、毒\n　状態にする。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300602, /*unit_name*/"ニードルクリーパー", /*prefab_id*/300602, /*motion_type*/0, /*se_type*/2, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】特異な力で変質したクリーパー。\n無数のトゲを持つ蔓を巻き付け、動きを奪う。\n\n・前方範囲内の敵に物理中ダメージを与え、\n　拘束状態にする。\n・前方範囲内の敵の物理防御力を下げ、毒\n　状態にする。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300700, /*unit_name*/"オークリーダー", /*prefab_id*/300700, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】族長として群れを鼓舞し\n大地を砕くほどの連撃で\n敵に大ダメージを与える\nオークの中のオーク。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300701, /*unit_name*/"オークチーフ", /*prefab_id*/300701, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"特異な力で変質したオークリーダー。\n巨大な盾で、攻防一体の一撃を繰り出す。\n\n・前方範囲内の敵に物理大ダメージを与え、\n　スタン状態にする。\n・前方範囲内の敵の防御力を大幅に下げる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300800, /*unit_name*/"エルダーホーン", /*prefab_id*/300800, /*motion_type*/0, /*se_type*/1, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】ひとたび暴れ出せば\n手が付けられず、その嘶きは\n天まで届くと言われる\n伝説のワイルドホーン。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300801, /*unit_name*/"スピリットホーン", /*prefab_id*/300801, /*motion_type*/0, /*se_type*/1, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】特異な力で変質したエルダーホーン。\n角に魔力を込め、打撃より強力な一撃を放つ。\n\n・前方範囲内の敵に魔法大ダメージを与え、\n　ノックバックさせると同時にスタンさせる。\n・前方範囲内の敵に魔法小ダメージを与え、\n　魔法防御と攻撃速度を小ダウンさせる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300900, /*unit_name*/"エルダータイラント", /*prefab_id*/300900, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】強力な頭突きで地を砕き\n全体に大ダメージを与える。\n身体や甲羅が巨大に進化した\n岩山のようなタートルタイラント。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301000, /*unit_name*/"シードレイク", /*prefab_id*/301000, /*motion_type*/0, /*se_type*/14, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理/魔法】強力な氷のブレスで\n前衛を一瞬で氷漬けにし、\n咬みついて身体を砕く\n獰猛な深海竜。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301100, /*unit_name*/"サベージベア", /*prefab_id*/301100, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.7, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】体格にそぐわぬ俊敏さと\n咬みつき攻撃で体力を奪い、\n巨大なツメで敵を引き裂く\n残虐非道な森の支配者。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301101, /*unit_name*/"マッドベア", /*prefab_id*/301101, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"特異な力で変質したサベージベア。\n怒りを込めた剛腕は、敵ごと地面を引き裂く。\n\n・前方範囲内の敵に物理大ダメージを与え、\n　スタン状態にする。\n・前方の敵1キャラに物理小ダメージを与え、\n　物理攻撃力を中ダウンさせる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301200, /*unit_name*/"グレーターゴーレム", /*prefab_id*/301200, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】際限なくゴーレムを召喚し\n岩の針で体力と精神を同時に削る\n無数のゴーレムの残骸を\n魔力で再構築した巨大なゴーレム。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301300, /*unit_name*/"グラットン", /*prefab_id*/301300, /*motion_type*/0, /*se_type*/4, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/3.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"暴食の凶獣として悪名轟く、天秤座の魔星獣。\n奪った魂の善悪をはかり、味の違いを愉しむ。\n\n・敵からの攻撃による麻痺、スタン、束縛、\n　行動速度低下効果への耐性を持たない。\n・敵全体に魔法大ダメージを与え、さらに自\n　身の物理攻撃力と魔法攻撃力を特大アップ。\n・後方範囲内の敵に魔法中ダメージ。\n・前方範囲内の敵に魔法中ダメージを与え、Ｔ\n　Ｐを小ダウン。さらに自身のＴＰを中アップ\n　させる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301400, /*unit_name*/"メデューサ", /*prefab_id*/301400, /*motion_type*/0, /*se_type*/3, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"無数の毒蛇から成る頭髪の、乙女座の魔星獣。\n悲恋の苦しみは、全てを拒み石に変える。\n\n・敵全体に魔法大ダメージを与え、前方範囲内\n　の敵はさらに石化状態にする。\n・前方範囲内の敵に物理ダメージを与え、石化\n　状態の敵はさらにＴＰを大ダウン。\n・物理攻撃力が最も高い敵1キャラに魔法大ダ\n　メージを与え、毒状態にする。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301500, /*unit_name*/"ウールヴヘジン", /*prefab_id*/301500, /*motion_type*/0, /*se_type*/12, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】遠吠えで獲物を麻痺させ\nひるんだ所を爪で引き裂く。\n数々の冒険者の行く手を阻んできた\n残虐を極める魔界の狼。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301600, /*unit_name*/"ジズ", /*prefab_id*/301600, /*motion_type*/0, /*se_type*/3, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】猛り狂う暴風を纏いし大怪鳥。\n魔物図鑑を抜け出し、破壊の限りを尽くす。\n\n・敵全体にキャラの最大ＨＰに応じた\n　大ダメージを与える。\n・前方範囲内の敵に物理中ダメージを与え、\n　さらにスタンさせる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301601, /*unit_name*/"ジズ", /*prefab_id*/301600, /*motion_type*/0, /*se_type*/3, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】猛り狂う暴風を纏いし大怪鳥。\n魔物図鑑を抜け出し、破壊の限りを尽くす。\n\n・敵全体にキャラの最大ＨＰに応じた\n　大ダメージを与える。\n・前方範囲内の敵に物理中ダメージを与え、\n　さらにスタンさせる。\n・前方範囲内の敵にキャラの最大ＨＰに応じた\n　中ダメージを与え、物理防御力を下げる。\n・ジズ自身のＨＰが50％を切ると、さらに\n　攻撃が激しく凶暴になる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301800, /*unit_name*/"サジタリウス", /*prefab_id*/301800, /*motion_type*/0, /*se_type*/3, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"天を駆け数多の標的を射止める射手座の魔星獣。\n雷光纏いし矢は、刹那にすべてを穿つ至高の技。\n\n・ユニオンバーストは敵全体に最大ＨＰに応じ\n　て魔法ダメージを与える。\n　暗闇状態の敵はダメージが3倍になる。\n・敵全体に魔法中ダメージを与え、さらに残り\n　ＨＰ割合が最も少ない敵1キャラに強力な暗\n　闇状態を付与。\n・前方の敵3キャラに物理ダメージを与える。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301900, /*unit_name*/"ウェアオルカ", /*prefab_id*/301900, /*motion_type*/0, /*se_type*/11, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.7, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理/魔法】鋭い眼つきで、獲物\nと波を探し求める海のハンター。\n魔力で無限に生み出す潮水は\n敵の刃と気力を鈍らせる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302000, /*unit_name*/"ワイルドグリフォン", /*prefab_id*/302000, /*motion_type*/0, /*se_type*/11, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"過酷な環境で進化したプチグリフォン。\n竜巻で獲物を巻き上げ、空で狩りをする。\n\n・前方範囲内の敵に魔法中ダメージ。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302001, /*unit_name*/"ワイルドグリフォン", /*prefab_id*/302001, /*motion_type*/0, /*se_type*/11, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"過酷な環境で進化したプチグリフォン。\n竜巻で獲物を巻き上げ、空で狩りをする。\n\n・前方範囲内の敵に魔法中ダメージ。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302100, /*unit_name*/"ワイバーン", /*prefab_id*/302100, /*motion_type*/0, /*se_type*/11, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"大きな皮翼を使い、空を駆ける翼竜。\n巨大な火球を吐き、侵入者の行く手を阻む。\n\n・前方範囲内の敵に物理中ダメージ。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302101, /*unit_name*/"ワイバーン", /*prefab_id*/302101, /*motion_type*/0, /*se_type*/11, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"大きな皮翼を使い、空を駆ける翼竜。\n巨大な火球を吐き、侵入者の行く手を阻む。\n\n・前方範囲内の敵に物理中ダメージ。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302200, /*unit_name*/"イクト・ガラディオン", /*prefab_id*/302200, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/2, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【魔法】ルナの魔力で目覚めた、古代の機械兵器。\n無慈悲に放たれる魔力が、侵入者を阻む。\n\n・敵全体に魔法特大ダメージを与え、魔法防御力\n　を大ダウン。\n・前方範囲内の敵に物理大ダメージを与える。\n・前方範囲内の敵に魔法大ダメージを与え、\n　麻痺状態にする。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302300, /*unit_name*/"ワイズオウル", /*prefab_id*/302300, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/2, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302400, /*unit_name*/"アークガーディアン", /*prefab_id*/302400, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302500, /*unit_name*/"キマイラ", /*prefab_id*/302500, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.4, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302600, /*unit_name*/"ツインピッグス", /*prefab_id*/302600, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】盗賊行為を重ねる、双子座の魔星獣。\n抜群の連携で、アジトの侵入者に襲い掛かる。\n\n・敵全体に、最大ＨＰに応じた物理\n　大ダメージ。\n・後衛の敵に物理中ダメージを与え、\n　攻撃速度を小ダウン。\n・敵全体に、物理小ダメージ。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302601, /*unit_name*/"ツインピッグス", /*prefab_id*/302601, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.9, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】盗賊行為を重ねる、双子座の魔星獣。\n抜群の連携で、アジトの侵入者に襲い掛かる。\n\n・敵全体に、最大ＨＰに応じた物理\n　大ダメージ。\n・後衛の敵に物理中ダメージを与え、\n　攻撃速度を小ダウン。\n・敵全体に、物理小ダメージ。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302700, /*unit_name*/"カルキノス", /*prefab_id*/302700, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】船墓の入江に巣食う、蟹座の魔星獣。\n巨大なハサミの一撃で、無数の船を沈める。\n\n・敵全体に魔法大ダメージを与え、物理防御力\n　をダウン。\n・前方範囲内の敵すべてに物理中ダメージを与\n　え、さらにスタン状態にする。\n・敵全体に物理中ダメージ。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302800, /*unit_name*/"オルレオン", /*prefab_id*/302800, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"サバンナの覇者と名高い、獅子座の魔星獣。\n刃向かう者は、灼熱の咆哮で焼き尽くす。\n\n・敵全体に、最大ＨＰに応じた防御力無視の\n　物理大ダメージを与え、残りＨＰの少ない\n　敵はさらに火傷状態にする。\n・敵1キャラに物理大ダメージを与え、火傷\n　状態の場合はさらに追加ダメージ。\n・前方範囲内の敵に物理中ダメージを与え、\n　火傷状態の場合はさらに追加ダメージ。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303100, /*unit_name*/"トウミングマ", /*prefab_id*/303100, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/3.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】氷穴の最奥に引きこもり、\n生涯冬眠すると決めた巨大熊。\n長い歳月を夢うつつの中で過ごし\n獲物を狩るその一瞬、覚醒する。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303200, /*unit_name*/"マダムピック", /*prefab_id*/303200, /*motion_type*/0, /*se_type*/6, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理/魔法】吹雪と共に現れ、\n縄張りを荒らす者には\n大群を呼び寄せ襲い掛かる\n雪国イチのゴージャスマダム。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303300, /*unit_name*/"ガーゴイル", /*prefab_id*/303300, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.1, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理/魔法】朽ちた遺跡に残り\n主亡き地を護る、謎の鳥像。\n街も命令も、全て砂と消えた今は\n誰彼構わず炎を吐き襲い掛かる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303400, /*unit_name*/"スナノヅチ", /*prefab_id*/303400, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.9, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】生存競争激しい砂漠の\n頂点に君臨する、巨大トカゲ。\n毒を使い、砂を活かす戦法は、\n過酷な環境で得たサバイバル術。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303500, /*unit_name*/"ムシュフシュ", /*prefab_id*/303500, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理/魔法】吸い続けた他者の\n魔力で、妖術を体得した魔物。\n敵に付けた傷から魔力を奪い、\nさらなる力を得ようとする。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303600, /*unit_name*/"バーンサウルス", /*prefab_id*/303600, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.7, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】強靭な脚と鋭利なツメで\n獲物を一瞬で狩る、獰猛な恐竜。\n滅びた大地でマグマを喰らい、\n生存の道を見つけた孤高の存在。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303700, /*unit_name*/"ヘルハウンド", /*prefab_id*/303700, /*motion_type*/0, /*se_type*/14, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/0.7, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】火山に身を落としてなお\n地獄の炎を纏い蘇った双頭の魔犬。\n愛犬ボニーと共に縄張りを駆け、\n侵入者には灼熱の洗礼を浴びせる。");
